function fn_D11_Validation02()
{ 
	for(i=0;  i < ds_DangerJob.GetRowCount() ; i++)
	{
		if( gfn_isNull( ds_DangerJob.GetColumn(i,"JOB_APPRO_STR_TIME") ) ){
			alert(gfn_GetMessage("M000015","%AAA%","SCG 작업계획의 승인시작시간은 "));
			return 1;
		}
		if(  gfn_isNull( ds_DangerJob.GetColumn(i,"JOB_APPRO_END_TIME") ) ){
			alert(gfn_GetMessage("M000015","%AAA%","SCG 작업계획의 승인종료시간은"));
			return 1;
		}
		if(  gfn_isNull( ds_DangerJob.GetColumn(i,"DANGER_SCG_PLAN_CD") ) ){
			alert(gfn_GetMessage("M000015","%AAA%","SCG 작업계획의 작업내용은 "));
			return 1;
		}
	}
	
}	 

