function btn_RowAdd01_OnClick(obj)
{
	gfn_SetGrdComboBox_Comcode ("grd_DangerJob",4,"D10156");
	//gfn_SetGrdComboBox_Comcode ("grd_DangerJob",9,"D10153");	
	grd_DangerJob.Enable = true;
	ds_DangerJob.AddRow();
		grd_DangerJob.SetCellProp("body",1,"Edit","mask");
		grd_DangerJob.SetCellProp("body",3,"Edit","mask");
		grd_DangerJob.SetCellProp("body",4,"Edit","combo");
		// grd_DangerJob.SetCellProp("body",6,"Edit","none");
		// grd_DangerJob.SetCellProp("body",8,"Edit","none");
		// grd_DangerJob.SetCellProp("body",9,"Edit","none");
		// grd_DangerJob.SetCellProp("body",10,"Edit","none");		
	if(ds_DangerJob.GetRowCount() == 1){
		return;
	}else{
		ds_DangerJob.SetColumn(ds_DangerJob.row,"JOB_STR_TIME",ds_DangerJob.GetColumn(ds_DangerJob.row-1,"JOB_END_TIME"));
	}
}

function btn_RowDel01_OnClick(obj)
{
	var strtime = ds_DangerJob.GetColumn(ds_DangerJob.RowCount()-1,"JOB_STR_TIME");
	var endtime = ds_DangerJob.GetColumn(ds_DangerJob.RowCount()-1,"JOB_END_TIME");
	var dangerJPC = ds_DangerJob.GetColumn(ds_DangerJob.RowCount()-1,"DANGER_JOB_PLAN_CD");
	var rmk = ds_DangerJob.GetColumn(ds_DangerJob.RowCount()-1,"RMK");
	
	if(ds_DangerJob.GetRowType(ds_DangerJob.RowCount()-1) == "insert")
	{
		if((strtime==""||strtime==null) && (endtime==""||endtime==null) && (dangerJPC==""||dangerJPC==null) && (rmk==""||rmk==null))
		{
			ds_DangerJob.DeleteRow(ds_DangerJob.row);
		}else if(confirm("작성된 내용이 있습니다. 그래도 삭제하시겠습니까?"))
		{
			ds_DangerJob.DeleteRow(ds_DangerJob.row);
		}
	}else if(confirm("등록된 작업계획을 삭제하시겠습니까?"))
	{
		fn_D1_WorkJobDelete(ds_DangerJob.row);
	}
}
//----------------------------------------------------------------------------------------------
// 함수명: fn_D1_WorkJobDelete()
// 설명:   인건비 내역 삭제
// arguments
// return 내용: 없음
//----------------------------------------------------------------------------------------------
function fn_D1_WorkJobDelete(row)
{    
	ds_DangerJob.DeleteRow(row);

	var strSvcID = "fn_D1_WorkJobDelete";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_DangerWorkRequiRegisHmppMgr-service&delete=1";  
	var strInDatasets  = "ds_DangerJob=ds_DangerJob:u";
	var strOutDatasets  = "";
	var strArgument  = "";	
	
	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  );
				  
	if(ErrorCode < 0)
	{    
		return;
	}else{
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"),"black",true); 
	}					  
}
