}

function cal_JobReqYmd1_OnChanged    (obj,strOldText,strNewText){			// 작업요청일 체크 
	if(cal_JobReqYmd1.Value < today() && !gfn_IsNull(cal_JobReqYmd1.Value)){
		alert("작업요청일자가 오늘날짜보다 작을 수 없습니다. 수정해주세요.");
		cal_JobReqYmd1.Value = "";
		return;	
	}	
}
