	}	
}
function cal_ReviewYmd_OnChanged(obj,strOldText,strNewText){										// 검토일자 체크 
	if(gfn_IsNull(cal_ReviewYmd.Value)){
		return;
	}else{
		fn_DateCheckTodayOver(obj,"검토일자");
	}	
}				
