var	strDangerNum;				// 위험작업번호 
var	strWorkName;				// 위험작업명 

function D1502010014U_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
	fn_Init();	
}


//---------------------------------------------------------
//	초기 설정
//---------------------------------------------------------
function fn_Init()
{
	
	// 임시
	// 버튼을 강제 세팅할 경우만 사용
	// 권한관리 하면 사용말것 *** 순서대로 세팅
	gfn_SetButton("0001111");
	
}

//-------------------------------------------------------------------------------
// 조회 버튼 실행 
//-------------------------------------------------------------------------------
function fn_CommonSearch()
{
	//sorting - Sort Mark를 제거 
	
	gfn_SetHeadClearAll(grd_List);
	
	var strSvcID = "CommonSearch";
	var strPart = "D10";
	var strURL = "";
	var checkOpt = "find1";				    // 기본적인 검색경로 
	
	strComId = "01";

	strDangerNum = edt_DangerNum.Text;
	strWorkName = edt_WorkName.Text;
	
	strURL = "handle.do?ServiceName=d1502010014u-service&"+checkOpt+"=1&COM_ID=" + strComId;
	
	strURL = strURL + "&DANGER_NUM=" + strDangerNum;
	strURL = strURL + "&WORK_NAME1=" + strWorkName;
	strURL = strURL + "&WORK_FIRM=" + strWorkName;			// 시공사 
	strURL = strURL + "&STR_YMD=" + strWorkName;			// 시작일자 
	strURL = strURL + "&END_YMD=" + strWorkName;			// 종료일자 
	strURL = strURL + "&BRANCH_CD=" + strWorkName;			// 지사구분 
	strURL = strURL + "&POST_ADDR=" + strWorkName;			// 우편번호(구구분)
	
	
	
	var strInDatasets  = "";
	    //strInDatasets += "ds_codem=ds_codem";
	var strOutDatasets  = "";
	    strOutDatasets += "ds_DangerList=ds_codem";
	var strArgument  = "";
	var strCallbackFunc = "fn_CallBack";
	
	gfn_AsyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			       , strCallbackFunc
			   );
}
//-------------------------------------------------------------------------------
// 
//-------------------------------------------------------------------------------
function fn_CallBack(srvId,ErrorCode,ErrorMsg)
{
	if(ErrorCode < 0)
	{
		return;
	}
	
	
	if(srvId == "CommonSearch")
	{
		// 조회 후 처리
		
	}
}


//-------------------------------------------------------------------------------
// 엑셀 버튼 실행 
//-------------------------------------------------------------------------------
function fn_CommonExcelExport()
{
	grd_List.ExportExcelEx("sheet1");
	
}

//---------------------------------------------------------
//	공통 버튼 클릭 - 선택
//---------------------------------------------------------
function fn_CommonSelect()
{
	var arrList = array();
	var nRow = ds_codem.row;
	
	// 담당자명 
	
	arrList[0] = grd_List.GetColumn(nRow,"DANGER_NUM");		// 위험작업번호  
	
	Close(arrList);
}

function grd_List_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	fn_CommonSelect();
}
