//--------------------------------------------------------------------------------------------------
function D1601010010F_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
	fn_Init();

	// fn_D13_Findlist();

	fv_emp_id = gfn_GetGlobalValue("G_USER_NO");
	fv_emp_nm = gfn_GetGlobalValue("G_NAME");
	fv_COM_ID = gfn_GetGlobalValue("G_COM_ID");
	fv_DEAL_CD= gfn_GetGlobalValue("G_DEAL_CD");
	fv_DEAL_NM= gfn_GetGlobalValue("G_COM_NAME");
	fv_POS_RNK_CD=gfn_GetGlobalValue("G_POS_RNK_CD");// 직위코드 (325-팀장)

	if(fv_COM_ID == "92") // 시공사-협력사 여부
	{
		// 시공사는 입력 못함.
		gfn_SetForceButton("00110101");
		fv_COOP_YN = true;
	}
	
	rdo_ReqFlag.Enable = false;
	rdo_ReqFlag.Value = "";
	
	fn_D10_ChkButton01(); 	//체크버튼 초기값 0 
	fn_D10_ChkButton02(); 	//체크버튼 비활성화
	fn_D11_Common01();  	//정비보수 작업등록 false(시설물)	
	fn_D11_Common03();  	//정비보수 작업등록 false(시설물외)
	chk_Change1.Enable = false;
	fn_D11_Common05();  	//정비보수의뢰 승인부분 false
	fn_D11_Common07();
	fn_D11_Common25();		//굴착대상이 아닐경우 비활성화
	grd_Mat.Editable = false;
	grd_Mat.Enable = true;
	fn_D11_Common09();
	fn_D11_Common11();
	
	fn_D11_Common13();
	fn_D11_Common15();
	fn_D11_Common17();
	fn_D11_Common19();
	fn_D11_Common21();
	fn_D11_Common23();
	
	btn_RowAdd01.Enable = false;
	btn_RowDel01.Enable = false;
	btn_RowAdd02.Enable = false;
	btn_RowDel02.Enable = false;
		
		
	btn_Material.Enable = false;
	btn_FileFolder.Enable = false;
	btn_FileFolder01.Enable = false;
	btn_gis.Enable = false;

	
	/*
	if(IsExistVar("FLAG",true)){
		if(FLAG = "POPUP"){
			cob_Branchcd.Value       = BRANCH_CD;
			cob_ZipNo1.Value         = ZIP_NO1;
			cal_ReqYmdEnd.Value        = WORK_REQ_YMD;
			cob_MainterStsFlag.Value = MAINTER_STS_FLAG;
			
			fn_CommonSearch();
			grd_list_OnCellClick(obj,0,"","","","");
		}
	}
	
	if(IsExistVar("FACI_NUM", true)){
			
		cob_Branchcd.Value       = BRANCH_CD;
		cob_ZipNo1.Index = 0;
		
		fn_CommonSearch();
		
		var nRow = 0;
				
		if(DETAIL_NUM == '' || DETAIL_NUM == null){
			DETAIL_NUM = FACI_NUM; 
		}
		var DetailNum = trim(SUBSTR(DETAIL_NUM, 0, 2));
			
		for(var i = 0; i<ds_Multi.GetRowCount(); i++){
			if(DetailNum = trim(ds_Multi.GetColumn(i, "FACI_NUM_SUB")) && FACI_NUM = ds_Multi.GetColumn(i, "FACI_NUM")){
				nRow = i;					
			}
		}
		// 등록된 ROW를 기본으로 셋팅
		ds_Multi.SelectRow(0, false);
		ds_Multi.SelectRow(nRow);
		ds_Multi.row = nRow;
						
		grd_list_OnCellClick(obj, ds_Multi.row, "", "", "" ,"");
		
		return;
	}	
		
		*/
	//-----------------------------------------
	//  조회조건 초기값 설정
	//-----------------------------------------	
	
 	// 시공사이면
 	if( fv_COOP_YN )
 	{
		// 버튼 셋팅
 	
		// 지사설정
		cob_BranchCd.Enable = true;
		cob_BranchCd.Value = "";
		cob_BranchCd_OnChanged();
		
		// 팀설정	
		cob_TeamCdSearch.Enable = true;	
		cob_TeamCdSearch.Value = "";
		
		// 시공사 설정
		edt_WorkFirmCdSearch.Text = fv_DEAL_CD;
		edt_WorkFirmNmSearch.Text = fv_DEAL_NM;
		
		// 업체설정
		edt_WorkFirmCdSearch.Enable = false;
		btn_WorkFirmCdSearch.Enable = false;
		rmk1.Enable = false;
 	}
 	else
 	{
		// 버튼 셋팅 	
 	
 		// SCG이면
		rmk1.Enable = true;
		//-----------------------------------------
		//  지사,팀 콤보박스의 초기 설정
		//-----------------------------------------	
		if (gfn_GetGlobalValue("G_BRANCH_CD") == "999999") 
		{
			// 지사설정
			cob_BranchCd.Enable = true;
			cob_BranchCd.Value = "";
			cob_BranchCd_OnChanged();
			// 팀설정	
			cob_TeamCdSearch.Enable = true;	
			cob_TeamCdSearch.Value = "";
			
			// 업체설정
			edt_WorkFirmCdSearch.Enable = true;
			btn_WorkFirmCdSearch.Enable = true;			
		
		} 
		else 
		{
			// 지사설정
			cob_BranchCd.Enable = false;
			cob_BranchCd.Value = gfn_GetGlobalValue("G_BRANCH_CD");
			cob_BranchCd_OnChanged();
			
			// 팀설정 //상황팀 관계로 팀 해제 2010.3.4 권기철
			//cob_TeamCdSearch.Enable = false;					
			cob_TeamCdSearch.Enable = true;	
			cob_TeamCdSearch.Value = gfn_GetGlobalValue("G_DEPT_CODE");
			//trace("fv_POS_RNK_CD: "+fv_POS_RNK_CD);
			
			// 직위코드 팀장급(325) - 팀을 풀어달라. 2009-11-09 정우현과장, 권기철과장
			if( fv_POS_RNK_CD == "325") 
			{
				cob_TeamCdSearch.Enable = true;
			}

			// 업체설정
			edt_WorkFirmCdSearch.Enable = true;
			btn_WorkFirmCdSearch.Enable = true;	

		}		
 	}	
 	
 	// (gfn_GetGlobalValue("G_BRANCH_YN") == "N") // 지사가 없는경우 
		
	cob_MainterStsFlag.Value = ""; // 공사진행상태
	cob_WorkFlag.Value = "";       // 공사비 승인
	cob_DirFlag.Value = "";        // 직접복구비 승인
	cob_IndFlag.Value = "";        // 간접복구비 승인
	
	// 작업의뢰일자 default 이번달 1일부터 오늘까지 
	cal_ReqYmdStart.Value = substr(today(),0,6)+"01";		
	//cal_ReqYmdEnd.Value = today();
	cal_ReqYmdEnd.Value = adddate(addMonth(substr(today(),0,6)+"01", 1),-1); // 해당날의 마지막날 구하기

	
	cbo_ReqFlagSearch.Value = "";  		// 정비보수의뢰구분
    cob_FaciFlagSearch.Value = "";      // 시설물구분
	
	fn_CommonActivate();
		
}
//--------------------------------------------------------------------------------------------------
//	fn_CommonActivate
//-------------------------------------------------------------------------------------------------
function fn_CommonActivate()
{
	if(IsExistVar("OPEN_FLAG", true))
	{
		//alert("[시설물 정비보수 작업 연계] - 연계처리 로직!! ");
/*
		trace("OPEN_FLAG"          + ": " + OPEN_FLAG        ); // 01. 구분 (지정된 화면)
		trace("MAINTER_NUM"        + ": " + MAINTER_NUM      ); // 02. 정비보수번호      
		trace("BRANCH_CD"          + ": " + BRANCH_CD        ); // 03. 지사코드          
		//trace("ZIP_NO1"            + ": " + ZIP_NO1          ); // 04. 시/구코드         
		trace("TEAM_CD"            + ": " + TEAM_CD          ); // 05. 팀코드            
		trace("WORK_FIRM_CD"       + ": " + WORK_FIRM_CD     ); // 06. 시공사코드        
		trace("FACI_NUM"           + ": " + FACI_NUM         ); // 07. 시설물코드        
		trace("FACI_NM"            + ": " + FACI_NM          ); // 08. 시설물명          
		trace("ZIP_SEQ"            + ": " + ZIP_SEQ          ); // 09. 우편일련번호      
		trace("ZIP_NO1"            + ": " + ZIP_NO1          ); // 10. 우편번호1         
		trace("ZIP_NO2"            + ": " + ZIP_NO2          ); // 11. 우편번호2         
		trace("HILL"               + ": " + HILL             ); // 12. 산번지            
		trace("ADDR_M"             + ": " + ADDR_M           ); // 13. 본번              
		trace("ADDR_S"             + ": " + ADDR_S           ); // 14. 부번              
		trace("SECOND_ADDR"        + ": " + SECOND_ADDR      ); // 15. 보조주소          
		trace("BLD_DONG"           + ": " + BLD_DONG         ); // 16. 건물동            
		trace("HOSU"               + ": " + HOSU             ); // 17. 호수              
		trace("LOT_NUM_UNION"      + ": " + LOT_NUM_UNION    ); // 18. 지번주소조합      
		trace("PAVEMENT_KIND"      + ": " + PAVEMENT_KIND    ); // 19. 포장종류코드      
		trace("WORK_REQ_EMPID"     + ": " + WORK_REQ_EMPID   ); // 20. 의뢰자ID          
		trace("WORK_REQ_EMPID_NM"  + ": " + WORK_REQ_EMPID_NM); // 21. 의뢰자명          
		trace("KEY1"               + ": " + KEY1             ); // 22. PK1               
		trace("KEY2"               + ": " + KEY2             ); // 23. PK2               
		trace("KEY3"               + ": " + KEY3             ); // 24. PK3               
		trace("KEY4"               + ": " + KEY4             ); // 25. PK4               
		trace("KEY5"               + ": " + KEY5             ); // 26. PK5  
	
*/
		if( ! gfn_IsNull( MAINTER_NUM )  )
		{
		
		    // 조회조건셋팅
		    cob_Branchcd.Value = BRANCH_CD; 		//지사코드
		    cob_BranchCd_OnChanged();
		    cob_TeamCdSearch.Value = TEAM_CD;		//팀코드
			
			fv_FindRow_ShowRow = 1;
			fv_FindRow_ShowKey = MAINTER_NUM; //위험작업번호
	
			fn_D1_FindListOne(MAINTER_NUM);
						
			if( ds_Multi.GetRowCount() > 0)
			{
				alert("[정비보수의뢰 연계] 정비보수번호("+MAINTER_NUM +") 의 데이터가 조회 되었습니다.");
			}
			else
			{
				alert("[정비보수의뢰 연계] 정비보수번호("+MAINTER_NUM +") 의 데이터가 존재하지 않습니다.");				
			}
			
			
		}
		else
		{
		
			
		    // 조회조건셋팅
		    cob_Branchcd.Value = BRANCH_CD; 		//지사코드
		    cob_BranchCd_OnChanged();
		    cob_TeamCdSearch.Value = TEAM_CD;		//팀코드
		    
		    // 신규등록
		    // 추가버튼 클릭후 
		    fn_CommonAddRow();

		    cob_TeamCd01.Value = TEAM_CD; //팀코드	

		    ds_Multi_Re.SetColumn(0, "FACI_FLAG"        ,substr(FACI_NUM,0,2) ); // 03. 지사코드
			ds_Multi_Re.SetColumn(0, "FACI_NUM"         ,FACI_NUM         ); // 07. 시설물코드        
			ds_Multi_Re.SetColumn(0, "FACI_NM"          ,FACI_NM          ); // 08. 시설물명          
						
			// 정기검사_배관 , 자율검사_배관
		    if( OPEN_FLAG == "D1301020010F" || OPEN_FLAG == "D1301040010F")
		    {
				rdo_ReqFlag.Value = "02"; // 시설물 외
				ds_Multi_Re.SetColumn(0, "MAINTER_REQ_FLAG" ,"02");
		    }
		    		    
		    //ds_Multi_Re.FireEvent = false;
		    ds_Multi_Re.SetColumn(0, "OPEN_FLAG"        ,OPEN_FLAG        ); // 01. 구분 (지정된 화면)
		    

			ds_Multi_Re.SetColumn(0, "BRANCH_CD"        ,BRANCH_CD        ); // 03. 지사코드          
			ds_Multi_Re.SetColumn(0, "ZIP_NO1"          ,ZIP_NO1          ); // 04. 시/구코드         
			ds_Multi_Re.SetColumn(0, "TEAM_CD"          ,TEAM_CD          ); // 05. 팀코드            
			ds_Multi_Re.SetColumn(0, "WORK_FIRM_CD"     ,WORK_FIRM_CD     ); // 06. 시공사코드        
			ds_Multi_Re.SetColumn(0, "ZIP_SEQ"          ,ZIP_SEQ          ); // 09. 우편일련번호      
			ds_Multi_Re.SetColumn(0, "ZIP_NO1"          ,ZIP_NO1          ); // 10. 우편번호1         
			ds_Multi_Re.SetColumn(0, "ZIP_NO2"          ,ZIP_NO2          ); // 11. 우편번호2         
			ds_Multi_Re.SetColumn(0, "HILL"             ,HILL             ); // 12. 산번지            
			ds_Multi_Re.SetColumn(0, "ADDR_M"           ,ADDR_M           ); // 13. 본번              
			ds_Multi_Re.SetColumn(0, "ADDR_S"           ,ADDR_S           ); // 14. 부번              
			ds_Multi_Re.SetColumn(0, "SECOND_ADDR"      ,SECOND_ADDR      ); // 15. 보조주소          
			ds_Multi_Re.SetColumn(0, "BLD_DONG"         ,BLD_DONG         ); // 16. 건물동            
			ds_Multi_Re.SetColumn(0, "HOSU"             ,HOSU             ); // 17. 호수              
			ds_Multi_Re.SetColumn(0, "LOT_NUM_UNION"    ,LOT_NUM_UNION    ); // 18. 지번주소조합      
			ds_Multi_Re.SetColumn(0, "PAVEMENT_KIND"    ,PAVEMENT_KIND    ); // 19. 포장종류코드      
			ds_Multi_Re.SetColumn(0, "WORK_REQ_EMPID"   ,WORK_REQ_EMPID   ); // 20. 의뢰자ID          
			ds_Multi_Re.SetColumn(0, "WORK_REQ_EMPID_NM",WORK_REQ_EMPID_NM); // 21. 의뢰자명          
			ds_Multi_Re.SetColumn(0, "KEY1"             ,KEY1             ); // 22. PK1               
			ds_Multi_Re.SetColumn(0, "KEY2"             ,KEY2             ); // 23. PK2               
			ds_Multi_Re.SetColumn(0, "KEY3"             ,KEY3             ); // 24. PK3               
			ds_Multi_Re.SetColumn(0, "KEY4"             ,KEY4             ); // 25. PK4               
			ds_Multi_Re.SetColumn(0, "KEY5"             ,KEY5             ); // 26. PK5  
			

		    //ds_Multi_Re.FireEvent = true;
			
			alert("[정비보수의뢰 연계] 신규데이터를 입력가능합니다. 내용을 입력하여 저장하시기 바랍니다.");
		}
		
	}
	
	// 팀장승인
	if(IsExistVar("TEAM_FLAG", true))
	{
	
		if( ! gfn_IsNull( MAINTER_NUM )  )
		{
		
		    // 조회조건셋팅
		    cob_Branchcd.Value = BRANCH_CD; 		//지사코드
		    cob_BranchCd_OnChanged();
		    cob_TeamCdSearch.Value = TEAM_CD;		//팀코드
			
			fv_FindRow_ShowRow = 1;
			fv_FindRow_ShowKey = MAINTER_NUM; //위험작업번호
	
			fn_D1_FindListOne(MAINTER_NUM);
						
			if( ds_Multi.GetRowCount() <= 0)
			{
				alert("[정비보수의뢰 연계] 정비보수번호("+MAINTER_NUM +") 의 데이터가 존재하지 않습니다.");				
			}
			
			
		}
		
	}
}

//---------------------------------------------------------------------------------
// 기      능   :  초기값 설정 
//---------------------------------------------------------------------------------
function fn_Init()
{

	grd_list.NoDataText = gfn_GetMessage("M000022","%AAA%","조회결과가");	
	
	gfn_D1_All_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_Branchcd"));			//지사코드(전체포함)
	//gfn_D1_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_Branchcd"));				//지사코드(전체포함)	
	gfn_D1_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_BranchCd01"));				//지사코드
	
	gfn_D1_All_SetComboBox_Comcode("D10115",gfn_setDsObject_Comcode("cob_MainterStsFlag"),""); 	//공사진행상태
	//gfn_D1_All_SetComboBox_Comcode("D10116",gfn_setDsObject_Comcode("cob_AssentType"),"");		//승인상태
	gfn_SetComboBox_Comcode("B10004",gfn_setDsObject_Comcode("cob_ZipNo2"));  					//시/구
	gfn_SetComboBox_Comcode("D10105",gfn_setDsObject_Comcode("cob_AdjType"));  					//정산유형
	gfn_SetComboBox_Comcode("A10320",gfn_setDsObject_Comcode("cob_ReceiveYn"));  				//접수여부	

	gfn_D1_NULL_SetComboBox_Comcode("D10040",gfn_setDsObject_Comcode("cob_PavementKind")); 		//도로포장구분     
	gfn_D1_NULL_SetComboBox_Comcode("D30001",gfn_setDsObject_Comcode("cob_PipeKind")); 			//배관구분
	gfn_D1_NULL_SetComboBox_Comcode("D10034",gfn_setDsObject_Comcode("cob_PressFlag")); 		//압력구분

	gfn_SetComboBox_Comcode("D10117",gfn_setDsObject_Comcode("cob_WorkLFlag")); 				//정비보수 대구분
	gfn_SetComboBox_Comcode("B20107",gfn_setDsObject_Comcode("cob_WorkMFlag")); 				//정비보수 중구분
	gfn_D1_SetComboBox_Comcode("B20031",gfn_setDsObject_Comcode("cob_WorkSFlag"));		
	//cob_Po 포종
	//cob_Kwan
	//cob_Je
	//cob_Bel
	gfn_D1_SetComboBox_Comcode("B20041",gfn_setDsObject_Comcode("cob_Po"));
	gfn_D1_SetComboBox_Comcode("B30007",gfn_setDsObject_Comcode("cob_Kwan"));
	gfn_D1_SetComboBox_Comcode("B20087",gfn_setDsObject_Comcode("cob_Je"));
	gfn_D1_SetComboBox_Comcode("B20114",gfn_setDsObject_Comcode("cob_Bel"));	
	
	gfn_D1_NULL_SetComboBox_Comcode("D10002",gfn_setDsObject_Comcode("cob_MaterFlag")); 		//재질코드
	gfn_D1_NULL_SetComboBox_Comcode("B30007",gfn_setDsObject_Comcode("cob_PipeDiamCd")); 		//배관관경
	gfn_D1_SetComboBox_Comcode("B20031",gfn_setDsObject_Comcode("cob_JuyaFlag"),"30#40");		//주야구분
	
	gfn_SetComboBox_Comcode('A10260','ds_APPRO_FLAG'); // 자재승인여부
	
}

