//--------------------------------------------------------------------------------------------------
//	서비스 CallBack
//--------------------------------------------------------------------------------------------------
function fn_D1_CallBack(SrvID,ErrorCode,ErrorMsg)
{

	if(ErrorCode < 0) {
	
		alert(ErrorMsg);
		//에러시 초기화
		fv_FindRow_ShowRow = -1;
		return;
	}
	
    switch(SrvID){

		//상세 조회
        case "findDetail":
        
			gfn_D1_SetComboBox_Teamcd01("ds_Team_01",cob_BranchCd01); 
			fn_D1_SearchDetailTeamUserList();	// 팀장조회	
        
			fn_D1_OnRowPosChanged();
        
        
			// 로우선택하기
			if(fv_FindRow_ShowRow > 0)
			{
				var nFocusRow = ds_Multi.FindRow("MAINTER_NUM",fv_FindRow_ShowKey);
				if (nFocusRow != -1)
				{
					ds_Multi.row = nFocusRow;
				}	
				fv_FindRow_ShowRow = -1;
			}
        	
        break;	
        	

        default:

    }
}
