//--------------------------------------------------------------------------------------------------
// 상세조회
//--------------------------------------------------------------------------------------------------
function fn_D1_SearchDetail(nRow)
{	

	// trace("fn_D1_SearchDetail mainter_num - " +mainter_num);

	if(nRow < 0) return;
		
	var strMAINTER_NUM = ds_Multi.GetColumn(nRow,"MAINTER_NUM");	
	
	ds_Multi_Re.ClearData();
	ds_Material.ClearData();
	ds_MaterialAdd.ClearData();
	
	var strSvcID = "findDetail";
	var strPart = "D10";	
	var strURL = "handle.do?ServiceName=D1_MaintRepaiWorkFacilRegisMgr-service";  
	var strInDatasets  = "";
	var strOutDatasets  = "ds_Multi_Re=ds_Multi_Re ds_Material=ds_Material ds_MaterialAdd=ds_MaterialAdd";
	var strArgument  = "findDetail=1 MAINTER_NUM=" +quote(strMAINTER_NUM);
	var strCallbackFunc = "fn_D1_CallBack";	
		
	gfn_AsyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				   , strCallbackFunc
			   );
			   
	// 2013.05.30. 이건민 - 정비보수업체 셋팅
	fn_D1_WorkFirmNm();
}


