//--------------------------------------------------------------------------------------------------
// 첨부화일 조회
//--------------------------------------------------------------------------------------------------
function fn_D1_SearchDetailFile_List()
{  
	work_flag = "10";

    var strSvcID = "findFile";
	var strPart = "D10";	
	var strURL = "handle.do?ServiceName=D1_MaintRepaiWorkFacilRegisMgr-service";  
	var strInDatasets  = "";
	var strOutDatasets = "ds_FileList=ds_FileList";
	var strArgument    = "findFile=1";
	    strArgument   += " MAINTER_NUM=" + quote(mainter_num);
		strArgument	  += " WORK_FLAG="   + quote(work_flag);
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );

	edtFileList.text = "";
	for(var i=0; i<ds_FileList.RowCount(); i++)
	{   
		edtFileList.text += ds_FileList.GetColumn(i,"ORG_FILE_NAME");
		if(ds_FileList.RowCount()-1 != i) edtFileList.text += "/";
	}
}

