//---------------------------------------------------------------------------------
//공통의 추가버튼을 눌렀을때
//---------------------------------------------------------------------------------
function fn_CommonAddRow()
{

	if (gfn_isNull( cob_BranchCd.Value )) //지사 선택
	{
		alert(gfn_GetMessage("M000040", "%AAA%", "지사를"));
		cob_BranchCd.SetFocus();
		return;
	}
	
    mainter_sts_flag = "00";
    rdo_ReqFlag.Enable = true;
	
	ds_Multi_Re.ClearData();
	ds_Multi_Re.AddRow();
		
	edt_WorkReqEmpid.Text = fv_emp_id;
	edt_WorkReqEmpidNm.Text = fv_emp_nm;
	
	cob_BranchCd01.Value = cob_Branchcd.Value;
	cob_BranchCd01_OnChanged();
	
	cob_TeamCd01.Value = gfn_GetGlobalValue("G_DEPT_CODE");
	
	ds_Multi_Re1.ClearData();
	ds_Multi_Re2.ClearData();
	ds_Material.ClearData();
	ds_Amt.ClearData();
	ds_GumTo01.ClearData();
	ds_GumTo02.ClearData();
	ds_GumTo03.ClearData();
	ds_Sungin01.ClearData();
	ds_Sungin02.ClearData();
	ds_Sungin03.ClearData();
	
	//fn_D10_Clear();
	cal_Work_Req_Ymd_1.Value = today();
	
	fn_D10_ChkButton01(); //체크버튼 초기값 0 
	fn_D10_ChkButton02(); //체크버튼 비활성화
	chk_Change1.Enable = false;
	fn_D11_Common05();  //정비보수의뢰 승인부분 false
	fn_D11_Common07();
	fn_D11_Common25();	//굴착대상이 아닐경우 비활성화
	grd_Mat.Editable = false;                 // 자재신청내역 비활성화
	grd_Mat.Enable = true;
	grd_MatAdd.Enable = false;               // 추가자재신청내역 비활성화
	fn_D11_Common09();	//정비보수작업 결과내역 false
	fn_D11_Common11();	//정비보수작업내역 검토 및 승인의 일자관련 fasle
	fn_D11_Common13();	//검토 공사비 비활성화
	fn_D11_Common15();	//검토 간접복구비 비활성화
	fn_D11_Common17();	//검토 직접복구비 비활성화
	fn_D11_Common19();	//승인 공사비 비활성화 
	fn_D11_Common21();	//승인 간접복구비 비활성화
	fn_D11_Common23();	//승인 직접복구비 비활성화
	btn_RowAdd01.Enable = false;
	btn_RowDel01.Enable = false;
	btn_RowAdd02.Enable = false;
	btn_RowDel02.Enable = false;

		
	btn_FileFolder.Enable = true;
	edtFileList.Text ="";
	edtFileList01.Text ="";
	
	btn_gis.Enable = false;
	
	cal_GumTo01.Value = "";
	cal_GumTo02.Value = "";
	cal_GumTo03.Value = "";
	cal_GumTo04.Value = "";
	
	cal_SungIn01.Value = "";
	cal_SungIn02.Value = "";
	cal_SungIn03.Value = "";
	cal_SungIn04.Value = "";
	
	
	rdo_ReqFlag.Value = '01';
		
	// 2013.05.30. 이건민 - 안전공급 개선사항 (지사별 보수업체 자동셋팅)
	if(gfn_GetGlobalValue("G_BRANCH_CD") == '100584')
	{
		cob_WorkFirmNm.Index = 0;
	}else if(gfn_GetGlobalValue("G_BRANCH_CD") == '100591')
	{
		cob_WorkFirmNm.Index = 1;
	}else if(gfn_GetGlobalValue("G_BRANCH_CD") == '100664')
	{
		cob_WorkFirmNm.Index = 2;
	}
	else if(gfn_GetGlobalValue("G_BRANCH_CD") == '101500')
	{
		cob_WorkFirmNm.Index = 3;
	}else
	{
		cob_WorkFirmNm.Text = '';
	}
	
	
}
//-------------------------------------------------------------------------------
//	공통 버튼 클릭 - 삭제
//-------------------------------------------------------------------------------
function fn_CommonDeleteRow()
{
	if(ds_Multi.GetRowCount() == 0){
		alert("조회를 먼저 해주십시오.");
		return;
	}else{
		if(mainter_sts_flag == "00"){ 	
	
			if(length(trim(ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG"))) <> 0){
				//00 요청전 01 요청 02  검토반려 04 검토완료 일때만 삭제가능
				/*
				if(ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") != '00' && ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") != '01'
				   && ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") != '02' && ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") != '04')
				{
					alert("가정관 진행 상태가["+ ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG_NM") + "] 로 삭제가 불가능합니다.");
					return;
				}
				*/
				//2012.04.23 권기철 과장님 추가요청_이건민
				if( ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") != '00' && ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") != '02' )
				{
					alert("가정관 진행 상태가["+ ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG_NM") + "] 로 삭제가 불가능합니다.");
					return;
				}
			}
			/*
			if(confirm("등록된 정비보수 작업내역을 삭제하시겠습니까?"))
			{
				fn_delete1();														// 시공사가 의뢰한 작업내역 삭제 
																		
			}else{
				return;
			}
			*/
			// 2012.04.24 권기철 과장님 수정사항_이건민
			if(confirm("해당 위험작업도 함께 취소처리 됩니다. 취소처리 하시겠습니까?"))
			{
				fn_delete1();														// 시공사가 의뢰한 작업내역 삭제 
																		
			}else{
				return;
			}
		}else{
			alert("승인된 정비보수 작업내역은 삭제가 불가능합니다.");
			return;
		}
	}
}
//--------------------------------------------------------------------------------------------------
// 기      능   :  공통 저장 
//--------------------------------------------------------------------------------------------------
function fn_CommonSave()
{	

	
	if(ds_Multi_Re.GetRowCount() == 0)
	{
		alert("등록할 데이터가 존재하지 않습니다. 추가 버튼을 눌러 저장 바랍니다.");
		return;
	}
	
	
	// 시설물 구분
	var radio = rdo_ReqFlag.Value;
	
	if(gfn_IsNull(radio))
	{
		alert("시설물/시설물 외 구분을 체크해주세요.");
		return;
	}
	
	if(gfn_IsNull(cob_Cathodic_Yn.Value)) // 전기방식구분 Y,N 저장 미선택시 저장 안되게 함
	{
		alert("전기방식관련 항목을 선택해주세요.");
		return false;
	}	

//trace("### radio=" + radio + ",  mainter_sts_flag="+mainter_sts_flag+" chk_Change1=" + chk_Change1.Value + " " );
//trace("### decidestsflag=" + decidestsflag + ",  mainter_sts_flag="+mainter_sts_flag+" chk_Change1=" + chk_Change1.Value + " " );

	// ###########################################################################
	// 협력사이면 수정가능
	// ###########################################################################
	
	if( fv_COOP_YN ) 
	{
		// alert(radio);
		// alert(mainter_sts_flag);
		// alert(decidestsflag);
		// 접수(20), 조치(30) 일때 수정
		if(radio == '01' && mainter_sts_flag == "10" && decidestsflag == "Y")
		{	
		
//	trace("### fn_CommonSave 3 ### " );		
			// 의뢰 후 승인상태 
			var chk01 = fn_D10_Validation02();											// 접수 체크
			if(chk01 == false){
				return;
			}
				
			if(ds_Material.GetRowCount() == 0){
			
				if(confirm("작성된 자재신청 내역이 없습니다. 이대로 진행하시겠습니까?"))
				{
					fn_update2();	// 정비보수작업 접수내역
				}
				else
				{
					return;
				}
				
			}else{
				fn_update2();															// 굴착대상일경우 정비보수작업 접수내역 추가
			}
		}	
		else if(radio == '01' && mainter_sts_flag == "20")
		{	
		
//	trace("### fn_CommonSave 4 ### " );		
			// 접수상태 
			
			// 출고 수량 deliqty > 0
			// 승인 수량 approqty > 0
			// 출승인 수량이 존재하면
			if( ds_Material.GetRowCount() ==0 || (approqty > 0 && deliqty > 0))
			{				
				var chk01 = fn_D10_Validation03();											// 결과 체크
				if(chk01 == false){
					return;
				}		
				if(ds_Material.GetRowCount() > 0){
					if(approqty == null && deliqty == "0"){
						alert("자재 승인수량 입력 후 자재승인 버튼을 클릭하세요.");
						return;
					}else if(approqty != null && deliqty == "0"){
						alert("자재 출고수량이 없습니다. 기다려주세요.");
						return;
					}else{
						fn_update6();														// 결과내역 등록
					}
				}else{
					fn_update6();															// 결과내역 등록
				}
				
			}
			else
			{
				
//		trace("### fn_CommonSave 4-1 ### " );	
				// 등록한 후 승인 된 상태 
				var chk01 = fn_D10_Validation02();											// 접수 체크
				if(chk01 == false){
					return;
				}
					
				if(ds_Material.GetRowCount() == 0)
				{
					if(confirm("작성된 자재신청 내역이 없습니다. 이대로 진행하시겠습니까?"))
					{
						fn_update2();  // 정비보수작업 접수내역
					}
				}
				else
				{
					fn_update2();	// 정비보수작업 접수내역
				}
				
			} // end else
				
				
		}
		else if(radio == '01' && mainter_sts_flag == "30")
		{	
//	trace("### fn_CommonSave 5 ### " );	
	
			var chk01 = fn_D10_Validation03();											// 결과 체크
			if(chk01 == false){
				return;
			}	
				
			fn_update6();
			/*
			if(decideworkflag == "N" || decideindflag == "N" || decidedirflag == "N"){
				fn_update6();
				if(assentworkflag == "N" || assentindflag == "N" || assentdirflag == "N"){
					fn_update6();
				}else{
					return;
				}
			}else{
				return;
			}*/
		
		}
		else if(radio == '02' && mainter_sts_flag == "10" && decidestsflag == "Y")
		{		
		
//	trace("### fn_CommonSave 8 ### " );	
			// 등록한 후 승인 된 상태 
			var chk01 = fn_D10_Validation02();											// 접수 체크
			if(chk01 == false){
				return;
			}
				
			if(ds_Material.GetRowCount() == 0)
			{
				if(confirm("작성된 자재신청 내역이 없습니다. 이대로 진행하시겠습니까?"))
				{
				
					fn_update2();
				}
			}
			else
			{
				fn_update2(); // 정비보수작업 접수내역
			}
			
		}
		else if(radio == '02' && mainter_sts_flag == "20")
		{	
			// 접수상태 
		
//	trace("### fn_CommonSave 9 ### " );	
	
			// 출고 수량 deliqty > 0
			// 승인 수량 approqty > 0
			// 출승인 수량이 존재하면
			//아래 부분 수정 출고 수량은 조건 체크제외 2010.7.20권기철 자재 반려관련 적용
			//if( ds_Material.GetRowCount() ==0 || (approqty > 0 && deliqty > 0))
			if( ds_Material.GetRowCount() ==0 || approqty > 0)
			{
	
				var chk01 = fn_D10_Validation03();											// 결과 체크
				if(chk01 == false){
					return;
				}		
				if(ds_Material.GetRowCount()>0){
					if(approqty == null && deliqty == "0"){
						alert("자재 승인수량 입력 후 자재승인 버튼을 클릭하세요.");
						return;
					//아래 부분 수정 출고 수량은 조건 체크제외 2010.7.20권기철 자재 반려관련 적용
					//}else if(approqty != null && deliqty == "0"){						
					//	alert("자재 출고수량이 없습니다. 기다려주세요.");
					//	return;
					}else{
						fn_update6();														// 결과내역 등록
					}
				}else{
					fn_update6();															// 결과내역 등록
				}
			}
			else
			{
				
//		trace("### fn_CommonSave 8-1 ### " );	
				// 등록한 후 승인 된 상태 
				var chk01 = fn_D10_Validation02();											// 접수 체크
				if(chk01 == false){
					return;
				}
					
				if(ds_Material.GetRowCount() == 0)
				{
					if(confirm("작성된 자재신청 내역이 없습니다. 이대로 진행하시겠습니까?"))
					{
						fn_update2();  // 정비보수작업 접수내역
					}
				}
				else
				{
					fn_update2();	// 정비보수작업 접수내역
				}
				
			} // end else
			
		}else if(radio == '02' && mainter_sts_flag == "30"){	
		
			var chk01 = fn_D10_Validation03();											// 결과 체크
			if(chk01 == false){
				return;
			}	
				
			fn_update6();
			
			/*if(decideworkflag == "N" || decideindflag == "N" || decidedirflag == "N"){
				
				if(assentworkflag == "N" || assentindflag == "N" || assentdirflag == "N"){
					fn_update6();
				}else{
					return;
				}
			}else{
				return;
			}
			*/
		}
		else
		{
			alert("[시공사] 저장할 수 없습니다. 수정가능한 진행상태까지 기다려 주십시오.");
			return;
		}	
	}
	else // SCG
	{
	
	
		// ###########################################################################
		// SCG 이면 수정가능
		// ###########################################################################
	
		// 승인 아닐때 (X)
		if(mainter_sts_flag == "00" && chk_Change1.Value == 0)			
		{   	
	
//	trace("### fn_CommonSave 1 ### " );		
			// ################################################################################
			//---------------------------------------<<< 시설물 >>>
			// ################################################################################
			
			var chk01 = fn_D10_Validation01();
	
			if(chk01 == true)
			{
				fn_D1_Save1();
				
				return;
			}		
	
		}
		else if(mainter_sts_flag == "00" && chk_Change1.Value == 1)
		{	
//	trace("### fn_CommonSave 2 ### " );		
			// SCG 승인후에 (O)
			if(gfn_IsNull(cob_DecideEmpidReq.Value))
			{
				alert("[정비보수작업등록] 승인자를 선택해주세요.");
			}else{
				fn_update1();														    	// 승인 저장
			}
		}
		else
		{
			fn_D1_Save1();
			//특기사항 저장
			//alert("[SCG] 저장할 수 없습니다. 수정가능한 진행상태까지 기다려 주십시오. \n- << 자재승인은 자재승인 버튼을 이용하세요. >>");
			return;
		}
	
	
	} // end SCG
	
	// if(cob_Cathodic_Yn.Value == null)
	// {
		// alert("전기방식관련 항목을 선택해주세요.");
		// return;
	// }
	


}
//---------------------------------------------------------------------------------
// 기      능   :  시설물 등록시
//---------------------------------------------------------------------------------
function fn_D1_Save1()
{
	//update 인지 insert 인지 확인하기 (정비보수번호 자동부여)
	if(ds_Multi_Re.GetRowType(0) == "insert")
	{
		//정비보수번호 max 값 불러오기
		fn_max();
		var strMax = ds_list.GetColumn(0, "MAX"); // 정비보수번호
		ds_Multi_Re.SetColumn(0,"MAINTER_NUM",strMax);
		ds_Multi_Re.SetColumn(0,"MAINTER_STS_FLAG","00");
		ds_Multi_Re.SetColumn(0,"ASSENT_TYPE","00");
	}
	
	if(dig_obj_yn.Value == "Y"){
		//ds_Multi_Re.SetColumn(0,"DIG_SCALE_A",edt_DIG_SCALE_A.Value);
	}else{
		ds_Multi_Re.SetColumn(0,"DIG_SCALE_A",0);
	}
	
	ds_Multi_Re.SetColumn(0,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS")); 
	ds_Multi_Re.SetColumn(0,"UPD_EMPID",gfn_GetGlobalValue("G_USER_NO"));
	ds_Multi_Re.SetColumn(0,"CRT_IP",gfn_GetGlobalValue("G_IPADDRESS")); 
	ds_Multi_Re.SetColumn(0,"CRT_EMPID",gfn_GetGlobalValue("G_USER_NO"));	
	
	fv_FindRow_ShowRow = 1;
	fv_FindRow_ShowKey = ds_Multi_Re.GetColumn(0,"MAINTER_NUM");     //정비보수번호

	// 13종 화면 연계시 필요한 KEY 
	var strMAINTER_NUM = ds_Multi_Re.GetColumn(0,"MAINTER_NUM");
	var strOPEN_FLAG = ds_Multi_Re.GetColumn(0,"OPEN_FLAG");
	var strKEY1 = ds_Multi_Re.GetColumn(0,"KEY1");
	var strKEY2 = ds_Multi_Re.GetColumn(0,"KEY2");
	var strKEY3 = ds_Multi_Re.GetColumn(0,"KEY3");
	var strKEY4 = ds_Multi_Re.GetColumn(0,"KEY4");
	var strKEY5 = ds_Multi_Re.GetColumn(0,"KEY5");

	var strSvcID = "fn_D1_Save1";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_MaintRepaiWorkFacilRegisMgr-service";
	var strInDatasets  = "ds_Multi_Re=ds_Multi_Re:u";
	var strOutDatasets = "";
	var strArgument  = "save=1";	
	    strArgument += " MAINTER_NUM="+quote(strMAINTER_NUM);
	    strArgument += " OPEN_FLAG="+quote(strOPEN_FLAG);
	    strArgument += " KEY1="+quote(strKEY1);
	    strArgument += " KEY2="+quote(strKEY2);
	    strArgument += " KEY3="+quote(strKEY3);
	    strArgument += " KEY4="+quote(strKEY4);
	    strArgument += " KEY5="+quote(strKEY5);
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}else{
	
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
		alert(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"));
		if(ds_Multi_Re.GetColumn(0,"MAINTER_STS_FLAG") == "00")
		{
			var Gubun = ds_Multi_Re.GetColumn(0,"DANGER_APPRO_OBJ_YN");  // 위험작업대상일 경우 위험작업 화면으로 이동 
			if(Gubun == "Y")
			{
				if(Confirm(" 위험승인대상이므로 위험작업화면으로 이동하시겠습니까?"))
				{
					btn_Danger_OnClick();
				}
			}
		}
		fn_CommonSearch();
	}
}

//--------------------------------------------------------------------------------------------------
// 자재승인 버튼
//--------------------------------------------------------------------------------------------------
function btn_Material_OnClick(obj)
{
	for(var i=0; i < ds_Material.GetRowCount(); i++)
	{
		if( gfn_isNull(ds_Material.GetColumn(i,"APPRO_QTY")) )
		{
			alert("자재승인 수량을 입력해주세요.");
			ds_Material.row = i;
			return;
		}
		
		if( ds_Material.GetColumn(i,"APPRO_QTY") <= 0 )
		{
			alert("자재승인 수량은 0 이상 입력해주세요.");
			ds_Material.row = i;
			return;
		}
	}
	if(confirm("[자재구매승인요청] 승인처리 이후 수량변경이 불가능합니다. 자재 승인 하시겠습니까? "))
	{
		fn_D11_Save3(); 
	}
}

//---------------------------------------------------------------------------------
// 기      능   :  자재신청 내역 등록 <승인(08), 반려(07)>
//---------------------------------------------------------------------------------
function fn_D11_Save3()
{

	var strMAINTER_NUM = ds_Multi_Re.GetColumn(0,"MAINTER_NUM");
	var strUPD_EMPID = gfn_GetGlobalValue("G_USER_NO");
	var strUPD_IP = gfn_GetLocalIpAddress();
	
	for(var i=0; i < ds_Material.GetRowCount(); i++)
	{

		// 수정된 로우 셋팅
		if (ds_Material.GetRowType(i) == "update"){

			ds_Material.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
			ds_Material.SetColumn(i, "UPD_IP",    gfn_GetLocalIpAddress());
		
		}	// update end	
	}
	fv_FindRow_ShowRow = 1;
	fv_FindRow_ShowKey = strMAINTER_NUM;     //정비보수번호	
	
	var strSvcID = "fn_D11_Save3";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_MaintRepaiWorkFacilRegisMgr-service";
	var strInDatasets  = "ds_Material=ds_Material:u";
	var strOutDatasets  = "";
	var strArgument  = "updateMaterialApproval=1";	
	    strArgument += " MAINTER_NUM="+quote(strMAINTER_NUM);
	    strArgument += " UPD_EMPID="+quote(strUPD_EMPID);
	    strArgument += " UPD_IP="+quote(strUPD_IP);

	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  );
				  
	if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}else{
		alert("자재승인 되었습니다.");
	//	gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
		fn_CommonSearch();
	}				  
}
