function fn_delete1()
{
	ds_Multi.UpdateControl = false;
	ds_Multi.SetRowType(ds_Multi.row,"delete");
	
	var strSvcID = "fn_delete1";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_MaintRepaiWorkFacilRegisMgr-service";  
	var strInDatasets  = "ds_Multi=ds_Multi:u";
	var strOutDatasets  = "";
	var strArgument  = "delete1=1";
		strArgument += " MAINTER_NUM=" + quote(mainter_num);
	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  );
				  
	if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}else{
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"),"black",true); 
		alert(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를")); 
		fn_CommonSearch();		
	}				  
}

//---------------------------------------------------------------------------------
// 기      능   :  상태값(MAINTER_STS_FLAG) 변화
//---------------------------------------------------------------------------------
function fn_update1()
{   

	if( ! fn_D1_TeamManagerYN( fv_emp_id) )
	{
		alert("[권한]팀장만 승인 가능합니다. 로그인자가 팀장(325)권한이 아닙니다.");
		fn_D10_ds_Multi_Re1(); // 리셋
		return;
	}
	
	// var strEmpid = ds_Multi_Re1.GetColumn(0,"DECIDE_EMPID_REQ");
	 
	// if( ! fn_D1_TeamManagerYN( strEmpid ) );
	// {
		// alert("[권한]팀장만 승인 가능합니다. ");
		// return;
	// }
	
	ds_Multi_Re1.UpdateControl = false;
	ds_Multi_Re1.SetRowType(0,"update");
	
	ds_Multi_Re1.SetColumn(0,"MAINTER_STS_FLAG","10"							 );
	ds_Multi_Re1.SetColumn(0,"DECIDE_STS_FLAG" ,"Y"								 );	// 결재구분 
	ds_Multi_Re1.SetColumn(0,"ASSENT_TYPE"     ,"11"                             );	// 공사의뢰 승인 
	ds_Multi_Re1.SetColumn(0,"MAINTER_NUM"     ,mainter_num                      );
	ds_Multi_Re1.SetColumn(0,"UPD_IP"          ,gfn_GetGlobalValue("G_IPADDRESS")); 
	ds_Multi_Re1.SetColumn(0,"UPD_EMPID"       ,gfn_GetGlobalValue("G_USER_NO"  ));
		
	fv_FindRow_ShowRow = 1;
	fv_FindRow_ShowKey = ds_Multi_Re.GetColumn(ds_Multi_Re.row,"MAINTER_NUM");     //정비보수번호
			
	var strSvcID       = "fn_update1";
	var strPart        = "D10";
	var strURL         = "handle.do?ServiceName=D1_MaintRepaiWorkFacilRegisMgr-service";
	var strInDatasets  = "ds_Multi_Re1=ds_Multi_Re1:u";
	var strOutDatasets = "";
	var strArgument    = "update1=1";	
		
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	if(ErrorCode < 0)
	{    
		alert(ErrorMsg);	
		return;
	}else{
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
		alert(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을")); 
		fn_CommonSearch();
	}			   
}

//---------------------------------------------------------------------------------
// 기      능   :  정비보수작업 접수내역 후 상태값 변화 (굴착대상)(05)
//---------------------------------------------------------------------------------

function fn_update2()
{    

	var str_MAINTER_NUM = ds_Multi_Re.GetColumn(0,"MAINTER_NUM");
	
	
	// 1. 자재신청내역
	for(var i=0; ds_Material.GetRowCount()>i; i++)
	{

		// 추가된 로우 셋팅
		if (ds_Material.GetRowType(i) == "insert") {

			ds_Material.SetColumn(i, "MAINTER_NUM", str_MAINTER_NUM);	
			ds_Material.SetColumn(i, "CRT_EMPID",   gfn_GetGlobalValue("G_USER_NO"));
			ds_Material.SetColumn(i, "CRT_IP",      gfn_GetLocalIpAddress()); 
			ds_Material.SetColumn(i, "UPD_EMPID",   gfn_GetGlobalValue("G_USER_NO"));	
			ds_Material.SetColumn(i, "UPD_IP",      gfn_GetLocalIpAddress()); 
		}
		
		// 수정된 로우 셋팅
		if (ds_Material.GetRowType(i) == "update"){

			ds_Material.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
			ds_Material.SetColumn(i, "UPD_IP",    gfn_GetLocalIpAddress());
		
		}	// update end	
	
	}
	
	// 2. 추가 자재신청내역	
	for(var i=0; ds_MaterialAdd.GetRowCount()>i; i++)
	{

		// 추가된 로우 셋팅
		if (ds_MaterialAdd.GetRowType(i) == "insert") {

			ds_MaterialAdd.SetColumn(i, "MAINTER_NUM", str_MAINTER_NUM);	
			ds_MaterialAdd.SetColumn(i, "CRT_EMPID",   gfn_GetGlobalValue("G_USER_NO"));
			ds_MaterialAdd.SetColumn(i, "CRT_IP",      gfn_GetLocalIpAddress()); 
			ds_MaterialAdd.SetColumn(i, "UPD_EMPID",   gfn_GetGlobalValue("G_USER_NO"));	
			ds_MaterialAdd.SetColumn(i, "UPD_IP",      gfn_GetLocalIpAddress()); 
		}
		
		// 수정된 로우 셋팅
		if (ds_MaterialAdd.GetRowType(i) == "update"){

			ds_MaterialAdd.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
			ds_MaterialAdd.SetColumn(i, "UPD_IP",    gfn_GetLocalIpAddress());
		
		}	// update end	
	
	}	
	
	ds_Multi_Re2.UpdateControl = false;
	ds_Multi_Re2.SetRowType(0,"update");

	ds_Multi_Re2.SetColumn(0,"MAINTER_STS_FLAG","20");
	ds_Multi_Re2.SetColumn(0,"UPD_EMPID",  gfn_GetGlobalValue("G_USER_NO"));
	ds_Multi_Re2.SetColumn(0,"UPD_IP",     gfn_GetGlobalValue("G_IPADDRESS")); 
	ds_Multi_Re2.SetColumn(0,"MAINTER_NUM",str_MAINTER_NUM);
		
	fv_FindRow_ShowRow = 1;
	fv_FindRow_ShowKey = str_MAINTER_NUM;     //정비보수번호
			
	var strSvcID = "fn_update2";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_MaintRepaiWorkFacilRegisMgr-service";
