//---------------------------------------------------------------------------------
// 기      능   :  정비보수작업 결과내역 (09)
//---------------------------------------------------------------------------------

function fn_update6()
{    
	ds_Amt.UpdateControl = false;
	ds_Amt.SetRowType(0,"update");


	var strMAINTER_NUM = ds_Multi_Re.GetColumn(0,"MAINTER_NUM");
	ds_Amt.SetColumn(0,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS")); 
	ds_Amt.SetColumn(0,"UPD_EMPID",gfn_GetGlobalValue("G_USER_NO"));
	ds_Amt.SetColumn(0,"MAINTER_NUM",strMAINTER_NUM);
	ds_Amt.SetColumn(0,"MAINTER_STS_FLAG","30");
		
	// 2. 추가 자재신청내역	
	for(var i=0; ds_MaterialAdd.GetRowCount()>i; i++)
	{

		// 추가된 로우 셋팅
		if (ds_MaterialAdd.GetRowType(i) == "insert") {

			ds_MaterialAdd.SetColumn(i, "MAINTER_NUM", strMAINTER_NUM);	
			ds_MaterialAdd.SetColumn(i, "CRT_EMPID",   gfn_GetGlobalValue("G_USER_NO"));
			ds_MaterialAdd.SetColumn(i, "CRT_IP",      gfn_GetLocalIpAddress()); 
			ds_MaterialAdd.SetColumn(i, "UPD_EMPID",   gfn_GetGlobalValue("G_USER_NO"));	
			ds_MaterialAdd.SetColumn(i, "UPD_IP",      gfn_GetLocalIpAddress()); 
		}
		
		// 수정된 로우 셋팅
		if (ds_MaterialAdd.GetRowType(i) == "update"){

			ds_MaterialAdd.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
			ds_MaterialAdd.SetColumn(i, "UPD_IP",    gfn_GetLocalIpAddress());
		
		}	// update end	
	
	}			
		
		
	fv_FindRow_ShowRow = 1;
	fv_FindRow_ShowKey = strMAINTER_NUM;     //정비보수번호
			
	var strSvcID = "fn_update6";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_MaintRepaiWorkFacilRegisMgr-service";
	var strInDatasets  = "ds_Amt=ds_Amt:u ds_MaterialAdd=ds_MaterialAdd:u";
	var strOutDatasets  = "";
	var strArgument  = "update9=1";	
	    strArgument += " MAINTER_NUM=" + quote(mainter_num);
	
	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  );
				  
	if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}else{
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
		alert(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을")); 
		fn_CommonSearch();
	}				  
}
