//-------------------------------------------------------------------------------
//   함수명: fn_ZipSearch()
//   설명: 우편번호 조회클릭시 우편번호 조회 popup화면 로딩
//   arguments   
//   obj: 사용된 객체를 전달 한다.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_ZipSearch(obj)
{
	var arrList = gfn_Dialog("A10","A1101020010U","",1100,600,true,-1,-1);

	if(arrList == "" || arrList == null || arrList == 0){
		return;
	}
		ds_Multi_Re.SetColumn(0,"ZIP_NO1",arrList[2] ); // 우편번호 1
		ds_Multi_Re.SetColumn(0,"ZIP_NO2",arrList[3] ); // 우편번호 2
		ds_Multi_Re.SetColumn(0,"ZIP_SEQ",arrList[0] ); // 주소일련번호 
		ds_Multi_Re.SetColumn(0,"HILL",arrList[10] );    // 산번지
		ds_Multi_Re.SetColumn(0,"ADDR_M",arrList[11] );  // 본번 
		ds_Multi_Re.SetColumn(0,"ADDR_S",arrList[12] );  // 부번 
		ds_Multi_Re.SetColumn(0,"SECOND_ADDR",arrList[13] ); //보조주소
		ds_Multi_Re.SetColumn(0,"DONG",arrList[14] ); // 동
		ds_Multi_Re.SetColumn(0,"HOSU",arrList[15] ); // 호수
		ds_Multi_Re.SetColumn(0,"LOT_NUM_UNION",arrList[17] );  // 지번주소조합 
		// ds_Multi.SetColumn(0,"NEW_ADDR_BLD_SEQ",arrList[18] ); // 새주소건물일련번호
		// ds_Multi.SetColumn(0,"NEW_HOSU",arrList[21] );         // 새주소 호수 
		// ds_Multi.SetColumn(0,"NEW_ADDR_UNION",arrList[23] );   // 새주소 조합 
}

