}

//---------------------------------------------------------------------------------
// 기      능   :  원인자 이설보수번호 가져오기
//---------------------------------------------------------------------------------
function btn_MoveRepairNum_OnClick(obj)
{
	if( gfn_IsNull(cob_BranchCd01.Value))
	{
		alert("지사와 시/구를 입력해주세요");
		return;		
	}
	if( gfn_IsNull(cob_ZipNo2.Value))
	{
		alert("지사와 시/구를 입력해주세요");
		return;		
	}
	
	var args = " BRANCH_CD=" + quote(cob_BranchCd01.Value)
			 + " ZIP_NO1=" + quote(cob_ZipNo2.Value);
			 
	var strResult = gfn_Dialog("D10","D1601010014U",args,660,400,true,-1,-1);
	
	if( length(strResult) > 0)
	{
		edit_MoveRepairNum.Text = strResult[0];  // 이설번호
		
		var nRow = ds_Multi_Re.row;
		ds_Multi_Re.SetColumn(nRow, "ZIP_NO1"      , strResult[1]);  // 우편번호1
		ds_Multi_Re.SetColumn(nRow, "ZIP_NO2"      , strResult[2]);  // 우편번호2
		ds_Multi_Re.SetColumn(nRow, "ZIP_SEQ"      , strResult[3]);  // 우편일련번호
		ds_Multi_Re.SetColumn(nRow, "HILL"         , strResult[4]);  // 산번지
		ds_Multi_Re.SetColumn(nRow, "ADDR_M"       , strResult[5]);  // 본번
		ds_Multi_Re.SetColumn(nRow, "ADDR_S"       , strResult[6]);  // 부번
		ds_Multi_Re.SetColumn(nRow, "BLD_DONG"     , strResult[7]);  // 건물동
		ds_Multi_Re.SetColumn(nRow, "HOSU"         , strResult[8]);  // 호수
		ds_Multi_Re.SetColumn(nRow, "SECOND_ADDR"  , strResult[9]);  // 보조주소
		ds_Multi_Re.SetColumn(nRow, "LOT_NUM_UNION", strResult[10]); // 지번주소조합		
		
	}
	
}

