// 기      능   :  D1601010015U(정압기조회 팝업)으로부터 값 받아오기 
//---------------------------------------------------------------------------------
function btn_search_OnClick(obj)
{
	if(rdo_ReqFlag.Value == '01')
	{

		var args =  "BRANCH_CD=" + quote(cob_BranchCd01.Value);
		
		var strResult = gfn_Dialog("D10","D1601010015U",args,-1,-1,true,-1,-1);
		
		if( length(strResult) > 0 )
		{
			cob_ZipNo2.Enable = false;
			
			ds_Multi_Re.SetColumn(0,"FACI_NUM" , strResult[0]);  // 시설물코드
			
			ds_Multi_Re.SetColumn(0,"ZIP_NO1"      , strResult[1]); // 우편번호1
			ds_Multi_Re.SetColumn(0,"ZIP_NO2"      , strResult[2]); // 우편번호2
			ds_Multi_Re.SetColumn(0,"ZIP_SEQ"      , strResult[3]); // 우편일련번호
			ds_Multi_Re.SetColumn(0,"HILL"         , strResult[4]); // 산번지
			ds_Multi_Re.SetColumn(0,"ADDR_M"       , strResult[5]); // 본번
			ds_Multi_Re.SetColumn(0,"ADDR_S"       , strResult[6]); // 부번
			ds_Multi_Re.SetColumn(0,"BLD_DONG"     , strResult[7]); // 건물동
			ds_Multi_Re.SetColumn(0,"HOSU"         , strResult[8]); // 호수
			ds_Multi_Re.SetColumn(0,"SECOND_ADDR"  , strResult[9]); // 보조주소
			ds_Multi_Re.SetColumn(0,"LOT_NUM_UNION", strResult[10]);// 지번주소조합	
			
			ds_Multi_Re.SetColumn(0,"BRANCH_CD"    , strResult[11]); // 지사코드
			//ds_Multi_Re.SetColumn(0,"TEAM_CD"      , strResult[12]);// 팀코드	
			
			ds_Multi_Re.SetColumn(0,"FACI_FLAG"    , strResult[13]); //시설물구분
			ds_Multi_Re.SetColumn(0,"DETAIL_NUM"   , strResult[14]); // 상세번호	
			
			ds_Multi_Re.SetColumn(0,"FACI_NM"      , strResult[16]); // 시설물명
			
		}

	}
}
function cob_BranchCd01_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fn_Part_SetComboBox_Comcode("1", cob_BranchCd01.value, fn_setDsObject_Comcode("1", "cob_ZipNo2"));
	gfn_D1_SetComboBox_Teamcd01("ds_Team_01",cob_BranchCd01);
	
	// 2013.05.29. 이건민 - 안전공급 개선사항 (보수업체 자동셋팅)
	fn_D1_WorkFirmNm();
	
	fn_D1_SearchDetailTeamUserList();	// 팀장조회		
}

function fn_D1_WorkFirmNm()
{
	var strSvcID = "findList";
	var strPart = "D10";	
	var strURL = "handle.do?ServiceName=D1_MaintRepairEnterpriseMgr-service";  
	var strInDatasets  = "";
	var strOutDatasets = "ds_WorkFirmNm=ds_WorkFirmNm";
	var strArgument    = "find2=1";
    var CallBack       = "";

	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
		           , CallBack);
}

