{
	edt_DIG_SCALE_A.Value =ToNumber(edt_ACROSS_A.Value)*ToNumber(edt_HEIGHT_A.Value);
}

//---------------------------------------------------------------------------------
// 자재코드 찾기 
//---------------------------------------------------------------------------------

function grd_Mat_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	if(nCell == 0)
	{
		if(ds_Material.GetRowType(nRow) == "insert")
		{
			var list = gfn_Dialog("B30","B3101010020U","",656,400,true,-1,-1);
			if(list != null)
			{
				ds_Material.AddColumn("MATERIAL_NM");
				ds_Material.AddColumn("MATERIAL_STD");
				
				ds_Material.SetColumn(ds_Material.row,"MATERIAL_CD",list[0]);
				
				for(var i=0; ds_Material.GetRowCount()>i; i++){
					if(ds_Material.GetColumn(ds_Material.row,"MATERIAL_CD") == ds_Material.GetColumn(i, "MATERIAL_CD") &&  i != ds_Material.row){
						
						alert("이미 작성된 코드입니다. 재입력 해주세요");
						ds_Material.SetColumn(ds_Material.row,"MATERIAL_CD","");
						return;
					 }
				}					
				ds_Material.SetColumn(ds_Material.row,"MATERIAL_NM",list[1]);
				ds_Material.SetColumn(ds_Material.row,"MATERIAL_STD",list[3]);
			}
		}
	}
}


function btn_RowAdd01_OnClick(obj)
{

    var Arguments = "";
    Arguments = Arguments + "fa_ClassCd="+quote("") + " ";
    Arguments = Arguments + "fa_UseYn="+quote("Y") + " ";
    Arguments = Arguments + "fa_ReturnFunc="+quote("fn_D1_SetSearchTopFromPopup") + " ";
    Arguments = Arguments + "fa_DealCd="+quote("");
    
	var list = gfn_Dialog("B30","B3101010020U",Arguments,656,400,true,-1,-1);
}

// 자재반납
function fn_D1_SetSearchTopFromPopup(dsResult)
{

	for(var i=0; i<dsResult.GetRowCount(); ++i)
	{
		var nRow = ds_Material.AddRow();
		
		// set default info.
		ds_Material.SetColumn(nRow, "CRT_EMPID",	gfn_GetGlobalValue("G_USER_NO"));
		ds_Material.SetColumn(nRow, "CRT_IP",		gfn_GetGlobalValue("G_IPADDRESS"));
		ds_Material.SetColumn(nRow, "UPD_EMPID",	gfn_GetGlobalValue("G_USER_NO"));
		ds_Material.SetColumn(nRow, "UPD_IP",		gfn_GetGlobalValue("G_IPADDRESS"));
		
		// set material info.
		ds_Material.SetColumn(nRow, "MATERIAL_CD",	dsResult.GetColumn(i, "MATERIAL_CD"));
		ds_Material.SetColumn(nRow, "MATERIAL_NM",	dsResult.GetColumn(i, "MATERIAL_NM"));
		ds_Material.SetColumn(nRow, "MATERIAL_STD",	dsResult.GetColumn(i, "MATERIAL_STD"));

	}

}

function btn_RowDel01_OnClick(obj)
{

	var nRow = ds_Material.row;
	
	if(ds_Material.GetRowType(nRow) == "insert")
	{
		if(confirm("작성된 내용이 있습니다. 그래도 삭제하시겠습니까?"))
		{
			ds_Material.DeleteRow(ds_Material.row);
		}
		
	}else if(confirm("등록된 자재 내역을 삭제하시겠습니까?"))
	{
	
		var strREQ_FLAG = ds_Material.GetColumn(ds_Material.row , "REQ_FLAG");
		if(strREQ_FLAG == "Y")
		{
			alert("자재 연계된 데이터는 삭제가 불가능합니다. \n 삭제를 원하시면 시스템관리자에게 문의바랍니다. ");
			return;
		}
		else
		{
			fn_D1_DetailDelete1();
		}
	}
}

//----------------------------------------------------------------------------------------------
// 함수명: fn_D1_DetailDelete1()
// 설명:   자재 내역 삭제
// arguments
// return 내용: 없음
//----------------------------------------------------------------------------------------------
function fn_D1_DetailDelete1()
{    
	ds_Material.DeleteRow(ds_Material.row);
	
	var strSvcID = "fn_D1_DetailDelete1";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_MaintRepaiWorkFacilRegisMgr-service";  
	var strInDatasets  = "ds_Material=ds_Material:u";
	var strOutDatasets  = "";
	var strArgument  = "deleteMaterial=1";	
	
	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  );
				  
	if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}else{
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
		alert(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을")); 
	}				  
}



//-------------------------------------------------------------------------------
// 기      능   :  파일첨부
//-------------------------------------------------------------------------------
function btn_FileFolder_OnClick(obj)
{
	if(gfn_IsNull(edt_MainterNum.Text))
	{
		alert("등록된 정비보수작업이 없습니다. 등록먼저 해주세요.");
		return;
	}
	else
	{

		var strEnable = "";
		
		// 정비보수 - 작성중(00)
		if( mainter_sts_flag != "00")
		{
			strEnable = "Y";	
		}

		var arg = "fa_Url=handle.do?ServiceName=D1_MaintRepaiWorkFacilRegisMgr-service&MAINTER_NUM="+ mainter_num +"&WORK_FLAG=10 fa_Part=D10 fa_Enable="+strEnable;
		var arrList = gfn_Dialog("D10","D1101010018U",arg,450,280,true,-1,-1);
		edtFileList.text = "";
		for(var i=0; i<arrList.length; i++)
		{
			edtFileList.text += arrList[i];
			if(arrList.length-1 != i) edtFileList.text += "/";
		}		
	}
}

function btn_FileFolder01_OnClick(obj)
{
	if(gfn_IsNull(edt_MainterNum.Text))
	{
		alert("등록된 정비보수작업이 없습니다. 등록먼저 해주세요.");
		return;
	}
	else
	{
	
		var strEnable = "";
		
		// 정비보수 - 접수(20), 정산(20)
		if( ! ( mainter_sts_flag == "20" || mainter_sts_flag == "30" ) )
		{
			strEnable = "Y";	
		}
		else
		{
			// 시공사가 아닐때 막는다
			if( ! fv_COOP_YN )
			{
				strEnable = "Y";			
			}
		}
	
		var arg = "fa_Url=handle.do?ServiceName=D1_MaintRepaiWorkFacilRegisMgr-service&MAINTER_NUM="+ mainter_num +"&WORK_FLAG=20 fa_Part=D10 fa_Enable="+strEnable;
		var arrList = gfn_Dialog("D10","D1101010018U",arg,450,280,true,-1,-1);
		edtFileList01.text = "";
		for(var i=0; i<arrList.length; i++)
		{
			edtFileList01.text += arrList[i];
			if(arrList.length-1 != i) edtFileList01.text += "/";
		}
	}
}


//--------------------------------------------------------------------------------------------------
// validation check
//--------------------------------------------------------------------------------------------------
// 시설물
function fn_D10_Validation01()           
{

	if( rdo_ReqFlag.Value == "01" )
	{
		if( gfn_isNull(edt_FaciNum.Value) )
		{
			alert("[정비보수 작업등록] 시설물 선택을 하지 않았습니다. 시설물을 선택해주세요.");
			cob_FaciNumSub.SetFocus();
			return false;
		}
	}
	
	if( gfn_isNull(cob_BranchCd01.Value) )
	{
		alert("[정비보수 작업등록] "+gfn_GetMessage("M000015","%AAA%","지사는 "));
		cob_BranchCd01.SetFocus();
		return false;
	}
	if( gfn_isNull(cob_ZipNo2.Value) )
	{
		alert("[정비보수 작업등록] "+gfn_GetMessage("M000015","%AAA%","시/구는 "));
		cob_ZipNo2.SetFocus();
		return false;
	}
	
	if( gfn_isNull(cob_TeamCd01.Value) )
	{
		alert("[정비보수 작업등록] "+gfn_GetMessage("M000015","%AAA%","팀은 "));
		cob_TeamCd01.SetFocus();
		return false;
	}
	

	if( gfn_isNull(edt_WorkFirmCd.Text) )
	{
		alert("[정비보수 작업등록] "+gfn_GetMessage("M000015","%AAA%","보수업체는 "));
		edt_WorkFirmCd.SetFocus();
		return false;
	}
	
	if( gfn_isNull(edt_WORK_REQ_DESC.Value) )
	{
		alert("[정비보수 작업등록] "+gfn_GetMessage("M000015","%AAA%","의뢰내용은 "));
		edt_WORK_REQ_DESC.SetFocus();
		return false;
	}
	
	return true;
}


// 정비보수작업 접수내역 (시공사)
function fn_D10_Validation02()           
{
	if( gfn_isNull(cal_ExpectJobYmd.Value) )
	{
		alert("[정비보수작업 접수내역] "+gfn_GetMessage("M000015","%AAA%","예상작업일자는 "));
		cal_ExpectJobYmd.SetFocus();
		return false;
	}
	if( gfn_isNull(cob_ReceiveYn.Value) )
	{
		alert("[정비보수작업 접수내역] "+gfn_GetMessage("M000015","%AAA%","접수여부는 "));
		cob_ReceiveYn.SetFocus();
		return false;
	}
	if( gfn_isNull(cal_ReceiveYmd.Value) )
	{
		alert("[정비보수작업 접수내역] "+gfn_GetMessage("M000015","%AAA%","접수일자는 "));
		cal_ReceiveYmd.SetFocus();
		return false;
	}
	if( gfn_isNull(edt_ReceiverNm.Text) )
	{					
		alert("[정비보수작업 접수내역] "+gfn_GetMessage("M000015","%AAA%","접수자명은 "));
		edt_ReceiverNm.SetFocus();
		return false;
	}

	if( gfn_isNull(cob_AdjType.Value) )
	{
		alert("[정비보수 작업등록] "+gfn_GetMessage("M000015","%AAA%","정산유형은 "));
		cob_AdjType.SetFocus();
		return false;
	}
	
	
	for(var i=0; ds_Material.GetRowCount()>i; i++)
	{
		if( gfn_isNull(ds_Material.GetColumn(i,"MATERIAL_CD")) )
		{
			alert("자재코드를 입력해주세요.");
			ds_Material.row = i;
			return false;
		}

		if( gfn_isNull(ds_Material.GetColumn(i,"PLAN_QTY")) )
		{
			alert("자재요청 수량을 입력해주세요.");
			ds_Material.row = i;
			grd_Mat.SetCellPos(3);
			return false;
		}
		
		if( ds_Material.GetColumn(i,"PLAN_QTY") <= 0 )
		{
			alert("자재요청 수량은 0 이상 입력해주세요.");
			ds_Material.row = i;
			grd_Mat.SetCellPos(3);
			return false;
		}
	}	
	 
	// if(dig_obj_yn.Value == "Y"){
		// if(cal_DigApproFrom.Value == "" || cal_DigApproFrom.Value == NULL){
			// alert(gfn_GetMessage("M000015","%AAA%","굴착승인기간은 "));
			// return 1;
		 // }
		// if(cal_DigApproTo.Value == "" || cal_DigApproTo.Value == NULL){
			// alert(gfn_GetMessage("M000015","%AAA%","굴착승인기간은 "));
			// return 1;
		 // }
		// if(edt_DigAdmitNum.Text == "" || edt_DigAdmitNum.Text == NULL){
			// alert(gfn_GetMessage("M000015","%AAA%","굴착허가번호는 "));
			// return 1;
		 // }	 
	// }else if(gfn_IsNull(dig_obj_yn.Value) || dig_obj_yn.Value == "N"){
		// return;
	// }
	
	return true; 
}
