// 검토(공사비)
// 검토 완료시 공사비 청구일자는 수정불가
function fn_D11_ds_GumTo01()
{
	ds_GumTo01.ClearData();
	ds_GumTo01.AddRow();
	if(ds_Multi_Re.GetColumn(0,"DECIDE_WORK_FLAG") == "Y"){
		chk_Change3.Value = 1;
		chk_Change4.Value = 0;
		cal_UnitPrcYmd.Enable = false;
		cal_RealCostYmd.Enable = false;
		//Button4.Enable = false;
	}else if(ds_Multi_Re.GetColumn(0,"DECIDE_WORK_FLAG") == "N"){
		chk_Change3.Value = 0;
		chk_Change4.Value = 1;
		cal_UnitPrcYmd.Enable = true;
		cal_RealCostYmd.Enable = true;
		Button4.Enable = true;
	}else{
		chk_Change3.Value = 0;
		chk_Change4.Value = 0;
		cal_UnitPrcYmd.Enable = true;
		cal_RealCostYmd.Enable = true;
	}
	
	ds_GumTo01.SetColumn(0,"DECIDE_EMPID_WORK_COST",ds_Multi_Re.GetColumn(0,"DECIDE_EMPID_WORK_COST"));
	ds_GumTo01.SetColumn(0,"DECIDE_EMPID_WORK_COST_NM",ds_Multi_Re.GetColumn(0,"DECIDE_EMPID_WORK_COST_NM"));
	ds_GumTo01.SetColumn(0,"DECIDE_YMD_WORK_COST",ds_Multi_Re.GetColumn(0,"DECIDE_YMD_WORK_COST"));
	ds_GumTo01.SetColumn(0,"DECIDE_RETURN_WHY_WORK_COST",ds_Multi_Re.GetColumn(0,"DECIDE_RETURN_WHY_WORK_COST"));
}

function fn_D11_ds_GumTo04()
{
	if(ds_Multi_Re2.GetColumn(0,"ADJ_TYPE") == "10"){//실비	
		btn_Select_UnitPrc.Enable = false;
		Button4.Enable = true;
	}else if(ds_Multi_Re2.GetColumn(0,"ADJ_TYPE") == "20"){//단가
		btn_Select_UnitPrc.Enable = true;
		Button4.Enable = false;	
	}
}

