//---------------------------------------------------------------------------------
// 기      능   :  정비보수 실비 가져오기
//---------------------------------------------------------------------------------
function Button4_OnClick(obj)
{
    // alert("정비보수 실비 정산 요율 변경으로 인한 보완관계로n\2011년5월8일까지 정비보수 정산이 불가능합니다.");
    // return;
    //자재반납처리 관련 보완 반납수량 존재시 수정세금계산서 발행되어야 실비정산 가능
	if(ds_Material.RowCount() > 0)
	{
		for(var i=0; ds_Material.GetRowCount()>i; i++)
		{
			if(ds_Material.GetColumn(i,"BACK_PROD_QTY") > 0 && ds_Material.GetColumn(i,"BACK_PROD_YN") == 'N')
			{
				alert("자재 반납건에 대한 수정세금계산서 발행이 처리되지 않았습니다. \n자재팀 담당자에게 문의바랍니다..");
				return;
			}
		}	
	}		

	var strACT_STR_TIME = trim(act_str_time.Value); // 작업시작시간
	var strACT_END_TIME = trim(act_end_time.Value);	// 작업종료시간


	// 작업시작시간
	if( gfn_isNull(strACT_STR_TIME) )
	{
		alert("작업시작시간을 입력바랍니다. (예)10:30 ");
		act_str_time.SetFocus();
		return;
	}
	
	if(parseInt(strACT_STR_TIME) < 2400)
	{
		if( !(substr(strACT_STR_TIME,2,2)=="30"||substr(strACT_STR_TIME,2,2)=="00") )
		{
			alert("분은 30분 단위로 입력해 주십시오.");
			act_str_time.SetFocus();					
		}
		
	}
	else
	{
		alert("시간형식이 아닙니다. (예)11:30");
		act_str_time.SetFocus();
	}
			
	// 작업종료시간
	if( gfn_isNull(strACT_END_TIME) )
	{
		alert("작업종료시간을 입력바랍니다. (예)12:30 ");
		act_end_time.SetFocus();
		return;
	}
	if(parseInt(strACT_END_TIME) < 2400)
	{
		if( !(substr(strACT_END_TIME,2,2)=="30"||substr(strACT_END_TIME,2,2)=="00") )
		{
			alert("분은 30분 단위로 입력해 주십시오.");
			act_end_time.SetFocus();					
		}
		
	}
	else
	{
		alert("시간형식이 아닙니다. (예)11:30");
		act_end_time.SetFocus();
	}
	
	var MainterNum     = ds_Multi_Re.GetColumn(ds_Multi_Re.row,"MAINTER_NUM");
	var Yn_Flag  = "";
	var Job_Time = "";
	
	if(act_str_time.Value > act_end_time.Value)
	{
	   // 작업시작 시간 
	   var timestr1 = substr(act_str_time.Value,0,2);
	   var timestr2 = "";
	   if(substr(act_str_time.Value,2,2)=="30")
	   {
			timestr2 = "0.5" ;
	   }
	   else 
	   {
			timestr2 = "0";
	   }
	   // 작업종료시간 
	   var timeend1 = substr(act_end_time.Value,0,2);
	   var timeend2 = "";
	   if(substr(act_end_time.Value,2,2)=="30")
	   {
			timeend2 = "0.5" ;
	   }
	   else 
	   {
			timeend2 = "0";
	   }
	   
	   var timeStrcnt  = toNumber(timestr1)+toNumber(timestr2);
       var timeEndcnt  = toNumber(timeend1)+toNumber(timeend2)+24;
	   
	   Job_Time = timeEndcnt-timeStrcnt;
	}
	else
	{
	   // 작업시작 시간 
	   var timestr1 = substr(act_str_time.Value,0,2);
	   var timestr2 = "";
	   if(substr(act_str_time.Value,2,2)=="30")
	   {
			timestr2 = "0.5" ;
	   }
	   else 
	   {
			timestr2 = "0";
	   }
	   // 작업종료시간 
	   var timeend1 = substr(act_end_time.Value,0,2);
	   var timeend2 = "";
	   if(substr(act_end_time.Value,2,2)=="30")
	   {
			timeend2 = "0.5" ;
	   }
	   else 
	   {
			timeend2 = "0";
	   }
	   
	   var timeStrcnt  = toNumber(timestr1)+toNumber(timestr2);
       var timeEndcnt  = toNumber(timeend1)+toNumber(timeend2);
	   
	   Job_Time = timeEndcnt-timeStrcnt;	
	   
	}
	if(chk_Change3.Value=="1")
	{
		Yn_Flag ="Y"; // 승인
	}
	else
	{
		Yn_Flag ="N"; // 반려 
	}


	// 팝업
	var strArgs  = "MAINTER_DANGER_FLAG=" + quote(MainterNum);
	    strArgs += " YN_FLAG=" + quote(Yn_Flag);
	    strArgs += " JOB_TIME=" + quote(Job_Time);
	    strArgs += " REAL_JOB_YMD=" + ds_Amt.GetColumn(ds_Amt.row,"REAL_JOB_YMD");  
		
	var strArrList = gfn_Dialog("D10","D1601010016U",strArgs,905,576,true,-1,-1);

	if( length( strArrList ) > 0 )
	{
		edt_RealCost.Value = strArrList[0];
	}

}	

