//----------------------------------------------------------------------------------------------
// 함수명: fn_D11_FindCostlist()
// 설명:   정비단가 조회
// arguments
// return 내용: 없음
//----------------------------------------------------------------------------------------------
function fn_D11_FindCostlist()
{	
	var strSvcID = "";
	var strPart = "D10";	
	var strURL = "handle.do?ServiceName=D1_MaintRepaiWorkFacilRegisMgr-service&search9=1"
			   + "&WORK_M_FLAG=" + cob_WorkMFlag.Value
			   + "&PIPE_MATER_FLAG=" + cob_Je.Value
			   + "&PAVEMENT_KIND=" + cob_Po.Value
			   + "&STD_CD=" + cob_Kwan.Value
			   //+ "&=" +cob_Bel.Value
			   ;  
	var strInDatasets  = "";
	var strOutDatasets  = "ds_AmtCost=ds_AmtCost";
	var strArgument  = "";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
}

function edt_Ea_OnCharChanged(obj,strPreText,strPostText)
{

	edt_AmtCost.Value = ToNumber(medt_Unit_Prc.Value) * ToNumber(edt_Ea.Text);
	edt_AmtCost_OnCharChanged();
}

