// 자재신청시 승인 수량
function ds_Material_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "APPRO_QTY")
	{
		var int_planqty = ds_Material.GetColumn(nRow,"PLAN_QTY");
		var int_approqty = ds_Material.GetColumn(nRow,"APPRO_QTY");
		if(int_planqty < int_approqty){
			alert("요청수량보다 승인수량이 큽니다. 수정해주세요");
			ds_Material.SetColumn(nRow,"APPRO_QTY","");
			return;
		}
	}
}
//-------------------------------------------------------------------------------
// 설명     :   FROM ~ TO 체크 (조회)
//-------------------------------------------------------------------------------
function cal_ReqYmdStart_OnChanged(obj,strOldText,strNewText)
{
	if(cal_ReqYmdStart.Value !="" && cal_ReqYmdEnd.Value !="" && cal_ReqYmdStart.Value > cal_ReqYmdEnd.Value){	
		alert("TO 날짜는 FROM 날짜보다 같거나 큰값을 입력하세요.");
		cal_ReqYmdStart.SetFocus();
		cal_ReqYmdStart.value = "";
		cal_ReqYmdEnd.value = "";
		return;
	}	
}

function cal_ReqYmdEnd_OnChanged(obj,strOldText,strNewText)
{
	if(cal_ReqYmdStart.Value !="" && cal_ReqYmdEnd.Value !="" && cal_ReqYmdStart.Value > cal_ReqYmdEnd.Value){	
		alert("TO 날짜는 FROM 날짜보다 같거나 큰값을 입력하세요.");
		cal_ReqYmdStart.SetFocus();
		cal_ReqYmdStart.value = "";
		cal_ReqYmdEnd.value = "";
		return;
	}	
}
//-------------------------------------------------------------------------------
// 설명     :   FROM ~ TO 체크 (내역)
//-------------------------------------------------------------------------------
function cal_DigApproFrom_OnChanged(obj,strOldText,strNewText)
{
	if(cal_DigApproFrom.Value !="" && cal_DigApproTo.Value !="" && cal_DigApproFrom.Value > cal_DigApproTo.Value){	
		alert("TO 날짜는 FROM 날짜보다 같거나 큰값을 입력하세요.");
		cal_DigApproFrom.SetFocus();
		cal_DigApproFrom.value = "";
		cal_DigApproTo.value = "";
		return;
	}	
}



function cal_DigApproTo_OnChanged(obj,strOldText,strNewText)
{
	if(cal_DigApproFrom.Value !="" && cal_DigApproTo.Value !="" && cal_DigApproFrom.Value > cal_DigApproTo.Value){	
		alert("TO 날짜는 FROM 날짜보다 같거나 큰값을 입력하세요.");
		cal_DigApproFrom.SetFocus();
		cal_DigApproFrom.value = "";
		cal_DigApproTo.value = "";
		return;
	}		
}

function CommonCheck()
{
		gfn_D1_SetComboBox_Comcode("",gfn_setDsObject_Comcode("cob_Po"));
		cob_Po.Enable = false;
		gfn_D1_SetComboBox_Comcode("",gfn_setDsObject_Comcode("cob_Kwan"));
		cob_Kwan.Enable = false;
		gfn_D1_SetComboBox_Comcode("",gfn_setDsObject_Comcode("cob_Je"));
		cob_Je.Enable = false;
		gfn_D1_SetComboBox_Comcode("",gfn_setDsObject_Comcode("cob_Bel"));
		cob_Bel.Enable = false;
}


function dig_obj_yn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	// 굴착대상
	if(dig_obj_yn.Value == "Y"){
		edt_ACROSS_A.Enable = true;
		edt_HEIGHT_A.Enable = true;
	}
	else
	{
		edt_ACROSS_A.Value = "";
		edt_ACROSS_A.Enable = false;
		edt_HEIGHT_A.Value = "";
		edt_HEIGHT_A.Enable = false;
		edt_DIG_SCALE_A.Value = "";
	}
}


function work_req_ymd_OnChanged(obj,strOldText,strNewText)
{
	if(work_req_ymd.Value !="" && cal_Work_Req_Ymd_1.Value !="" && work_req_ymd.Value < cal_Work_Req_Ymd_1.Value){	
		if(confirm("공사요청일자가 의뢰일자 이전의 날짜입니다. 입력하시겠습니까?")){
		}else{
			work_req_ymd.SetFocus();
			work_req_ymd.value = "";
			return;
		}
	}		
}
