// 기      능   :  체크 버튼 초기값 및 변동시 동작 
//---------------------------------------------------------------------------------
function chk_Change3_OnClick(obj,strValue)
{
	if (length(cal_UnitPrcYmd.value) == 0 && length(cal_RealCostYmd.value) == 0)
	{
		alert("공사비가 청구되지 않은 건으로 검토가 불가능합니다...");
		return;
	}	
	
	if(chk_Change3.Value  == 1){
	
		if(confirm("공사비 검토 확인을 하시겠습니까?")){
		
			edt_DecideEmpidWorkCost.Text = fv_emp_id;
			edt_DecideEmpidWorkCostNm.Text = fv_emp_nm;
			chk_Change4.Value = 0;
			cal_DecideYmdWorkCost.Value = today();
		
			fn_update3();
		}
		else
		{
			chk_Change3.Value = 0;
		}
	}
}
function chk_Change4_OnClick(obj,strValue)
{
	if (length(cal_UnitPrcYmd.value) == 0 && length(cal_RealCostYmd.value) == 0)
	{
		alert("공사비가 청구되지 않은 건으로 반려가 불가능합니다...");
		return;
	}	
	
	if(chk_Change4.Value  == 1){
	
		if(confirm("공사비 검토 반려를 하시겠습니까?")){
			edt_DecideEmpidWorkCost.Text = fv_emp_id;
			edt_DecideEmpidWorkCostNm.Text = fv_emp_nm;
		 
			chk_Change3.Value = 0;
			cal_DecideYmdWorkCost.Value = "";
	
			fn_update3();
		}
		else
		{
			chk_Change4.Value = 0;
		}
	}	
}
function chk_Change5_OnClick(obj,strValue)
{

	if (length(cal_IndCostYmd.value) == 0)
	{
		alert("간접복구비가 청구되지 않은 건으로 검토가 불가능합니다...");
		return;
	}	

	if(chk_Change5.Value  == 1){
	
		if(confirm("간접복구비 검토 확인을 하시겠습니까?")){
			
		edt_DecideEmpidIndCost.Text = fv_emp_id;
		edt_DecideEmpidIndCostNm.Text = fv_emp_nm;
		
		chk_Change6.Value = 0;
		cal_DecideYmdIndCost.Value = today();

		fn_update4();
		
		}
		else
		{
			chk_Change6.Value = 0;
		}
	}	
}
