function chk_Change10_OnClick(obj,strValue)
{

	if( ! fn_D1_TeamManagerYN( fv_emp_id) )
	{
		alert("[권한]팀장만 반려가 가능합니다. 로그인자가 팀장(325)권한이 아닙니다.");
		chk_Change10.Value = 0;
		return;
	}
	
	edt_AssentEmpidWorkCost.text = fv_emp_id;
	edt_AssentEmpidWorkCostNm.text = gfn_GetGlobalValue("G_NAME");
	
	if(chk_Change10.Value  == 1)
	{
		// if(gfn_IsNull(cob_AssentEmpidWorkCostNm.Text)){
			// alert("공사비 승인자를 입력해주세요.");
			// chk_Change10.Value = 0;
			// return;
		// }else{
			if(confirm("공사비 승인 반려를 하시겠습니까?")){
				chk_Change9.Value = 0;
				cal_AssentYmdWorkCost.Value = today();
				
				fn_update7();
			}
			else
			{
				chk_Change10.Value = 0;				
			}
		//}
	}	
}
