//-------------------------------------------------------------------------------
//	  GIS 위치보기 
//-------------------------------------------------------------------------------
function btn_gis_OnClick(obj)
{
	var strArgs = "";
	var strFACI_NUM = ds_Multi_Re.GetColumn(0,"FACI_NUM");
	var strFACI_FLAG = ds_Multi_Re.GetColumn(0,"FACI_FLAG");
	var strBUNGI = "";
	
	if ( gfn_IsNull(strFACI_FLAG))
	{
		alert("GIS위치보기에 필요한 시설물구분코드가 등록되지 않았습니다.");
		return;
	}
	
	// 시설물
	if(rdo_ReqFlag.Value == '01')
	{
		if(strFACI_FLAG == "GR") // 정압실
		{
			strBUNGI = "04";
		}
		else if(strFACI_FLAG == "GV") // 정압기
		{
			strBUNGI = "05";
		}
		else if(strFACI_FLAG == "VB") // 밸브박스
		{
			strBUNGI = "06";

		}
		else if(strFACI_FLAG == "VV")// 밸브
		{
			strBUNGI = "07";
		}
		else if(strFACI_FLAG == "TB")// 테스트박스
		{
			strBUNGI = "10";
		}
		else if(strFACI_FLAG == "RE") // 정류기
		{
			strBUNGI = "11";
															
		}
		else if(strFACI_FLAG == "DR") // 배류기
		{
			strBUNGI = "12";
		}
		else if(strFACI_FLAG == "WD") // 수취기
		{
			strBUNGI = "13";
		}
		
		strArgs = "BUNGI="+quote(strBUNGI);
		strArgs += " FACI_NUM=" +  quote(strFACI_NUM);
		
	}

	// 아무것도 선택이 안되어 있으면
	if(strBUNGI == "")
	{
		// 시설물 외
		strBUNGI = "01";
		
		strArgs = "BUNGI="+quote(strBUNGI);		
		strArgs += " GU_NM="     + quote(cob_ZipNo2.Text) ;                    // 구명
		strArgs += " DONG_NM_H=" + quote(ds_Multi_Re.GetColumn(0,"BLD_DONG")); // 동명
		strArgs += " FNU="       + quote(ds_Multi_Re.GetColumn(0,"ADDR_M"));   // 본번
		strArgs += " SNU="       + quote(ds_Multi_Re.GetColumn(0,"ADDR_S"));   // 부번
		
	}
	
	// WEB GIS
	gfn_Dialog("D10","D1402060011U",strArgs,1208,840,true,-1,-1);
	
}


