// 준공도 등록 
function btn_Completion_OnClick(obj)
{
	var Map_Gubun = ds_Multi_Re.GetColumn(ds_Multi_Re.row,"MAP_UPD_OBJ_YN");  // 도면수정대상일 경우 준공도 등록 화면으로 이동
	if(Map_Gubun == "Y")
	{
		var strArg  = "";
		gfn_Dialog("D10","D1402060010U",strArg,660,400,true,-1,-1);
	}
	else
	{
		alert("준공도 등록 대상이 아닙니다.");
		return;
	}
}

function cob_MaterFlag_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)	
{
	var MaterFlag = obj.Value ;
	var PipeFlag  = '';
	
	if(obj == eval("cob_MaterFlag")) {PipeFlag = "cob_PipeDiamCd";}
	
	switch(MaterFlag)
	{
		//10 PLP : D10300
		case "10" : gfn_SetComboBox_Comcode("D10300",gfn_setDsObject_Comcode(PipeFlag));	
		break;
		//20 PE : D10301
		case "20" : gfn_SetComboBox_Comcode("D10301",gfn_setDsObject_Comcode(PipeFlag));	
		break;
		//30 ZS : D10302
		case "30" : gfn_SetComboBox_Comcode("D10302",gfn_setDsObject_Comcode(PipeFlag));	
		break;
		//40 AS : D10303
		case "40" : gfn_SetComboBox_Comcode("D10303",gfn_setDsObject_Comcode(PipeFlag));	
		break;
		//50 PT : D10304
		case "50" : gfn_SetComboBox_Comcode("D10304",gfn_setDsObject_Comcode(PipeFlag));	
		break;
	}	
}

// 정비보수작업 접수내역 - 예상작업일자
function cal_ExpectJobYmd_OnChanged(obj,strOldText,strNewText)
{
	if(!gfn_IsNull(cal_ExpectJobYmd.Value) && cal_ExpectJobYmd.Value < today()){
		alert("예상작업일자가 오늘날짜 이전의 날짜입니다. 이후의 날짜로 변경하십시오.");
		cal_ExpectJobYmd.SetFocus();
		cal_ExpectJobYmd.Value = "";
		return;
	}else if(!gfn_IsNull(cal_ExpectJobYmd.Value) && cal_ExpectJobYmd.Value >= today()){
		edt_ReceiverNm.Text = fv_emp_nm;
		cob_ReceiveYn.Value = "Y";
		cob_ReceiveYn_OnChanged();
	}else{
		edt_ReceiverNm.Text = "";
		cob_ReceiveYn.Value = "";
		cob_ReceiveYn_OnChanged();
	}
}
// 정비보수작업 접수내역 - 접수여부
function cob_ReceiveYn_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(cob_ReceiveYn.Value == "Y"){
		cal_ReceiveYmd.Value = today();			//접수일자 Default 
	}else{
		cal_ReceiveYmd.Value = "";
	}
}

function chk_Change1_OnClick(obj,strValue)
{
		if(danger_appro_obj_yn.Value == 'Y' && (length(edt_Danger_Num.Text) != 11))
		{
			alert("위험작업 대상으로 위험작업 내역이 등록되지 않았습니다..");
			return;
		}	


		if( ! fn_D1_TeamManagerYN( fv_emp_id) )
		{
			alert("[권한]팀장만 승인 가능합니다. 로그인자가 팀장(325)권한이 아닙니다.");
			chk_Change1.Value = 0;
			return;
		}
		
		
		if(chk_Change1.Value == 1)
		{
			cob_DecideEmpidReq.Enable = true;
			cal_Decide_Ymd_Req.Enable = true;
			cal_Decide_Ymd_Req.Value = today();
			decide_return_why_req.Enable = true;
		}
		else
		{
			cob_DecideEmpidReq.Enable = false;
			cal_Decide_Ymd_Req.Enable = false;
			decide_return_why_req.Enable = false;		
			cob_DecideEmpidReq.Value = "";
			cal_Decide_Ymd_Req.Value = "";
			decide_return_why_req.Text = "";
		}
		
	//}
}

