{
	PrintScreen(true);
}
/*===============================================================================
 * 기      능   :  공통 Excel
===============================================================================*/
function fn_CommonExcelExport()
{


	fn_D1_FindListExcel();

	var SearchCondition= "";
    SearchCondition = SearchCondition + "지사코드 :" + RPad(cob_Branchcd.Text," ",20);
    SearchCondition = SearchCondition + "팀코드 :" + RPad(cob_TeamCdSearch.Text," ",20);
    SearchCondition = SearchCondition + "시/구 :" + RPad(cob_ZipNo1.Text," ",20);
    SearchCondition = SearchCondition + "시설물구분 :" + RPad(cbo_ReqFlagSearch.Text +" "+ cob_FaciFlagSearch.Text," ",20);
    SearchCondition = SearchCondition + "작업의뢰일자 :" + RPad(cal_ReqYmdStart.Value +" ~ "+ cal_ReqYmdEnd.Value," ",20);
    SearchCondition = SearchCondition + "공사진행상태 :" + RPad(cob_MainterStsFlag.Text," ",20);
    
    gfn_ExportExcel("정비보수 작업내역", 5, SearchCondition, "grd_ExcelList"); 	
}


//--------------------------------------------------------------------------------------------------
// 로우 변경시
//--------------------------------------------------------------------------------------------------
function ds_Multi_OnRowPosChanged(obj,nOldRow,nRow)
{
	
//	trace("ds_Multi_OnRowPosChanged - " +nRow);
	
	if(nRow < 0) 
	{
		//로우 없을경우 초기화
		ds_Multi_Re.ClearData();
		ds_Material.ClearData();
		
		fn_D10_ds_Multi_Re1();      // 의뢰승인 데이터출력
		fn_D10_ds_Multi_Re2();		// 정비보수작업 접수내역
		fn_D10_ds_Amt();            // 정비보수작업 결과내역
		fn_D11_ds_GumTo01_1();		// 정비보수작업내역 GIS 일자조회
		fn_D11_ds_GumTo01();  // 검토(공사비)
		fn_D11_ds_GumTo02();  // 검토(간접복구비)
		fn_D11_ds_GumTo03();  // 검토(직접복구비)
		fn_D11_ds_Sungin01(); // 승인(공사비)
		fn_D11_ds_Sungin02(); // 승인(간접복구비)
		fn_D11_ds_Sungin03(); // 승인(직접복구비)
	
		return;
	}
	else
	{
	
		// detail Grid 조회
		mainter_num      = ds_Multi.GetColumn(nRow,"MAINTER_NUM");
		mainter_sts_flag = ds_Multi.GetColumn(nRow,"MAINTER_STS_FLAG");
		// 결재상태 
		decidestsflag    = ds_Multi.GetColumn(nRow,"DECIDE_STS_FLAG");
	
		decideworkflag   = ds_Multi.GetColumn(nRow,"DECIDE_WORK_FLAG");
		decideindflag    = ds_Multi.GetColumn(nRow,"DECIDE_IND_FLAG");
		decidedirflag    = ds_Multi.GetColumn(nRow,"DECIDE_DIR_FLAG");
		
		assentworkflag   = ds_Multi.GetColumn(nRow,"ASSENT_WORK_FLAG");
		assentindflag    = ds_Multi.GetColumn(nRow,"ASSENT_IND_FLAG");
		assentdirflag    = ds_Multi.GetColumn(nRow,"ASSENT_DIR_FLAG");
		
		digobjyn   		 = ds_Multi.GetColumn(nRow,"DIG_OBJ_YN_A");
//		trace("mainter_num - " +mainter_num);
	
		fn_D1_SearchDetailFile_List();		// 첨부파일 조회 
		fn_D1_SearchDetailFile_List_20();   // 첨부파일 조회 
		
		// detail Grid 조회
		fn_D1_SearchDetail(nRow); 		// 정비보수 내역 조회

		btn_gis.Enable = true;
		
		if(digobjyn == "Y"){
			edt_ACROSS_A.Enable = true;
			edt_HEIGHT_A.Enable = true;
		}else if(digobjyn == "N"){
			edt_ACROSS_A.Enable = false;
			edt_HEIGHT_A.Enable = false;	
		}
		
	
//		trace("mainter_sts_flag " + mainter_sts_flag);
	}
	
	
	danger_appro_obj_yn_OnChanged();
}
	

function fn_D1_OnRowPosChanged()
{	


	//var str_EXPECT_JOB_YMD = ds_Multi_Re.GetColumn(0,"EXPECT_JOB_YMD");
    //trace("str_EXPECT_JOB_YMD:"+str_EXPECT_JOB_YMD);
	
	//approqty = ds_Material.GetColumn(grd_Mat.ClickedRow,"APPRO_QTY");				// 승인수량
	//deliqty  = ds_Material.GetColumn(grd_Mat.ClickedRow,"DELI_QTY");				// 출고수량 
	strMAINTER_REQ_FLAG  = ds_Multi.GetColumn(ds_Multi.row ,"MAINTER_REQ_FLAG");
	
	var nCntRow = ds_Material.SearchRow(" APPRO_QTY > 0 "); // 승인된수량
	
//trace("ds_Material.SearchRow:"+nCntRow);

	if( nCntRow != -1 )
	{
		// 승인된내역이 있다.
		approqty = 10;	
	}
	else
	{

		approqty = 0;

	}
	
	var nCntRowDeli = ds_Material.SearchRow(" DELI_QTY > 0 "); // 출고된수량
	if( nCntRowDeli != -1 )
	{
		// 출고된내역이 있다.
		deliqty = 10;	

	}
	else
	{
		deliqty = 0;
	}
		
//trace("approqty:"+approqty + " deliqty:"+deliqty);	

	var count = ds_Material.GetRowCount();
		
//	trace("fn_D1_OnRowPosChanged mainter_sts_flag " + mainter_sts_flag);
//	trace("fn_D1_OnRowPosChanged count " + count);

	//############################################################################
	// 모든화면을 막는다.
	//############################################################################
	
	fn_D10_ChkButton01(); 	//체크버튼 초기값 0 
	fn_D10_ChkButton02(); 	//체크버튼 비활성화	
	
	rdo_ReqFlag.Enable = false;  // 시설물구분
	fn_D11_Common01();		// 등록내용 비활성화 ------------------------------1
	fn_D11_Common03();		// 시설물 외 비활성  ------------------------------2

	chk_Change1.Enable = false; // 의뢰승인
	fn_D11_Common05(); 			// 의뢰승인 비활성화
	btn_FileFolder.Enable = true;
	
	//flase에서 true로 변경 자재신청내역 비활성화 --권기철 2010.11.4
	grd_Mat.Editable = true;    
	grd_Mat.Enable = true;
	//grd_MatAdd.Enable = false; // 추가자재신청내역 비활성화 
	//권기철 해제 20100701 추가자재내역 5건이상시 스크롤이 안움직임
	grd_MatAdd.Enable = true; // 추가자재신청내역 비활성화 
	
	btn_Material.Enable = false; // 자재승인버튼 비활성화

	btn_AddMaterial.Enable = false;

	fn_D11_Common07();			 // 접수내역 비활성화  - fn_D11_Common08()
	fn_D11_Common25();			 // 굴착대상일경우 비활성화
	
	btn_FileFolder01.Enable = true; 		
		
	fn_D11_Common09();			 // 결과내역 비활성 --------------------------------3
	fn_D11_Common11(); 			 // 도면접수, GIS이관, GIS확인 비활성 --------------4	
	
	fn_D11_Common13();	// 검토 공사비 비활성 -----------------------------------------5
	fn_D11_Common15();	// 검토 간접복구비 비활성
	fn_D11_Common17();	// 검토 직접복구비 비활성
	fn_D11_Common19();	// 승인 공사비 비활성
	fn_D11_Common21();	// 승인 간접복구비 비활성
	fn_D11_Common23();	// 승인 직접복구비 비활성		
	
	//btn_Completion.Enable = false; // 준공도 등록 (도면수정대상 일때만 활성화)
		
	// 데이터값 조회
	fn_D10_ds_Multi_Re1();      // 의뢰승인 데이터출력
	fn_D10_ds_Multi_Re2();		// 정비보수작업 접수내역
	fn_D10_ds_Amt();            // 정비보수작업 결과내역
	fn_D11_ds_GumTo01_1();		// 정비보수작업내역 GIS 일자조회
	fn_D11_ds_GumTo01();  // 검토(공사비)
	fn_D11_ds_GumTo02();  // 검토(간접복구비)
	fn_D11_ds_GumTo03();  // 검토(직접복구비)
	fn_D11_ds_Sungin01(); // 승인(공사비)
	fn_D11_ds_Sungin02(); // 승인(간접복구비)
	fn_D11_ds_Sungin03(); // 승인(직접복구비)
	
	cal_GumTo01.Value = cal_DecideYmdWorkCost.Value;
	cal_GumTo02.Value = cal_DecideYmdWorkCost.Value;
	cal_GumTo03.Value = cal_DecideYmdIndCost.Value;
	cal_GumTo04.Value = cal_DecideYmdDirCost.Value;
	
	cal_SungIn01.Value = cal_AssentYmdWorkCost.Value;
	cal_SungIn02.Value = cal_AssentYmdWorkCost.Value;
	cal_SungIn03.Value = cal_AssentYmdIndCost.Value;
	cal_SungIn04.Value = cal_AssentYmdDirCost.Value;	

	if( mainter_sts_flag != '00')
	{
		chk_Change1.Value = 1;	
	}
	
	//############################################################################
	// 권한에 따라서 화면을 풀어준다.
	//############################################################################
	if(mainter_sts_flag == "00")
	{ 		
//trace("###  1 ### " );	
			// <<<<< 공사 접수 상태 / 자재승인 요청한 상태 >>>>>
			if( fv_COOP_YN ) // 협력사이면 수정불가능
			{
			
			}
			else // SCG
			{
				// ################################################################################
				// <<<< 공사 등록 상태 >>>>
				// ################################################################################
				fn_D11_Common02();				// 등록내용 활성화 ------------------------------1
				fn_D11_Cancel_on();			
				btn_search.Enable = false;
				
				// 시설물외(02) 
				if(strMAINTER_REQ_FLAG == "02")
				{
					fn_D11_Common04();				// 시설물 외 일경우 활성화

				}else{
					fn_D11_Common03();				// 시설물 외 비활성
				}	
				
				//fn_D11_Common06(); 				// 의뢰승인 활성화 
				grd_Mat.Editable = true; 			// 자재신청내역 활성화
				grd_Mat.Enable = true;
				grd_MatAdd.Enable = true; 		// 추가자재신청내역 활성화
				
				chk_Change1.Enable = true;
				chk_Change1.Value = 0;	
								
				btn_FileFolder.Enable = true;
			}

	}
	else if(mainter_sts_flag == "10") // 의뢰(10)
	{   
	
//trace("###  2 ### " );	
		// ################################################################################
	    // <<<< 공사 승인 상태 >>>>
	    // ################################################################################
	    
	    
	    	// <<<<< 공사 접수 상태 / 자재승인 요청한 상태 >>>>>
			if( fv_COOP_YN ) // 협력사이면 수정가능
			{
			
				fn_D11_Common08();						// 접수내역 활성화 
				
				cob_JuyaFlag.Value = "10";
				
				if(dig_obj_yn.Value == "Y"){
					fn_D11_Common26();					// 굴착대상일경우 활성화
				}
				
				cob_DigObjYn.Value = dig_obj_yn.Value; 	//굴착대상 
				
				edt_AcrossB.Value = edt_ACROSS_A.Value;
				edt_HeightB.Value = edt_HEIGHT_A.Value;
				dig_scale.Value = edt_DIG_SCALE_A.Value;
		
				if(map_upd_obj_yn.Value == "Y"){		// 준공도등록 버튼 활성여부 
					btn_Completion.Enable = true;
				}
				
				grd_Mat.Editable = true; 						// 자재신청내역 활성화
				grd_Mat.Enable = true;
				grd_MatAdd.Enable = true; 					// 추가자재신청내역 활성화
				grd_Mat.SetCellProp("body",0,"Edit","normal"); // 자재코드
