function btn_AddMaterial_OnClick(obj)
{
	//alert("[2009년 8월 25일]권기철과장 추가요청 - 2009년 8월 26일부터 수정합니다.");
	
	if( ! ds_Material.GetUpdate() )
	{
		//변경된 내용이 없습니다.
		alert(gfn_GetMessage("M000008", "%AAA%", "변경된 내용이"));		
		return;
	}
	
	
	for(var i=0; ds_Material.GetRowCount()>i; i++)
	{
		if( gfn_isNull(ds_Material.GetColumn(i,"MATERIAL_CD")) )
		{
			alert("자재코드를 입력해주세요.");
			ds_Material.row = i;
			return false;
		}

		if( gfn_isNull(ds_Material.GetColumn(i,"PLAN_QTY")) )
		{
			alert("자재요청 수량을 입력해주세요.");
			ds_Material.row = i;
			grd_Mat.SetCellPos(3);
			return false;
		}
		
		if( ds_Material.GetColumn(i,"PLAN_QTY") <= 0 )
		{
			alert("자재요청 수량은 0 이상 입력해주세요.");
			ds_Material.row = i;
			grd_Mat.SetCellPos(3);
			return false;
		}
	}	
	
	var str_MAINTER_NUM = ds_Multi_Re.GetColumn(0,"MAINTER_NUM");
	
	for(var i=0; ds_Material.GetRowCount()>i; i++)
	{

		// 추가된 로우 셋팅
		if (ds_Material.GetRowType(i) == "insert") {

			ds_Material.SetColumn(i, "MAINTER_NUM", str_MAINTER_NUM);	
			ds_Material.SetColumn(i, "CRT_EMPID",   gfn_GetGlobalValue("G_USER_NO"));
			ds_Material.SetColumn(i, "CRT_IP",      gfn_GetLocalIpAddress()); 
			ds_Material.SetColumn(i, "UPD_EMPID",   gfn_GetGlobalValue("G_USER_NO"));	
			ds_Material.SetColumn(i, "UPD_IP",      gfn_GetLocalIpAddress()); 
		}
		
		// 수정된 로우 셋팅
		if (ds_Material.GetRowType(i) == "update"){

			ds_Material.SetColumn(i, "UPD_EMPID", gfn_GetGlobalValue("G_USER_NO"));
			ds_Material.SetColumn(i, "UPD_IP",    gfn_GetLocalIpAddress());
		
		}	// update end	
	
	}
	
	fv_FindRow_ShowRow = 1;
	fv_FindRow_ShowKey = str_MAINTER_NUM;     //정비보수번호	
	
	var strSvcID = "updateAdd";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_MaintRepaiWorkFacilRegisMgr-service";
	var strInDatasets  = "ds_Material=ds_Material:u";
	var strOutDatasets  = "";
	var strArgument  = "updateAdd=1";	

	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  );
				  
	if(ErrorCode < 0)
	{    
		alert(ErrorMsg);	
		return;
	}else{
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
		alert(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을")); 
		fn_CommonSearch();
	}	
	
}

function edt_AcrossB_OnCharChanged(obj,strPreText,strPostText)
{
	dig_scale.Value = ToNumber(edt_AcrossB.Value)*ToNumber(edt_HeightB.Value);
}

function edt_HeightB_OnCharChanged(obj,strPreText,strPostText)
{
	dig_scale.Value = ToNumber(edt_AcrossB.Value)*ToNumber(edt_HeightB.Value);	
}

// 위험작업 화면으로 이동
function btn_Danger_OnClick(obj)
{

	if(gfn_IsNull( edt_MainterNum.Text ))
	{
		alert("등록된 정비보수작업이 없습니다. 등록먼저 해주세요.");
		return;
	}
	else
	{
	
		if( danger_appro_obj_yn.Value != "Y") 
		{
			alert("위험작업 대상일경우만 등록이 가능합니다.");
			return;
		}
		
		var strArg = "";
		
		// 위험작업번호(가정관) 
		if( gfn_IsNull(edt_Danger_Num.Text) ) 
		{
			// 2013.05.31. 이건민 - 안전공급 개선사항 (위험작업 가정관 연계시 정비보수 자동 셋팅)
			fn_D1_WorkFirmInfo();

			// 가정관에 저장할 외래키
			var strDANGER_STOP_NUM = ds_Multi_Re.GetColumn(0, "MAINTER_NUM");
			var strDANGER_STOP_FLAG = "00";
			// 신규등록
			strArg += "MAIN_FLAG=NEW";
			strArg += " DANGER_STOP_NUM="+quote(strDANGER_STOP_NUM);
			strArg += " DANGER_STOP_FLAG="+quote(strDANGER_STOP_FLAG);
			
			strArg += " BRANCH_CD="+quote(cob_BranchCd01.Value);     // 지사
			strArg += " TEAM_CD="+quote(cob_TeamCd01.Value);         // 팀
			strArg += " OLD_MATER_FLAG="+quote(cob_MaterFlag.Value); // 기존재질
			strArg += " OLD_DIAM_CD="+quote(cob_PipeDiamCd.Value);   // 기존관경
			strArg += " OLD_PRESS_FLAG="+quote(cob_PressFlag.Value); // 기존압력
					
			strArg += " JOB_REQ_YMD="+quote(work_req_ymd.Value); // 작업요청일자 (공사요청일자) - 2009-11-09 권기철과장
			//strArg += " JOB_REQ_YMD="+quote(cal_RealJobYmd1.Value); // 작업요청일자
			strArg += " JOB_REQ_STR_TIME="+quote(act_str_time.Value); // 작업시작시간
			strArg += " JOB_REQ_END_TIME="+quote(act_end_time.Value); // 작업종료시간
	
			strArg += " WORK_FIRM_CD="		+quote(edt_WorkFirmCd.Text); // 시공사코드
			//strArg += " WORK_FIRM_CD_NM="+quote(edt_WorkFirmNm.Text); // 시공사명
			strArg += " WORK_FIRM_CD_NM="	+quote(cob_WorkFirmNm.Text); // 시공사명
			strArg += " REPRE_NM="       	+quote( ds_WorkFirmInfo.GetColumn(0, "REPRE_NM"      	) ); // 대표자명
			strArg += " WORK_FIRM_TEL_DDD="	+quote( ds_WorkFirmInfo.GetColumn(0, "TEL_DDD"      	) ); // 시공사전화번호
			strArg += " WORK_FIRM_TEL_EXN="	+quote( ds_WorkFirmInfo.GetColumn(0, "TEL_EXN"      	) ); // 시공사전화번호
			strArg += " WORK_FIRM_TEL_NUM="	+quote( ds_WorkFirmInfo.GetColumn(0, "TEL_NUM"      	) ); // 시공사전화번호
						
			strArg += " ZIP_NO1="      +quote( ds_Multi_Re.GetColumn(0, "ZIP_NO1"      ) ); // 우편번호1   
			strArg += " ZIP_NO2="      +quote( ds_Multi_Re.GetColumn(0, "ZIP_NO2"      ) ); // 우편번호2   
			strArg += " ZIP_SEQ="      +quote( ds_Multi_Re.GetColumn(0, "ZIP_SEQ"      ) ); // 우편일련번호
			strArg += " HILL="         +quote( ds_Multi_Re.GetColumn(0, "HILL"         ) ); // 산번지      
			strArg += " ADDR_M="       +quote( ds_Multi_Re.GetColumn(0, "ADDR_M"       ) ); // 본번        
			strArg += " ADDR_S="       +quote( ds_Multi_Re.GetColumn(0, "ADDR_S"       ) ); // 부번        
			strArg += " BLD_DONG="     +quote( ds_Multi_Re.GetColumn(0, "BLD_DONG"     ) ); // 건물동      
			strArg += " HOSU="         +quote( ds_Multi_Re.GetColumn(0, "HOSU"         ) ); // 호수        
			strArg += " SECOND_ADDR="  +quote( ds_Multi_Re.GetColumn(0, "SECOND_ADDR"  ) ); // 보조주소    
			strArg += " LOT_NUM_UNION="+quote( ds_Multi_Re.GetColumn(0, "LOT_NUM_UNION") ); // 지번주소조합

			// 가정관연계
			gfn_NewWindowAndExistClose("D10","D1501010011F",strArg);
		}
		else
		{
			// 조회할 KEY
			var strDANGER_STOP_NUM = ds_Multi_Re.GetColumn(0, "DANGER_NUM");
			var strDANGER_STOP_FLAG = "20";	

			// 기존화면조회
			strArg += "MAIN_FLAG=STOP";
			strArg += " DANGER_STOP_NUM="+quote(strDANGER_STOP_NUM);
			strArg += " DANGER_STOP_FLAG="+quote(strDANGER_STOP_FLAG);
			
			strArg += " BRANCH_CD="+quote(cob_BranchCd01.Value); // 지사
			strArg += " TEAM_CD="+quote(cob_TeamCd01.Value);     // 팀
			
			gfn_NewWindowAndExistClose("D10","D1501010011F",strArg);
		}
	}
}

