// 정비(실비) 내역서 
function bt_Oz_OnClick(obj)
{
    if(ds_Multi_Re2.GetColumn(ds_Multi_Re2.row,"ADJ_TYPE") == '20')
	{
		alert("단가건입니다...");
		return;
	}
	
    if(ds_Amt.GetColumn(ds_Amt.row,"WORK_AMT_REAL_COST") == 0)
	{
		alert("공사비 정산 전 입니다.");
		return;		
	}
	
    var MainterNum = ds_Multi_Re.GetColumn(ds_Multi_Re.row,"MAINTER_NUM");
	var strTitle ="정비(실비) 내역서"; 
	var strPart ="D10" ;         		
	var strReport="D1601010010P"; 
	var strParameter="MAINTER_NUM="+ MainterNum;
	         	
    //	공통 레포트 팝업 함수 호출 
	gfn_ReportPopup(strTitle,strPart,strReport,strParameter);	
}




// 정비보수의뢰구분(10:시설물, 20:시설물 외)  변경시
function ds_Multi_Re_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "MAINTER_REQ_FLAG" )
	{
		// 시설물
		if( varNewValue == "01")
		{
			fn_D11_Common02();  					//정비보수 작업등록 true
			btn_search.Enable = true;
			cob_ZipNo2.Enable = false;
			
			fn_D11_Common03();  					//정비보수 작업등록 false(시설물외)		

			cob_BranchCd01_OnChanged();	
			
			ds_Multi_Re.SetColumn(nRow, "FACI_FLAG", "");// 시설물구분
			ds_Multi_Re.SetColumn(nRow, "FACI_NM"  , "");// 시설물명

			ds_Multi_Re.SetColumn(nRow, "PIPE_KIND",    ""); // 배관종류
			ds_Multi_Re.SetColumn(nRow, "PRESS_FLAG",   ""); //압력구분
			ds_Multi_Re.SetColumn(nRow, "MATER_FLAG",   ""); // 재질구분
			ds_Multi_Re.SetColumn(nRow, "PIPE_DIAM_CD", ""); // 배관관경
			
			ds_Multi_Re.SetColumn(nRow, "ZIP_NO1"      , "");  // 우편번호1
			ds_Multi_Re.SetColumn(nRow, "ZIP_NO2"      , "");  // 우편번호2
			ds_Multi_Re.SetColumn(nRow, "ZIP_SEQ"      , "");  // 우편일련번호
			ds_Multi_Re.SetColumn(nRow, "HILL"         , "");  // 산번지
			ds_Multi_Re.SetColumn(nRow, "ADDR_M"       , "");  // 본번
			ds_Multi_Re.SetColumn(nRow, "ADDR_S"       , "");  // 부번
			ds_Multi_Re.SetColumn(nRow, "BLD_DONG"     , "");  // 건물동
			ds_Multi_Re.SetColumn(nRow, "HOSU"         , "");  // 호수
			ds_Multi_Re.SetColumn(nRow, "SECOND_ADDR"  , "");  // 보조주소
			ds_Multi_Re.SetColumn(nRow, "LOT_NUM_UNION", "");  // 지번주소조합
				
			ds_Multi_Re.SetColumn(nRow, "FACI_NUM"     , "");  // 시설물코드	
			ds_Multi_Re.SetColumn(nRow, "DETAIL_NUM"   , "");  // 상세번호	
			
			
		}
		else
		{
			
			// 시설물 외
			fn_D11_Common02();  					//정비보수 작업등록 true
			btn_search.Enable = false;
			cob_ZipNo2.Enable = true;
			fn_D11_Common04();  					//정비보수 작업등록 true(시설물외)	
	
			cob_BranchCd01_OnChanged();	
					
			// 시설물 외 (PP)
			ds_Multi_Re.SetColumn(nRow, "FACI_FLAG", "PP"      ); // 시설물구분
			ds_Multi_Re.SetColumn(nRow, "FACI_NM"  , "기타등등"); // 시설물명
			
			ds_Multi_Re.SetColumn(nRow, "PIPE_KIND",    ""); // 배관종류
			ds_Multi_Re.SetColumn(nRow, "PRESS_FLAG",   ""); //압력구분
			ds_Multi_Re.SetColumn(nRow, "MATER_FLAG",   ""); // 재질구분
			ds_Multi_Re.SetColumn(nRow, "PIPE_DIAM_CD", ""); // 배관관경
			
			ds_Multi_Re.SetColumn(nRow, "ZIP_NO1"      , "");  // 우편번호1
			ds_Multi_Re.SetColumn(nRow, "ZIP_NO2"      , "");  // 우편번호2
			ds_Multi_Re.SetColumn(nRow, "ZIP_SEQ"      , "");  // 우편일련번호
			ds_Multi_Re.SetColumn(nRow, "HILL"         , "");  // 산번지
			ds_Multi_Re.SetColumn(nRow, "ADDR_M"       , "");  // 본번
			ds_Multi_Re.SetColumn(nRow, "ADDR_S"       , "");  // 부번
			ds_Multi_Re.SetColumn(nRow, "BLD_DONG"     , "");  // 건물동
			ds_Multi_Re.SetColumn(nRow, "HOSU"         , "");  // 호수
			ds_Multi_Re.SetColumn(nRow, "SECOND_ADDR"  , "");  // 보조주소
			ds_Multi_Re.SetColumn(nRow, "LOT_NUM_UNION", "");  // 지번주소조합
				
			ds_Multi_Re.SetColumn(nRow, "FACI_NUM"     , "");  // 시설물코드	
			ds_Multi_Re.SetColumn(nRow, "DETAIL_NUM"   , "");  // 상세번호	
						
		}
	}
}

// 추가자재 등록
function btn_RowAdd02_OnClick(obj)
{
	var nRow = ds_MaterialAdd.AddRow();
	ds_MaterialAdd.SetColumn(nRow, "ATTACH_FILE_YN", "N");
	
}
// 추가자재 삭제
function btn_RowDel02_OnClick(obj)
{
	var nRow = ds_MaterialAdd.row;
	
	if(ds_MaterialAdd.GetRowType(nRow) == "insert")
	{
		if(confirm("작성된 내용이 있습니다. 그래도 삭제하시겠습니까?"))
		{
			ds_MaterialAdd.DeleteRow(ds_MaterialAdd.row);
		}
		
	}else if(confirm("등록된 자재 내역을 삭제하시겠습니까?"))
	{
		fn_D1_DetailDelete2();
	}
}

//----------------------------------------------------------------------------------------------
// 함수명: fn_D1_DetailDelete2()
// 설명:   추가 자재 내역 삭제
// arguments
// return 내용: 없음
//----------------------------------------------------------------------------------------------
function fn_D1_DetailDelete2()
{    
	ds_MaterialAdd.DeleteRow(ds_MaterialAdd.row);
	
	var strSvcID = "fn_D1_DetailDelete2";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_MaintRepaiWorkFacilRegisMgr-service";  
	var strInDatasets  = "ds_MaterialAdd=ds_MaterialAdd:u";
	var strOutDatasets  = "";
	var strArgument  = "deleteMaterialAdd=1";	
	
	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  );
				  
	if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}else{
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
		alert(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을")); 
	}				  
}

// 추가자재 변경시
function ds_MaterialAdd_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( varOldValue != varNewValue)
	{
		if ( strColumnID == "UNIT_PRC" || strColumnID == "EA")
		{
			var strSum = ds_MaterialAdd.GetColumn(nRow, "UNIT_PRC") *  ds_MaterialAdd.GetColumn(nRow, "EA");
			ds_MaterialAdd.SetColumn(nRow, "COST", strSum);
		}
	}
	
}

// 승인일자 변경시
function cal_Decide_ymd_req_OnChanged(obj,strOldText,strNewText)
{
	
	if(strOldText != strNewText)
	{

		if( cal_Decide_ymd_req.Value != "" && cal_Decide_ymd_req.Value > today())
		{
			alert("[확인] 승인일자는 현재날짜 포함한 의뢰일자 이후로 입력하여야 합니다.");
			cal_Decide_ymd_req.Value = strOldText;
			cal_Decide_ymd_req.SetFocus();
			return;
		}
				
		if( cal_Decide_ymd_req.Value != "" && cal_Work_Req_Ymd_1.Value != "" && cal_Decide_ymd_req.Value < cal_Work_Req_Ymd_1.Value)
		{
			alert("[확인] 승인일자는 현재날짜 포함한 의뢰일자 이후로 입력하여야 합니다.!");
			cal_Decide_ymd_req.Value = strOldText;
			cal_Decide_ymd_req.SetFocus();
			return;
		}
		
		
	}
}

// 정비보수업체 팝업
function btn_WorkFirmCdSearch_OnClick(obj)
{
	var args =  "BRANCH_CD=" + quote(cob_Branchcd.Value);
	
	var strResult = gfn_Dialog("D10","D1601010018U",args,-1,-1,true,-1,-1);
	
	if( length(strResult) > 0 )
	{
		
		edt_WorkFirmCdSearch.Text = strResult[0];  // 업체코드
		edt_WorkFirmNmSearch.Text = strResult[1]; // 업체명
	}

}
