// 검색 시공사코드 삭제시 시공사명도 같이 삭제 
function edt_WorkFirmCdSearch_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
	if( nChar == '8' || nChar == '46')
	{
		edt_WorkFirmCdSearch.Text = "";
		edt_WorkFirmNmSearch.Text = "";
	}
	
}

function ds_Amt_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	
	if(varOldValue != varNewValue) 
	{
		if(length(trim(varNewValue)) <= 0 ) return;
		
		// 시작시간
		if(strColumnID == "ACT_STR_TIME") 
		{
			if(parseInt(varNewValue) < 2400)
			{
				if(substr(varNewValue,2,2)=="30"||substr(varNewValue,2,2)=="00")
				{
					
				}
				else
				{
					alert("분은 30분 단위로 입력해 주십시오.");
					act_str_time.SetFocus();
				}
				
			}
			else
			{
				alert("시간형식이 아닙니다. (예)11:30");
				act_str_time.SetFocus();
			}
		}
		
		// 종료시간
		if(strColumnID == "ACT_END_TIME") 
		{
				
			if(parseInt(varNewValue)<2400)
			{
				if(substr(varNewValue,2,2)=="30"||substr(varNewValue,2,2)=="00")
				{
					
				}
				else
				{
					alert("분은 30분 단위로 입력해 주십시오.");
					act_end_time.SetFocus();
				}
				
			}
			else
			{
				alert("시간형식이 아닙니다. (예)11:30");
				act_end_time.SetFocus();
			}
		}
		
		
	}
	
}

// 검색 - 시설물 구분
function cbo_ReqFlagSearch_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_FaciNumSub_01.Filter(" FACI_FLAG = '"+strCode+"' || length(FACI_FLAG) = 0");

	
}

// 정비단가팝업
function btn_Select_UnitPrc_OnClick(obj)
{
	if(ds_Multi_Re2.GetColumn(ds_Multi_Re2.row,"ADJ_TYPE") == '10')
	{
		alert("실비건입니다...");
		return;
	}
	
	if(chk_Change3.Value == '1')
	{
		alert("공사비 정산 건입니다..");
		return;
	}
	
	var strArg = "REAL_JOB_YMD=" + ds_Amt.GetColumn(ds_Amt.row,"REAL_JOB_YMD");  
	if(ds_Amt.GetColumn(ds_Amt.row,"REAL_JOB_YMD") == '' ||  ds_Amt.GetColumn(ds_Amt.row,"REAL_JOB_YMD") == null)
	{
		alert("조치일자가 등록되지 안았습니다...");
		return;
	}
		
	var strResult = gfn_Dialog("D10","D1601010019U",strArg,660,400,true,-1,-1);
	
	if( length(strResult) > 0)
	{
		/*
		var strWorkFlag = "";
		
		if(strResult[0] == '010' || strResult[0] == '020' || 
		   strResult[0] == '030' || strResult[0] == '040' || 
		   strResult[0] == '060' || strResult[0] == '070' || 
		   strResult[0] == '100') 
		{
			// 시설물 보수 (10)
			strWorkFlag = "10";
		}
		else if(strResult[0] == '080' || strResult[0] == '090')
		{
			// 중폐지(30)
			strWorkFlag = "30";		
		}
		else if(strResult[0] == '050')
		{
			// 누기보수(70)
			strWorkFlag = "70";		
		}		
		else if(strResult[0] == '120' || strResult[0] == '130'|| 
		        strResult[0] == '140' || strResult[0] == '150')
		{
			// 휴일대기(60)
			strWorkFlag = "60";		
		}
		*/

		cob_WorkSFlag.Value = strResult[6];  // 주/야
		cob_WorkLFlag.Value = strResult[5];  // 대분류
		cob_WorkMFlag.Value = strResult[0]; // 중분류
		cob_Po.Value        = strResult[1]; // 포종
		cob_Je.Value        = strResult[2]; // 재질
		
		if(strResult[0] == '060' || strResult[0] == '120') // 밸브정비(060), 밸브정비(휴일)(120)
		{
			cob_Kwan.Value = "";           // 관경
			cob_Bel.Value  = strResult[3]; // 밸브
		}
		else
		{
			cob_Kwan.Value = strResult[3]; // 관경
			cob_Bel.Value  = "";           // 밸브
		}
		
		medt_Unit_Prc.Value = strResult[4]; // 단가
		
		edt_Ea.Enable = true;
		
		edt_Ea_OnCharChanged();
	
	}
	else
	{
		edt_Ea.Enable = false;
	}
}

function Btn_cancel_OnClick(obj)
{
	if(length(trim(ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG"))) <> 0){
		// 2012.04.23. 권기철 과장님 추가요청_이건민
		//10 공사완료, 90 공사 취소 불가
		if(ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") == '10' || ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") == '90')
		{
			alert("가정관 진행 상태가["+ ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG_NM") + "] 로 취소가 불가능합니다.");
			return;
		}
		
		/*
		//00 요청전 01 요청 02  검토반려 04 검토완료 일때만 삭제가능
		if(ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") != '00' && ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") != '01'
		   && ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") != '02' && ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") != '04')
		{
			alert("가정관 진행 상태가["+ ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG_NM") + "] 로 삭제가 불가능합니다.");
			return;
		}
		*/
	}

	if (ds_Multi.GetColumn(ds_Multi.row,"MAINTER_STS_FLAG") == '00')
	{
		// 2012.04.24. 권기철 과장님 추가요청_이건민
		if(    ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") == '01' || ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") == '03' 
			|| ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") == '04' || ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") == '05'
			|| ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") == '06' || ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") == '07'
			|| ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG") == '08')
		{
			if(confirm("위험작업이 있는 경우 해당 위험작업도 함께 취소처리 됩니다. 취소처리 하시겠습니까?"))
			{
				fn_D1_Cancel();
			}
			else
			{
				return;
			}	
		}
		else
		{
			alert("가정관 진행 상태가["+ ds_Multi_Re.GetColumn(ds_Multi_Re.row, "OK_HMPP_DANGER_JOB_STS_FLAG_NM") + "] 로 취소가 불가능합니다.");
			return;
		}
	}
	
	if (ds_Multi.GetColumn(ds_Multi.row,"MAINTER_STS_FLAG") == '30')
	{
		alert("조치건은 취소불가합니다.");
		return;
	}
	else if (ds_Multi.GetColumn(ds_Multi.row,"MAINTER_STS_FLAG") == '40')
	{
		alert("정산은 취소불가합니다.");
		return;
	}
	//2012.04.23. 권기철과장님 추가요청_이건민
	else if (ds_Multi.GetColumn(ds_Multi.row,"MAINTER_STS_FLAG") == '50')
	{
		alert("정산중에는 취소불가합니다.");
		return;
	}
	else if (ds_Multi.GetColumn(ds_Multi.row,"MAINTER_STS_FLAG") == '90')
	{
		alert("이미 취소 처리 되었습니다.");
		return;
	}	
		
	if (gfn_GetGlobalValue("G_USER_NO") == edt_WorkReqEmpid.value || gfn_GetGlobalValue("G_USER_NO") == '199443297' || gfn_GetGlobalValue("G_USER_NO") == '700004008') 
	{
		if (edt_cancel_rmk.text == '')
		{
			alert("취소사유를 입력 하세요");
			return;
		} 
		
		// 2012.04.24 권기철 과장님 추가요청으로 주석처리_이건민
		/*
		if(confirm("취소처리하시겠습니까?"))
		{
			fn_D1_Cancel();
		}
		*/
		// 2012.04.24. 권기철 과장님 추가요청_이건민
		if(confirm("위험작업이 있는 경우 해당 위험작업도 함께 취소처리 됩니다.\n취소처리 하시겠습니까?"))
		{
			fn_D1_Cancel();
		}
		else
		{
			return;
		}
	} 
	else
	{
		alert("취소는 의뢰자만 가능합니다..");
		return;
	}			
}

//----------------------------------------------------------------------------------------------
// 함수명: fn_D1_Cancel()
// 설명:   정비보수 취소
// arguments
// return 내용: 없음
//----------------------------------------------------------------------------------------------
function fn_D1_Cancel()
{    
	ds_Cancel.ClearData();
	ds_Cancel.UpdateControl = false ;
	var row = ds_Cancel.AddRow();
	ds_Cancel.SetRowType(row, "update");
    ds_Cancel.SetColumn(row,"MAINTER_NUM"  , ds_Multi.GetColumn(ds_Multi.row , "MAINTER_NUM"));  // 정비보수번호
	ds_Cancel.SetColumn(row,"UPD_EMPID" , gfn_GetGlobalValue("G_USER_NO"));  // UPD_EMPID
	ds_Cancel.SetColumn(row,"UPD_IP"    , gfn_GetLocalIpAddress());  // UPD_IP
	ds_Cancel.SetColumn(row,"CANCEL_RMK"   , edt_cancel_rmk.Text);  // 취소사유
	ds_Cancel.SetColumn(row,"CANCEL_EMPID" , gfn_GetGlobalValue("G_USER_NO"));  // 취소자사번

    ds_Cancel.SetColumn(row,"OK_HMPP_DANGER_NUM"  , ds_Multi_Re.GetColumn(ds_Multi_Re.row , "DANGER_NUM"));  // 가정관 위험작업번호
    ds_Cancel.SetColumn(row,"OK_HMPP_JOB_FLAG"  , '20');  // 가정관 위험작업구분

	var strSvcID = "cancel";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_MaintRepaiWorkFacilRegisMgr-service";  
	var strInDatasets  = "ds_Cancel=ds_Cancel:u";
	var strOutDatasets  = "";
	var strArgument  = "cancel=1";	
	
	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  );
				  
	if(ErrorCode < 0)
	{    
		alert(ErrorMsg);
		return;
	}else{
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 취소"),"black",true); 
		alert(gfn_GetMessage("M000009","%AAA%","정상적으로 취소")); 
		ds_Multi.SetColumn(ds_Multi.row,"MAINTER_STS_FLAG" , '90');
		ds_Multi.SetColumn(ds_Multi.row,"MAINTER_STS_FLAG_NM" , '취소');
		// 2012.04.24. 이건민 추가
		ds_Multi.SetColumn(ds_Multi.row,"OK_HMPP_DANGER_JOB_STS_FLAG" , '90');
		ds_Multi.SetColumn(ds_Multi.row,"OK_HMPP_DANGER_JOB_STS_FLAG_NM" , '공사취소');
	}				  
}


function ds_Multi_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if(nReason == 5)
	{
		ds_Multi_OnRowPosChanged();
	}
}

