//--------------------------------------------------------------------------------------------------
//	OnLoadCompleted
//--------------------------------------------------------------------------------------------------
function D1601010017U_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
	fn_Init();
	
	fv_emp_id = gfn_GetGlobalValue("G_USER_NO");
	fv_emp_nm = gfn_GetGlobalValue("G_NAME");

	fv_COM_ID = gfn_GetGlobalValue("G_COM_ID");
	if(fv_COM_ID == "92") // 시공사-협력사 여부
	{
		fv_COOP_YN = true;
	}			
			
	edt_DangerNum.Text = "";
	edt_DangerJobStsFlagNm.Text = "";
	//edt_DangerJobFlag.Text = "";
	fn_D10_CommonNeed_pers();           // 소요일원 초기화
	
	//fn_D10_Common9();					//위험작업내역 false
	
	fn_D11_Teamlist();					//팀코드 조회해오기 
	
	ds_Multi_Re.SetColumn(0,"MAINTER_NUM",MAINTER_DANGER_FLAG);
	
	fn_CommonAddRow();
}
