function btn_RowAdd01_OnClick(obj)
{
	ds_Grid1.AddRow();
	if(ds_Grid1.GetRowCount() == 1){
		return;
	}else{
		ds_Grid1.SetColumn(ds_Grid1.row,"JOB_STR_TIME",ds_Grid1.GetColumn(ds_Grid1.row-1,"JOB_END_TIME"));
	}	
}


// 시간대별 작업계획 삭제
function btn_RowDel01_OnClick(obj)
{
	var strtime = ds_Grid1.GetColumn(ds_Grid1.RowCount()-1,"JOB_STR_TIME");
	var endtime = ds_Grid1.GetColumn(ds_Grid1.RowCount()-1,"JOB_END_TIME");
	var dangerJPC = ds_Grid1.GetColumn(ds_Grid1.RowCount()-1,"DANGER_JOB_PLAN_CD");
	var rmk = ds_Grid1.GetColumn(ds_Grid1.RowCount()-1,"RMK");
	
	if(ds_Grid1.GetRowType(ds_Grid1.RowCount()-1) == "insert")
	{
		if((strtime==""||strtime==null) && (endtime==""||endtime==null) && (dangerJPC==""||dangerJPC==null) && (rmk==""||rmk==null))
		{
			ds_Grid1.DeleteRow(ds_Grid1.row);
		}else if(confirm("작성된 내용이 있습니다. 그래도 삭제하시겠습니까?"))
		{
			ds_Grid1.DeleteRow(ds_Grid1.row);
		}
	}else if(confirm("등록된 작업계획을 삭제하시겠습니까?"))
	{
		fn_D1_WorkJobDelete(ds_Grid1.row);
	}
}
//----------------------------------------------------------------------------------------------
// 함수명: fn_D1_WorkJobDelete()
// 설명:  작업계획 삭제 
// arguments
// return 내용: 없음
//----------------------------------------------------------------------------------------------
function fn_D1_WorkJobDelete(row)
{    
	ds_Grid1.DeleteRow(row);
	
	var strSvcID = "fn_D1_WorkJobDelete";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service&delete=1"
			   ;  
	var strInDatasets  = "ds_Grid1=ds_Grid1:u";
	var strOutDatasets  = "";
	var strArgument  = "";	
	
	gfn_SyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  );
				  
	if(ErrorCode < 0)
	{    
		return;
	}else{
		gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"),"black",true); 
	}					  
}
