//-------------------------------------------------------------------------------
// 기      능   :  안전수칙 보기
//-------------------------------------------------------------------------------
function btn_Safety_OnClick(obj)
{
	var DangerNum     = ds_Multi_Re.GetColumn(ds_Multi_Re.row,"DANGER_NUM")+"%$";
	var DangerJobFlag = ds_Multi_Re.GetColumn(ds_Multi_Re.row,"DANGER_JOB_FLAG");
	
	if(gfn_IsNull(DangerNum) || gfn_IsNull(DangerJobFlag)){
		alert("위험작업번호와 작업구분 정보가 없습니다. ");
		return false;
	}
	
	args = "WORK_FIRM_CD="     + quote(edt_WorkFirmCdNm1.Text)
	     + " SITE_CHIEF_NM="   + quote(edt_SiteChiefNm.Text  )
	     + " DANGER_JOB_FLAG=" + quote(DangerJobFlag         )
	     + " DANGER_NUM="      + quote(DangerNum             )
	     ;
	
	gfn_Dialog("D10","D1503010011U",args,660,536,true,-1,-1);
	
}
//-------------------------------------------------------------------------------
// 기      능   :  파일첨부
//-------------------------------------------------------------------------------
function btn_FileFolder_OnClick(obj)
{
	if(gfn_IsNull(edt_DangerNum.Text)){
		alert("등록된 위험작업이 없습니다. 등록먼저 해주세요.");
		return;
	}else{
		
		var arg = "fa_Url=handle.do?ServiceName=D1_DangerWorkRequirEMgr-service&DANGER_NUM="+danger_num+"&DANGER_JOB_FLAG=10&WORK_FLAG=10 fa_Part=D10";
		var arrList = gfn_Dialog("D10","D1101010018U",arg,450,280,true,-1,-1);
		edtFileList.text = "";
		for(var i=0; i<arrList.length; i++)
		{
			edtFileList.text += arrList[i];
			if(arrList.length-1 != i) edtFileList.text += "/";
		}		
	}
}

//--------------------------------------------------------------------------------------------
//  공사번호 가져오기 
//--------------------------------------------------------------------------------------------
function btn_WorkNumButton_OnClick(obj)
{
	var workNumList = gfn_Dialog("B20","B2202010012U","",728,424,true,-1,-1);
	if(workNumList == null || workNumList == 0 || workNumList == ""){
		return;
	}
		
	edt_WorkNum.Text        = workNumList[0];
	job_nm.Text             = workNumList[1];
	cob_BranchCdsSave.Value = workNumList[2];
	work_type               = workNumList[4];  		//공사구분 (투자,외부)	
	edt_WorkFirmCdNm.Text   = workNumList[5];
	cob_TeamCd.Text  	    = workNumList[6];		//팀? 
	WorkNum                 = edt_WorkNum.Text;

	fn_D10_WorkName();
	// edt_WorkName.Text = ds_WorkName.GetColumn(0,"NAME");
	
	cob_BranchCdsSave_OnChanged();
	cob_TeamCd.Enable = true;	
}

