//--------------------------------------------------------------------------------------------
//    공사번호에 따른 시공사 요청자명 가져오기 
//--------------------------------------------------------------------------------------------
function fn_D10_WorkName()
{   
	ds_WorkName.ClearData();
	ds_WorkName.AddRow();
	
	// 검색 문자열을 생성한다 
	var strSvcID = "fn_D10_WorkName";
	var strPart = "D10";
	var strURL = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service&name=1"
			   + "&WORK_NUM="+WorkNum
			   + "&WORK_FIRM_CD="+edt_WorkFirmCd1.Text
			   ;
	var strInDatasets  = "";
	var strOutDatasets  = "ds_WorkName=ds_WorkName";
		strOutDatasets  = "";
	var strArgument  = "";

	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
}


//-------------------------------------------------------------------------------
//	공통 버튼 클릭 - 삭제
//-------------------------------------------------------------------------------
function fn_CommonDeleteRow()
{
	if(ds_Multi.RowCount() == 0){
		alert("조회 먼저 해주세요.");
		return;
	}else{
		if(danger_job_sts_flag == "00"){
			if(confirm("작성된 위험작업 내역을 삭제하시겠습니까?")){
				fn_D1_DetailDelete1();
			}		
				return;
		}else{
				alert("진행상태가 위험작업요청전 상태만 삭제가 가능합니다. ");
				return;
		}
	}
}

//-------------------------------------------------------------------------------
// 설명     :   FROM ~ TO 체크 (내역)
//-------------------------------------------------------------------------------
function cal_DigAdmitFrom_OnChanged(obj,strOldText,strNewText)
{
	if(cal_DigAdmitFrom.Value < today() && !gfn_IsNull(cal_DigAdmitFrom.Value)){
		alert("굴착허가기간 시작일자는 오늘날짜보다 작을 수 없습니다. 수정해주세요.");
		cal_DigAdmitFrom.Value = "";
		return;	
	}
	
	if(!gfn_IsNull(cal_DigAdmitFrom.Value) && !gfn_IsNull(cal_DigAdmitTo.Value) && cal_DigAdmitFrom.Value > cal_DigAdmitTo.Value){	
		alert("TO 날짜는 FROM 날짜보다 같거나 큰값을 입력하세요.");
		cal_DigAdmitFrom.SetFocus();
		cal_DigAdmitFrom.value = "";
		cal_DigAdmitTo.value = "";
		return;
	}		
}

function cal_DigAdmitTo_OnChanged(obj,strOldText,strNewText)
{
	if(cal_DigAdmitTo.Value < today() && !gfn_IsNull(cal_DigAdmitTo.Value)){
		alert("굴착허가기간 시작일자는 오늘날짜보다 작을 수 없습니다. 수정해주세요.");
		cal_DigAdmitTo.Value = "";
		return;	
	}
	
	if(!gfn_IsNull(cal_DigAdmitFrom.Value) && !gfn_IsNull(cal_DigAdmitTo.Value) && cal_DigAdmitFrom.Value > cal_DigAdmitTo.Value){	
		alert("TO 날짜는 FROM 날짜보다 같거나 큰값을 입력하세요.");
		cal_DigAdmitFrom.SetFocus();
		cal_DigAdmitFrom.value = "";
		cal_DigAdmitTo.value = "";
		return;
	}		
}

