//-------------------------------------------------------------------------------
// 설명     :   validation check
//-------------------------------------------------------------------------------
function fn_D10_Validation01()           
{
	if( edt_DangerJobStsFlagNm.Text == "" || edt_DangerJobStsFlagNm.Text == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","진행상태는 "));
		return 1;
	}
	if(cob_BranchCdsSave.Value == "" || cob_BranchCdsSave.Value == NULL){		
		alert(gfn_GetMessage("M000015","%AAA%","지사는 "));
		return 1;
	}	
	if(cob_TeamCd.Value == "" || cob_TeamCd.Value == NULL){				
		alert(gfn_GetMessage("M000015","%AAA%","팀은 "));
		return 1;
	}	
	if(cal_JobReqWmd1.Value == "" || cal_JobReqWmd1.Value == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","작업요청일자는 "));
		return 1;
	 }
	if(edt_JobReqStartTime.Value == "" || edt_JobReqStartTime.Value == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","작업요청시작시간은 "));
		return 1;
	 }
	if(edt_JobReqEndTime.Value == "" || edt_JobReqEndTime.Value == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","작업요청종료시간은 "));
		return 1;
	 }	
	if(cob_JobFlag.Value == "" || cob_JobFlag.Value == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","작업구분은 "));
		return 1;
	 }	
	if(edt_WorkFirmCdNm.Text == "" || edt_WorkFirmCdNm.Text == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","시공사명은 "));
		return 1;
	 }
	if(edt_WorkNum.Text == "" || edt_WorkNum.Text == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","공사번호는 "));
		return 1;
	 }	
	if(edt_LotNumUnion.Text == "" || edt_LotNumUnion.Text == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","기존 작업위치는 "));
		return 1;
	 }	
	// if(edt_Addr_Union.Text == "" || edt_Addr_Union.Text == NULL){
		// alert(gfn_GetMessage("M000015","%AAA%","신작업위치는 "));
		// return 1;
	 // }		 	  
	if(cob_BefoMaterFlag1.Value == "" || cob_BefoMaterFlag1.Value == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","기존재질은 "));
		return 1;
	 }
	if(cob_BefoPipeDiamCd1.Value == "" || cob_BefoPipeDiamCd1.Value == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","기존관경은 "));
		return 1;
	 }
	if(cob_BefoMaterFlag2.Value == "" || cob_BefoMaterFlag2.Value == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","신규재질은 "));
		return 1;
	 }
	if(cob_BefoPipeDiamCd2.Value == "" || cob_BefoPipeDiamCd2.Value == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","신규관경은 "));
		return 1;
	 }	 	 	 	 	 	 
	if(cob_PressFlag.Value == "" || cob_PressFlag.Value == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","압력은 "));
		return 1;
	 }	 	 
	if(cob_JobMothodCd.Value == "" || cob_JobMothodCd.Value == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","작업방법은 "));
		return 1;
	 }		 	 	 	 	 
	if(edt_JobCnt.Text == "" || edt_JobCnt.Text == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","작업개소는 "));
		return 1;	
	 }	
	if(ds_Grid1.GetRowCount()<1){
		alert("작성된 시간대별 작업계획이 없습니다. 작성해주세요.");
		return 1;
	}	  
	if(ds_Grid1.GetColumn(0,"JOB_STR_TIME") == "" || ds_Grid1.GetColumn(0,"JOB_STR_TIME") == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","작업계획 시작시간은 "));
		return 1;
	 }	
	if(ds_Grid1.GetColumn(0,"JOB_END_TIME") == "" || ds_Grid1.GetColumn(0,"JOB_END_TIME") == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","작업계획 종료시간은 "));
		return 1;
	 }		 	 	 	 	 	 	 
	if(ds_Grid1.GetColumn(0,"DANGER_JOB_PLAN_CD") == "" || ds_Grid1.GetColumn(0,"DANGER_JOB_PLAN_CD") == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","작업계획 작업내용은 "));
		return 1;
	 }	
	if(edt_WorkFirmCdNm1.Text == "" || edt_WorkFirmCdNm1.Text == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","시공사명은 "));
		return 1;
	 }	
	if(cob_WorkFirmTelDdd.Value == "" || cob_WorkFirmTelDdd.Value == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","시공사 전화번호는 "));
		return 1;
	 }	
	if(work_firm_tel_exn.Text == "" || work_firm_tel_exn.Text == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","시공사 전화번호는 "));
		return 1;
	 }
	if(work_firm_tel_num.Text == "" || work_firm_tel_num.Text == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","시공사 전화번호는 "));
		return 1;
	 }	 
	if(cob_WorkManagerTelDdd.Value == "" || cob_WorkManagerTelDdd.Value == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","시공관리자 전화번호는 "));
		return 1;
	 }	
	if(work_manager_tel_exn.Text == "" || work_manager_tel_exn.Text == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","시공관리자 전화번호는 "));
		return 1;
	 }
	if(work_manager_tel_num.Text == "" || work_manager_tel_num.Text == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","시공관리자 전화번호는 "));
		return 1;
	 }	 	 
	if(cob_SiteChiefTelDdd.Value == "" || cob_SiteChiefTelDdd.Value == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","현장소장 전화번호는 "));
		return 1;
	 }	
	if(site_chief_tel_exn.Text == "" || site_chief_tel_exn.Text == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","현장소장 전화번호는 "));
		return 1;
	 }	
	if(site_chief_tel_num.Text == "" || site_chief_tel_num.Text == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","현장소장 전화번호는 "));
		return 1;
	 }		 	 
	if(cal_DigAdmitFrom.Value == "" || cal_DigAdmitFrom.Value == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","굴착허가 시작날짜는 "));
		return 1;
	 }	
	if(cal_DigAdmitTo.Value == "" || cal_DigAdmitTo.Value == NULL){
		alert(gfn_GetMessage("M000015","%AAA%","굴착허가 종료날짜는 "));
		return 1;
	 }	
}

// SCG담당자
function btn_ScgCharge_OnClick(obj)
{

	if(gfn_IsNull(cob_BranchCdsSave.value))
	{
		alert("먼저 지사를 선택하여 주십시오.");
		return;
	}
	
	if(gfn_IsNull(cob_TeamCd.value))
	{
		alert("먼저 팀을 선택하여 주십시오.");
		return;
	}
	
	var args = "BRANCH_CD=" + cob_BranchCdsSave.value
			 + " TEAM_CD="  + cob_TeamCd.value 
			 + " SFALG=Y";
					 
	var arrList = gfn_Dialog("D10","D1101020014U",args,700,400,true,-1,-1);
	if( length( arrList ) > 0 )
	{

		edt_Taker_EmpId.Text = arrList[0];
		edt_Taker_EmpNm.Text = arrList[1];
		
	}
}


//-------------------------------------------------------------------------------
// 설명     : 위험작업 요청(버튼 클릭시 상태값 강제 01) 변경
//-------------------------------------------------------------------------------
function Btn_Danger_Req_OnClick(obj)
{   
	danger_job_str_flag = ds_Multi_Re.GetColumn(0,"DANGER_JOB_STS_FLAG");
	var DangerNum       = ds_Multi_Re.GetColumn(0,"DANGER_NUM");

	if(gfn_IsNull(DangerNum)){
		alert("등록된 위험작업이 없습니다. 등록먼저 해주세요.");
		return false;
	}
	if(danger_job_str_flag == "00" || danger_job_str_flag == "02")
	{	
		if(Confirm("위험작업 요청을 하시겠습니까? ") == true)
		{
			ds_Multi_Re.UpdateControl = false;
			ds_Multi_Re.SetRowType(0,"update");
			
			ds_Multi_Re.SetColumn(0,"DANGER_JOB_STS_FLAG","01");
			ds_Multi_Re.SetColumn(0,"UPD_IP",gfn_GetGlobalValue("G_IPADDRESS"));   
			ds_Multi_Re.SetColumn(0,"UPD_EMPID",gfn_GetGlobalValue("G_USER_NO"));   
			ds_Multi_Re.SetColumn(0,"DANGER_NUM",danger_num);
			ds_Multi_Re.SetColumn(0,"DANGER_JOB_FLAG","10");
				
			fv_FindRow_ShowRow = 1;
			fv_FindRow_ShowKey = ds_Multi_Re.GetColumn(0,"DANGER_NUM"); //위험작업번호
	
			var strSvcID = "save_update01";
			var strPart = "D10";
			var strURL = "handle.do?ServiceName=D1_DangerWorkRequirEMgr-service";
			var strInDatasets  = "ds_Multi_Re=ds_Multi_Re:u";
			var strOutDatasets  = "";
			var strArgument  = "updateflag=1";	
			
			gfn_SyncCall(
							 strSvcID
						   , strPart
						   , strURL
						   , strInDatasets
						   , strOutDatasets
						   , strArgument
						  );
			fn_D10_BpmSearchStr();							//BPM 신청
			
			if(ErrorCode < 0)
			{    
				return;
			}else{
				gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
				fn_CommonSearch();	
			}			
		}else{
			return;
		}
	}else{
		alert("이미 위험작업 요청을 하였습니다.");
		return;
	}
}
