var fv_FindRow_ShowRow = -1; 		  // 조회후 보여줄 컬럼 체크변수
var fv_FindRow_ShowKey = -1;          // KEY값

var fv_emp_id = "";
var fv_emp_nm = "";

var fv_COM_ID = "";
var fv_COOP_YN = false;  // 협력사여부
var fv_DEAL_CD = "";     // 업체코드
var fv_DEAL_NM = "";     // 업체명
var fv_POS_RNK_CD = "";  // 직위코드 (325-팀장)

