//--------------------------------------------------------------------------------------------------
//	OnLoadCompleted
//--------------------------------------------------------------------------------------------------
function D1601020010F_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
	
	fn_Init();
	
	fv_emp_id = gfn_GetGlobalValue("G_USER_NO");
	fv_emp_nm = gfn_GetGlobalValue("G_NAME");
	fv_COM_ID = gfn_GetGlobalValue("G_COM_ID");
	fv_DEAL_CD= gfn_GetGlobalValue("G_DEAL_CD");
	fv_DEAL_NM= gfn_GetGlobalValue("G_COM_NAME");
	fv_POS_RNK_CD=gfn_GetGlobalValue("G_POS_RNK_CD");// 직위코드 (325-팀장)
	
	//trace("fv_POS_RNK_CD: "+fv_POS_RNK_CD);		
	//-----------------------------------------
	//  지사,팀 콤보박스의 초기 설정
	//-----------------------------------------	
	if (gfn_GetGlobalValue("G_BRANCH_CD") == "999999") 
	{
		// 지사설정
		cob_BranchCd.Enable = true;
		cob_BranchCd.Value = "";
		cob_BranchCd_OnChanged();
		// 팀설정	
		cob_TeamCdSearch.Value = "";
		
		// 업체설정
		edt_WorkFirmCdSearch.Enable = true;
		btn_WorkFirmCdSearch.Enable = true;	
				
		alert("본사직원은 지사정보(G_BRANCH_CD)가 없습니다. [조회작업만 진행가능합니다.]");
	
	} 
	else 
	{
		// 지사설정
		cob_BranchCd.Enable = false;
		cob_BranchCd.Value = gfn_GetGlobalValue("G_BRANCH_CD");
		cob_BranchCd_OnChanged();
		
		// 팀설정	
		cob_TeamCdSearch.Value = gfn_GetGlobalValue("G_DEPT_CODE");
		
		// 직위코드 팀장급(325) - 팀을 풀어달라. 2009-11-09 정우현과장, 권기철과장
		if( fv_POS_RNK_CD == "325") 
		{
			cob_TeamCdSearch.Enable = true;
		}

		// 업체설정
		edt_WorkFirmCdSearch.Enable = true;
		btn_WorkFirmCdSearch.Enable = true;	

	}		
	
	cob_MainterStsFlag.Value = "30"; // 공사진행상태 - 조치(30) - 결재대상
	cob_AssentType.Value = "";     // 승인상태
	cob_WorkFlag.Value = "";       // 공사비 승인
	cob_DirFlag.Value = "";        // 직접복구비 승인
	cob_IndFlag.Value = "";        // 간접복구비 승인
	
	// 작업의뢰일자 default 이번달 1일부터 오늘까지 
	cal_ReqYmdStart.Value = substr(today(),0,6)+"01";		
	//cal_ReqYmdEnd.Value = today();
	cal_ReqYmdEnd.Value = adddate(addMonth(substr(today(),0,6)+"01", 1),-1); // 해당날의 마지막날 구하기

	
	cbo_ReqFlagSearch.Value = "";  		// 정비보수의뢰구분
    cob_FaciFlagSearch.Value = "";      // 시설물구분
    
    
}

