//--------------------------------------------------------------------------------------------------
//	서비스 CallBack
//--------------------------------------------------------------------------------------------------
function fn_D1_CallBack(SrvID,ErrorCode,ErrorMsg)
{

	if(ErrorCode < 0) {
	
		alert(ErrorMsg);
		
		//에러시 초기화
		fv_FindRow_ShowRow = -1;
		return;
	}
	
    switch(SrvID){
        
        // 조회
        case "findList":
        
			gfn_D1_SetComboBox_Teamcd01("ds_Team_01",cob_BranchCd01); 
			        
			// 로우선택하기
			if(fv_FindRow_ShowRow > 0)
			{
				var nFocusRow = ds_Multi.FindRow("MAINTER_NUM",fv_FindRow_ShowKey);
				if (nFocusRow != -1)
				{
					ds_Multi.row = nFocusRow;
				}	
				fv_FindRow_ShowRow = -1;
			}        
        
			edt_Count.text = ds_Multi.GetRowCount();
			break;
			
        case "save":
            alert("작업이 완료되었습니다.");
            fn_CommonSearch();
			break;            

        default:

    }
}

//-------------------------------------------------------------------------------
// 기      능   :  공통 조회
//-------------------------------------------------------------------------------
function fn_CommonSearch()
{
	ds_Multi.ClearData();
	
    ds_SearchTop.SetColumn(0,"BRANCH_CD"          , cob_Branchcd.Value        );  // 지사
    ds_SearchTop.SetColumn(0,"ZIP_NO1"            , cob_ZipNo1.Value          );  // 시/구
    ds_SearchTop.SetColumn(0,"TEAM_CD"            , cob_TeamCdSearch.Value    );  // 팀
	ds_SearchTop.SetColumn(0,"WORK_FIRM_CD"       , edt_WorkFirmCdSearch.Text );  // 시공사
	ds_SearchTop.SetColumn(0,"MAINTER_STS_FLAG"   , cob_MainterStsFlag.Value  );  // 공사진행상태
    ds_SearchTop.SetColumn(0,"ASSENT_TYPE"        , cob_AssentType.Value      );  // 승인상태
    ds_SearchTop.SetColumn(0,"ASSENT_WORK_FLAG"   , cob_WorkFlag.Value        );  // 공사비 승인
    ds_SearchTop.SetColumn(0,"ASSENT_IND_FLAG"    , cob_DirFlag.Value         );  // 간접복구비 승인
    ds_SearchTop.SetColumn(0,"ASSENT_DIR_FLAG"    , cob_IndFlag.Value         );  // 직접복구비 승인
    ds_SearchTop.SetColumn(0,"REQ_YMD_START"      , cal_ReqYmdStart.Value     );  // 작업요청일자
    ds_SearchTop.SetColumn(0,"REQ_YMD_END"        , cal_ReqYmdEnd.Value       );  // 작업요청일자
    ds_SearchTop.SetColumn(0,"MAINTER_REQ_FLAG"   , cbo_ReqFlagSearch.Value   );  // 정비보수의뢰구분
    ds_SearchTop.SetColumn(0,"FACI_FLAG"          , cob_FaciFlagSearch.Value  );  // 시설물구분
	
	var strSvcID = "findList";
	var strPart  = "D10";	
	var strURL   = "handle.do?ServiceName=D1_MaintRepaiRequeTeamLeadApproMgr-service";
 	var strInDatasets   = "ds_SearchTop=ds_SearchTop";
	var strOutDatasets  = "ds_Multi=ds_Multi";
	var strArgument     = "searchList=1";
	var strCallBackFunc = "fn_D1_CallBack";	
				   	
	gfn_AsyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				   , strCallBackFunc
			   );

}		


//---------------------------------------------------------------------------------
// 기      능   :  지사_구 콤보 
//---------------------------------------------------------------------------------
function cob_Branchcd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) 
{
    fn_SetComboBox_Comcode("1", cob_Branchcd.value, fn_setDsObject_Comcode("1", "cob_ZipNo1"));
    cob_ZipNo1.Value = "";  
    gfn_D1_SetComboBox_Teamcd("ds_Team",cob_Branchcd);
    cob_TeamCdSearch.Value = "";
    
    ds_Multi.ClearData();
	edt_Count.text = ds_Multi.GetRowCount();
}
//-------------------------------------------------------------------------------
// 설명     :   FROM ~ TO 체크 (조회)
//-------------------------------------------------------------------------------
function cal_ReqYmdStart_OnChanged(obj,strOldText,strNewText)
{
	if(cal_ReqYmdStart.Value !="" && cal_ReqYmdEnd.Value !="" && cal_ReqYmdStart.Value > cal_ReqYmdEnd.Value){	
		alert("TO 날짜는 FROM 날짜보다 같거나 큰값을 입력하세요.");
		cal_ReqYmdStart.SetFocus();
		cal_ReqYmdStart.value = "";
		cal_ReqYmdEnd.value = "";
		return;
	}	
}

function cal_ReqYmdEnd_OnChanged(obj,strOldText,strNewText)
{
	if(cal_ReqYmdStart.Value !="" && cal_ReqYmdEnd.Value !="" && cal_ReqYmdStart.Value > cal_ReqYmdEnd.Value){	
		alert("TO 날짜는 FROM 날짜보다 같거나 큰값을 입력하세요.");
		cal_ReqYmdStart.SetFocus();
		cal_ReqYmdStart.value = "";
		cal_ReqYmdEnd.value = "";
		return;
	}	
}

// 검색 시공사코드 삭제시 시공사명도 같이 삭제 
function edt_WorkFirmCdSearch_OnChar(obj,strPreText,nChar,strPostText,LLParam,HLParam)
{
	if( nChar == '8' || nChar == '46')
	{
		edt_WorkFirmCdSearch.Text = "";
		edt_WorkFirmNmSearch.Text = "";
	}
	
}

// 검색 - 시설물 구분
function cbo_ReqFlagSearch_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_FaciNumSub_01.Filter(" FACI_FLAG = '"+strCode+"' || length(FACI_FLAG) = 0");
}

// 정렬
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	gfn_SetGridSort(obj, nCell);
}


// 정비보수업체 팝업
function btn_WorkFirmCdSearch_OnClick(obj)
{
	var args =  "BRANCH_CD=" + quote(cob_Branchcd.Value);
	
	var strResult = gfn_Dialog("D10","D1601010018U",args,-1,-1,true,-1,-1);
	
	if( length(strResult) > 0 )
	{
		
		edt_WorkFirmCdSearch.Text = strResult[0];  // 업체코드
		edt_WorkFirmNmSearch.Text = strResult[1]; // 업체명
	}

}


// 팀장 승인처리
function btn_Approval_OnClick(obj)
{

	if( gfn_GetGlobalValue("G_BRANCH_CD") == "999999" )
	{
		alert("본사직원은 지사정보가 없습니다. [조회작업만 진행가능합니다.]");
		return;
	}


	var strMAINTER_STS_FLAG = ds_Multi.GetColumn(ds_Multi.row ,"MAINTER_STS_FLAG");
	
	if( strMAINTER_STS_FLAG != '30')
	{
		alert("진행상태가 조치(30)상태가 아니므로 진행하실 수 없습니다.");
		return;
	}
	
	// 팀장승인체크
	if( ! fn_D1_TeamManagerYN( fv_emp_id) )
	{
		alert("[권한]팀장만 승인 가능합니다. 로그인자가 팀장(325)권한이 아닙니다.");
		return;
	}
	
	
	var strChk = ds_Multi.SearchRow(" CHK = '1' ");
	
	if(strChk  == -1 )
	{
		alert("리스트에서 일괄승인할 내역을 선택하십시오.");
		return;
	}
	
	if(Confirm(" 공사비, 간접복구비, 직접복구비 승인을 하시겠습니까? \n (단, 검토되지않은 건은 승인되지 않습니다.)"))
	{
	
		var strUPD_EMPID = gfn_GetGlobalValue("G_USER_NO");
		var strUPD_IP = gfn_GetLocalIpAddress();
		
		ds_Save.ClearData();
				
		for(var i = 0; i < ds_Multi.GetRowCount(); i++)
		{
		
			if( ds_Multi.GetColumn(i ,"CHK") == '1' )
			{
				var nRow = ds_Save.AddRow();
				ds_Save.SetColumn(nRow, "MAINTER_NUM", 	ds_Multi.GetColumn(i ,"MAINTER_NUM"));
				ds_Save.SetColumn(nRow, "UPD_EMPID",    strUPD_EMPID);
				ds_Save.SetColumn(nRow, "UPD_IP",       strUPD_IP);
			}
		}
		
	
		//fv_FindRow_ShowRow = 1;
		//fv_FindRow_ShowKey = strMAINTER_NUM;     //정비보수번호	
		
		var strSvcID = "save";
		var strPart  = "D10";	
		var strURL   = "handle.do?ServiceName=D1_MaintRepaiRequeTeamLeadApproMgr-service";
		var strInDatasets  = "ds_Save=ds_Save:u";
		var strOutDatasets = "";
		var strArgument  = "updateApproval=1";
		var strCallBackFunc = "fn_D1_CallBack";	
						
		gfn_AsyncCall( 
						 strSvcID
					   , strPart
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
					   , strCallBackFunc
				   );

	}
	
}

// 더블클릭 
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	// 선택셀은 제외
	if(nCell == 0) return;
	
	// 조회할 KEY
	var strMAINTER_NUM = ds_Multi.GetColumn(nRow, "MAINTER_NUM");

	// 기존화면조회
	var strArg = "";
	strArg += "TEAM_FLAG=1";
	strArg += " MAINTER_NUM="+quote(strMAINTER_NUM);
	
	strArg += " BRANCH_CD="+quote(cob_Branchcd.Value); 		// 지사
	strArg += " TEAM_CD="+quote(cob_TeamCdSearch.Value);    // 팀
	
	gfn_NewWindowAndExistClose("D10","D1601010010F",strArg);	
}
