//------------------------------------------------------------------------------- 
//   함수명: fn_D1_addRow()
//   설명  :  구간 행추가 
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_find()
{	
	ds_Search_Req.ClearData();
	var row = ds_Search_Req.AddRow();
	ds_Search_Req.SetColumn(row,"STR_REQ_YMD",cal_Ymd_From.Value);
	ds_Search_Req.SetColumn(row,"END_REQ_YMD",cal_Ymd_To.Value);
	ds_Search_Req.SetColumn(row,"BRANCH_CD",cob_branch.Value);
	ds_Search_Req.SetColumn(row,"TEAM_CD",cob_TeamCdSearch.Value);
	ds_Search_Req.SetColumn(row,"ZIP_NO1",cob_CountyFlag.Value);
	ds_Search_Req.SetColumn(row,"WORK_FIRM_CD",edt_WorkFirmCd.Text);
	
	ds_Req.ClearData();
	
    var strSvcID        = "search_Req";
	var strPart         = "D10";	             
	var strURL          = "handle.do?ServiceName=D1605030011F-service";
	var strInDatasets   = "ds_Search_Req=ds_Search_Req";
	var strOutDatasets  = "ds_Req=ds_Req";
	var strArgument     = "search_Req=1";

	gfn_syncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );			   
}

function fn_D1_Excel(){

	var SearchCondition= "";
   
    SearchCondition = SearchCondition + "지사 : " +     RPad(cob_branch.Text," ",20);
    SearchCondition = SearchCondition + "시/구 : " +    RPad(cob_CountyFlag.Text," ",20);
    SearchCondition = SearchCondition + "의뢰일자 : " + RPad(substr(today(),0,4) + '0101' + ' ~ ' + today()," ",40);
	SearchCondition = SearchCondition + "시공업체 : " + RPad(edt_WorkFirmNm.Text," ",20);
    
    gfn_ExportExcel("정비보수 정산 내역(의뢰일자별)", 5, SearchCondition, "Grid1");
}


