#include "JS::COMMONA10001.js";
#include "JS::COMMONA10002.js";
#include "JS::COMMOND10001.js";

function D1605040010F_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);
    fn_Init();
    gfn_D1_SetGrid_Sort(grd_Mainter);
    fn_D1_WorkFirmCdList();
}
/*===============================================================================
 * 기      능   :  공통 코드 셋팅
===============================================================================*/
function fn_Init()
{
	gfn_D1_SetComboBox_Branchcd(gfn_D1_setDsObject_Branchcd("cob_Branchcd"));					//지사코드
//-----------------------------------------
//  지사 콤보박스의 초기 설정
//-----------------------------------------	
	if (gfn_GetGlobalValue("G_BRANCH_CD") == "999999") {
		cob_Branchcd.Enable = true;
	}else{
		cob_Branchcd.Value = gfn_GetGlobalValue("G_BRANCH_CD");
		cob_Branchcd.Enable = false;
	} 			
	cob_BranchCd_OnChanged();
	gfn_D1_All_SetComboBox_Comcode("D10115",gfn_setDsObject_Comcode("cob_MainterStsFlag"),""); 	//공사진행상태
		cob_MainterStsFlag.Value="";
	gfn_D1_All_SetComboBox_Comcode("D10116",gfn_setDsObject_Comcode("cob_AssentType"),"");		//승인상태
		cob_AssentType.Index = 8;	
}
/********************************************************************************
* 기      능   :  지사_구 콤보 
********************************************************************************/
function cob_BranchCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
    fn_SetComboBox_Comcode("1", cob_BranchCd.value, fn_setDsObject_Comcode("1", "cob_ZipNo1"));
    //cob_ZipNo1.Index = 0;
    gfn_D1_SetComboBox_Teamcd("ds_Team",cob_Branchcd);
    
    if(cob_BranchCd.Value = "100584"){							//서부
		cob_ZipNo1.Value="";
		cob_TeamCd.Value="";
	}else if(cob_BranchCd.Value = "100664"){					//강북
		cob_ZipNo1.Value="";
		cob_TeamCd.Value="";
	}else if(cob_BranchCd.Value = "100591"){					//남부
		cob_ZipNo1.Value="";
		cob_TeamCd.Value="";
	}else if(cob_BranchCd.Value = "101500"){					//경기
		cob_ZipNo1.Value="";
		cob_TeamCd.Value="";
	}
}

/*===============================================================================
 * 기      능   :  공통 조회
===============================================================================*/
function fn_CommonSearch()
{
	if(cob_FaciNumSub.Value == "OUT"){
		fn_D1_List1();
		fn_D1_ExcelList1();
	}else{
		fn_D1_List();
		fn_D1_ExcelList();
	}
	edt_Count.Text = ds_Multi.GetRowCount();
}

/*===============================================================================
 * 기      능   :  조회(시설물)
===============================================================================*/
function fn_D1_List()
{
	var strSvcID = "";
	var strPart = "D10";	
	var strURL = "handle.do?ServiceName=D1_MaintRepaiWorkSearMgr-service&search=1"
				+ "&BRANCH_CD=" + cob_BranchCd.Value
				+ "&ZIP_NO1=" + cob_ZipNo1.Value
				+ "&TEAM_CD=" + cob_TeamCd.Value
				+ "&MAINTER_STS_FLAG=" + cob_MainterStsFlag.Value
				+ "&WORK_REQ_YMD_1_01=" + cal_WorkReqStrYmd.Value
				+ "&WORK_REQ_YMD_1_02=" + cal_WorkReqEndYmd.Value
				+ "&MAP_UPD_OBJ_YN=" + cob_Map.Value
				+ "&DANGER_APPRO_OBJ_YN=" + cob_Danger.Value
				+ "&FACI_NUM=" + cob_FaciNumSub.Value
				+ "&ASSENT_TYPE=" + cob_AssentType.Value
				+ "&WORK_FIRM_CD=" + cob_WorkFirmCd.Value
			   ;  															
	var strInDatasets  = "";
	var strOutDatasets  = "ds_Multi=ds_Multi";
	var strArgument  = "";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			    );
	grd_Mainter.NoDataText = gfn_GetMessage("M000022","%AAA%","조회결과가");		
}
