/*===============================================================================
 * 기      능   :  공통 Excel
===============================================================================*/
function fn_CommonExcelExport()
{
	var SearchCondition= "";
    SearchCondition = SearchCondition + "지사코드 :" + RPad(cob_BranchCd.Text," ",20);
    SearchCondition = SearchCondition + "팀코드 :" + RPad(cob_TeamCd.Text," ",20);
    SearchCondition = SearchCondition + "시/구 :" + RPad(cob_ZipNo1.Text," ",20);
    SearchCondition = SearchCondition + "진행상태 :" + RPad(cob_MainterStsFlag.Text," ",20);
    SearchCondition = SearchCondition + "작업의뢰일자 :" + RPad(cal_WorkReqStrYmd.Value +" ~ "+ cal_WorkReqEndYmd.Value," ",20);
    SearchCondition = SearchCondition + "위험승인대상 :" + RPad(cob_Danger.Text," ",20);
    SearchCondition = SearchCondition + "도면수정대상 :" + RPad(cob_Map.Text," ",20); 
    
    gfn_ExportExcel("정비보수 작업현황조회", 5, SearchCondition, "grd_ExcelList"); 	
}
//-------------------------------------------------------------------------------
