function fn_CommonPrint()
{
	PrintScreen(true);
}
//-------------------------------------------------------------------------------
// 설명     :   FROM ~ TO 체크 (조회)
//-------------------------------------------------------------------------------
function cal_WorkReqStrYmd_OnChanged(obj,strOldText,strNewText)
{
	if(cal_WorkReqStrYmd.Value !="" && cal_WorkReqEndYmd.Value !="" && cal_WorkReqStrYmd.Value > cal_WorkReqEndYmd.Value){	
		alert("TO 날짜는 FROM 날짜보다 같거나 큰값을 입력하세요.");
		cal_WorkReqStrYmd.SetFocus();
		cal_WorkReqStrYmd.value = "";
		cal_WorkReqEndYmd.value = "";		
		return;
	}	
}

function cal_WorkReqEndYmd_OnChanged(obj,strOldText,strNewText)
{
	if(cal_WorkReqStrYmd.Value !="" && cal_WorkReqEndYmd.Value !="" && cal_WorkReqStrYmd.Value > cal_WorkReqEndYmd.Value){	
		alert("TO 날짜는 FROM 날짜보다 같거나 큰값을 입력하세요.");
		cal_WorkReqStrYmd.SetFocus();
		cal_WorkReqStrYmd.value = "";
		cal_WorkReqEndYmd.value = "";		
		return;
	}	
}
//------------------------------------------------------------------------------- 
// 함수명: fn_D1_WorkFirmCdList()
//   설명:  정비 관련 시공사코드및 명 가져오기 
// arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_WorkFirmCdList()
{	
	var strSvcID = "";
	var strPart = "D10";	
	var strURL = "handle.do?ServiceName=D1_MaintRepaiWorkSearMgr-service&WorkFirmCd=1"
			   ;  
	var strInDatasets  = "";
	var strOutDatasets  = "ds_WorkFirmCd=ds_WorkFirmCd";
	var strArgument  = "";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	ds_WorkFirmCd.InsertRow(0);
	ds_WorkFirmCd.SetColumn(0,"DEAL_CD","");
	ds_WorkFirmCd.SetColumn(0,"DEAL_CD_NM","전체");
}
