function fn_Init()
{
	var result = today() ; //20090725	
	
	if(result < substr(result, 0, 4) + '0731'){
		cob_BaseYear.Value = substr(result, 0, 4);
	}else{
		cob_BaseYear.Value = toNumber(substr(result, 0, 4)) + toNumber(1);
	}
	
	
	// if(PIPE_OPER_PRICE_YEAR == '' || PIPE_OPER_PRICE_YEAR == null)
	// {
		// cob_BaseYear.Value =  substr(result, 0, 4);
	// }
	// else
	// {
		// cob_BaseYear.Value = PIPE_OPER_PRICE_YEAR;
	// }	
	// alert(cob_BaseYear.Value);
}

//---------------------------------------------------------
//	노무비 계산
//---------------------------------------------------------	
function Button18_OnClick(obj)
{	
	// 부장
	edt_m_1.Value = 0;
	edt_m_2.Value = 0;
	edt_m_3.Value = 0;
	edt_m_4.Value = 0;
	edt_m_5.Value = 0;
	 
	edt_m_1.Value  = toNumber(edt_p_1.Value) * toNumber(edt_t_1.Value) * toNumber(edt_c_1.Value);	
	edt_m_1.Value  = Truncate(edt_m_1.Value);
	
	// 차장
	edt_m_2.Value  = toNumber(edt_p_2.Value) * toNumber(edt_t_2.Value) * toNumber(edt_c_2.Value);
	edt_m_2.Value  = Truncate(edt_m_2.Value);
		
	// 과장
	edt_m_3.Value  = toNumber(edt_p_3.Value) * toNumber(edt_t_3.Value) * toNumber(edt_c_3.Value);
	edt_m_3.Value  = Truncate(edt_m_3.Value);
	
	// 계장
	edt_m_4.Value  = toNumber(edt_p_4.Value) * toNumber(edt_t_4.Value) * toNumber(edt_c_4.Value);
	edt_m_4.Value  = Truncate(edt_m_4.Value);
	
	//사원
	edt_m_5.Value  = toNumber(edt_p_5.Value) * toNumber(edt_t_5.Value) * toNumber(edt_c_5.Value);
	edt_m_5.Value  = Truncate(edt_m_5.Value);
	
	// 노무비 금액 소계
	edt_m_total.Value  = 0;
	edt_m_total.Value  = toNumber(edt_m_1.Value) + toNumber(edt_m_2.Value) + toNumber(edt_m_3.Value) + toNumber(edt_m_4.Value) + toNumber(edt_m_5.Value);
	edt_m_total.Value  = Truncate(edt_m_total.Value);
	
	// 스타렉스
	edt_m_21.Value  = 0;
	edt_m_21.Value  = toNumber(edt_p_21.Value) * toNumber(edt_t_21.Value) * toNumber(edt_c_21.Value);
	edt_m_21.Value  = Truncate(edt_m_21.Value);
	
	// 프라이드
	edt_m_22.Value  = 0;
	edt_m_22.Value  = toNumber(edt_p_22.Value) * toNumber(edt_t_22.Value) * toNumber(edt_c_22.Value);
	edt_m_22.Value  = Truncate(edt_m_22.Value);
	
	// 장비비 금액 소계
	edt_m_total1.Value = 0;
	edt_m_total1.Value = toNumber(edt_m_21.Value) + toNumber(edt_m_22.Value);
	edt_m_total1.Value  = Truncate(edt_m_total1.Value);

	// 전체 금액 소계
	edt_m_totala.Value  = 0;
	edt_m_totala.Value  = toNumber(edt_m_total.Value) + toNumber(edt_m_total1.Value);
	edt_m_totala.Value  = Truncate(edt_m_totala.Value);
	
	// 가스방출비 서울 금액
	edt_m_totalc.Value = 0;
	edt_m_totalc.Value = toNumber(edt_gasLeak_31.Value) * toNumber(edt_u_31.Value);
	edt_m_totalc.Value  = Truncate(edt_m_totalc.Value);
	
	// 가스방출비 경기 금액
	edt_m_totald.Value = 0;
	edt_m_totald.Value = toNumber(edt_gasLeak_32.Value) * toNumber(edt_u_32.Value);
	edt_m_totald.Value  = Truncate(edt_m_totald.Value);
							   
	// 간접노무비 금액 = 직접노무비 * 13.3%
	edt_a_1.Value = 0;
	edt_a_1.Value = toNumber(edt_m_total.Value) * (toNumber(Edit0.Value) * 0.01);
	edt_a_1.Value  = Truncate(edt_a_1.Value);
	
	// 산재고용보험료 = (직접노무비 + 간접노무비) * 4.1% 
	edt_a_2.Value = 0;
	edt_a_2.Value = (toNumber(edt_m_total.Value) + toNumber(edt_a_1.Value)) * (toNumber(Edit1.Value) * 0.01);
	edt_a_2.Value  = Truncate(edt_a_2.Value);
		         
	// 기타경비 =  (직접노무비 + 간접노무비) * 6.55%    
	edt_a_3.Value = 0;
	edt_a_3.Value = (toNumber(edt_m_total.Value) + toNumber(edt_a_1.Value)) * (toNumber(Edit2.Value) * 0.01);
	edt_a_3.Value  = Truncate(edt_a_3.Value);		         
		         
	// 안전관리비 = 직접노무비 * 2.48%
	edt_a_4.Value = 0;
	edt_a_4.Value = toNumber(edt_m_total.Value) * (toNumber(Edit3.Value) * 0.01) ;
		
	edt_a_4.Value  = Truncate(edt_a_4.Value);		
		
	// 관리비 합계
	edt_a_total.Value = 0;
	edt_a_total.Value = toNumber(edt_a_1.Value)
				 	  + toNumber(edt_a_2.Value)
					  + toNumber(edt_a_3.Value)
					  + toNumber(edt_a_4.Value)
					  ;
	edt_a_total.Value  = Truncate(edt_a_total.Value);
	
	// 일반관리비 = (소계 + 합계) * 6.0%
	edt_m_totalb.Value = 0;
	edt_m_totalb.Value = (toNumber(edt_m_totala.Value) + toNumber(edt_a_total.Value)) * (toNumber(Edit4.Value) * 0.01) ;
    edt_m_totalb.Value  = Truncate(edt_m_totalb.Value);
    
   // 총비용(소계 + 합계 + 관리비 + 가스방출비)
	edt_Pipe_Oper_Amt.Value = 0;
	if ( toNumber(pipeOperAmt) > 0 )
	{
		edt_Pipe_Oper_Amt.Value = pipeOperAmt;
		// 최초 한번만 적용한다.
		pipeOperAmt = 0;
	}
	else
	{
		edt_Pipe_Oper_Amt.Value = Truncate(((  toNumber(edt_m_totala.Value) 
											 + toNumber(edt_a_total .Value)
											 + toNumber(edt_m_totalb.Value)
											 + toNumber(edt_m_totalc.Value)
											 + toNumber(edt_m_totald.Value) ) / 100)) * 100 ;
	}
	//edt_Pipe_Oper_Amt.Value  = round(edt_Pipe_Oper_Amt.Value);
}

function fn_CommonSelect()
{
	if(ds_UnitPrice1.GetRowType(0) != 'normal') {
		
		var result = Confirm("변경된 데이터를 저장하시겠습니까? ");
		
		if(result == '0'){
				return;
		}
		
		fn_CommonSave();
	}
		
	var PIPE_OPER_AMT = edt_Pipe_Oper_Amt.Value;

	if(Confirm(cob_BaseYear + "년도 도관운영비 산출 정보를 사용하시겠습니까? ") == '0'){
		return;
	}
	
	fn_D1_save1();
	
	var arrList = array();
     
	arrList[0]  = edt_Pipe_Oper_Amt.Value;
	arrList[1]  = cob_BaseYear.Value     ;
	
	Close(arrList);
}

function edt_gasLeak_31_OnClick(obj)
{
	var arg  =  "PIPE_DIAM_VALUE="   + edt_pipeDiam_31.Value  ;
	    arg +=  " PRESS_VALUE="      + edt_press_31.Value     ; 
        arg +=  " LENG_VALUE="       + edt_leng_31.Value      ;
        arg +=  " GASLEAK_VALUE="    + edt_gasLeak_31.Value   ;
        arg +=  " PIPE_DIAM_VALUE2=" + edt_pipeDiam2_31.Value ;
	    arg +=  " PRESS_VALUE2="     + edt_press2_31.Value    ; 
        arg +=  " LENG_VALUE2="      + edt_leng2_31.Value     ;
        arg +=  " PRESS_FLAG="       + quote(PRESS_FLAG)      ;
        arg +=  " PRESS_FLAG2="      + quote(PRESS_FLAG2)     ;
        arg +=  " PIPE_DIAM_CD="     + quote(PIPE_DIAM_CD)    ;
        arg +=  " PIPE_DIAM_CD2="    + quote(PIPE_DIAM_CD2)   ;
		arg +=  " LENG="   		     + quote(LENG)            ;
		arg +=  " LENG2="   		 + quote(LENG2)           ;
		
	var arrList = gfn_Dialog("D10","D1702010011U",arg,464,496,true,-1,-1);
		
	if(arrList == '' || arrList == null || arrList == 0){
		return;
	}
		
    edt_pipeDiam_31.Value  = arrList[1];
    edt_press_31.Value     = arrList[2];
    edt_leng_31.Value      = arrList[3];
    
    edt_pipeDiam2_31.Value = arrList[5];
    edt_press2_31.Value    = arrList[6];
    edt_leng2_31.Value     = arrList[7];
    
    edt_gasLeak_31.Value    = arrList[8];
    
    Button18_OnClick();
}

