//-------------------------------------------------------------------------------
// 함수명: fn_CommonSave()
// 설명: 등록된 내용을 저장한다.
// arguments	없음
// return  없음
//-------------------------------------------------------------------------------
function fn_CommonSave(){
	
	if(length(cob_BaseYear.Value) < 4){
		alert("년도 형식이 부적절합니다. ");
		return;
	}
	
	if(fn_D1_Count1() <= 0){
		alert("등록된 단가정보가 없습니다. ");
		return;
	}
	
	// 2013.06.24. 이건민 - 권기철 과장님 요청사항 (저장 시 기존 도관운영비 조회)
	fn_D1_check();

	// 2013.06.25 이건민 - 주석처리
	/*
	fn_D1_save();

	alert(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을")); 
	
	if(Confirm(cob_BaseYear + "년도 도관운영비 산출 정보를 사용하시겠습니까? ") == '0'){
		return;
	}
	
	fn_D1_save1();
	
	var arrList = array();
	 
	arrList[0]  = edt_Pipe_Oper_Amt.Value;
	arrList[1]  = cob_BaseYear.Value     ;
	
	Close(arrList);
	*/
	
	// 2013.06.25. 이건민 - 권기철 과장님 요청사항 (저장 시 기존 도관운영년도와 적용년도 비교로직 추가)
	if(ds_Check.GetColumn(0, "BASE_YEAR") == "" || ds_Check.GetColumn(0, "BASE_YEAR") == null)
	{
		
		fn_D1_save();
	
		// 2013.06.25. 이건민 - 권기철 과장님 요청으로 주석처리
		//alert(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을")); 
		
		if(Confirm(cob_BaseYear + "년도 도관운영비 산출 정보를 사용하시겠습니까? ") == '0'){
			
			// 2013.06.25. 이건민 - 권기철 과장님 요청 (취소 시 도관운영비, 적용년도 공백리턴)
			var arrList = array();

			arrList[0]  = "";
			arrList[1]  = "";
			
			Close(arrList);
		}
		
		// 2013.06.25. 이건민 - 권기철 과장님 요청으로 주석처리
		//fn_D1_save1();
		
		var arrList = array();
		 
		arrList[0]  = edt_Pipe_Oper_Amt.Value;
		arrList[1]  = cob_BaseYear.Value     ;
		
		Close(arrList);
	}
	else
	{
		if(cob_BaseYear.Value == ds_Check.GetColumn(0, "BASE_YEAR"))
		{
			fn_D1_save();

			// 2013.06.25. 이건민 - 권기철 과장님 요청으로 주석처리
			//alert(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을")); 
			
			if(Confirm(cob_BaseYear + "년도 도관운영비 산출 정보를 사용하시겠습니까? ") == '0'){
								
				// 2013.06.25. 이건민 - 권기철 과장님 요청 (취소 시 도관운영비, 적용년도 공백리턴)
				var arrList = array();

				arrList[0]  = "";
				arrList[1]  = "";

				Close(arrList);
			}
			
			// 2013.06.25. 이건민 - 권기철 과장님 요청으로 주석처리
			//fn_D1_save1();
			
			var arrList = array();
			 
			arrList[0]  = edt_Pipe_Oper_Amt.Value;
			arrList[1]  = cob_BaseYear.Value     ;
			
			Close(arrList);
		}
		else
		{
			alert(""+ds_Check.GetColumn(0, "BASE_YEAR")+"년도에 적용된 도관운영비가 존재합니다.");
			return;
		}
	}
}

