//------------------------------------------------------------------------------- 
//   함수명: fn_D1_find()
//   설명  : 도관운영비 정보를 조회한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_find(){
	
	var strSvcID        = "";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1_ConduitOperatingExpensesMgr-service"	
						+ "&BASE_YEAR="       + cob_BaseYear.Value
						+ "&MOVE_REPAIR_NUM=" + replace(MOVE_REPAIR_NUM,"%$","")
						+ "&WORK_NUM="        + replace(WORK_NUM       ,"%$","")
						;
						
	var strInDatasets   = "";
	var strOutDatasets  = "ds_IndirCostPer=ds_IndirCostPer ds_UnitPrice1=ds_UnitPrice1";
	var strArgument     = "Search1";
	 	 
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
			   
	if(ds_IndirCostPer.GetRowCount()==0 || ds_UnitPrice.GetRowCount() == 0) {
	}
	Static34.Column = "DAY";
	/*
	if(today() < (substr(today(), 0, 4) + "0801")){
		Static34.Column = "DAY";
	}else{
		Static34.Column = "DAY1";
	}
	*/
}

//-------------------------------------------------------------------------------
//   함수명: fn_D1_check()
//   설명  : 이전 도관운영비 정보를 조회한다.
//   arguments
//   return 내용: 없음
//-------------------------------------------------------------------------------
function fn_D1_check()
{
	ds_Check.Clear();
	
	var strSvcID        = "";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1_ConduitOperatingExpensesMgr-service"	
						+ "&MOVE_REPAIR_NUM=" + replace(MOVE_REPAIR_NUM,"%$","")
						;
	var strInDatasets   = "";
	var strOutDatasets  = "ds_Check=ds_Check";
	var strArgument     = "SelectEmpCheck=1";
	 	 
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
}

//------------------------------------------------------------------------------- 
//   함수명: fn_D1_save()
//   설명  : 도관운영비 정보를 등록한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------- ------------ 
function fn_D1_save(){
	
	if(fn_D1_Count1() <= 0){
		alert("등록된 단가정보가 없습니다. ");
		return;
