// 2013.06.25. 이건민 - 권기철 과장님 추가요청사항 (도관운영비 삭제)
function fn_CommonDeleteRow()
{
	if(confirm("작성된 도관운영비 내역을 삭제하시겠습니까?"))
	{
		ds_Delete.AddRow();
		ds_Delete.UpdateControl=false;
		ds_Delete.SetColumn(0,"MOVE_REPAIR_NUM",	replace(MOVE_REPAIR_NUM,"%$",""));  
		ds_Delete.SetColumn(0,"BASE_YEAR",			cob_BaseYear.Value);
		ds_Delete.SetRowType(0,"delete");
				
		var strSvcID        = "";
		var strPart         = "D10";
		var strURL          = "handle.do?ServiceName=D1_ConduitOperatingExpensesMgr-service";  
		var strInDatasets   = "ds_Delete=ds_Delete:u";
		var strOutDatasets  = "";
		var strArgument     = "Delete=1";
		//var strCallbackFunc = "fn_D1_CallBack";
		
		gfn_SyncCall(strSvcID,
				  strPart,
				  strURL,
				  strInDatasets,
				  strOutDatasets,
				  strArgument,
				 );
				 
		if(ErrorCode < 0)
		{    
			alert(ErrorMsg);
			return;
		}
		else
		{
			alert(gfn_GetMessage("M000009","%AAA%","정상적으로 삭제를"));
			
			// 2013.06.25. 이건민 - 권기철 과장님 요청 (D1GT_MOVE_DOCU 테이블에 도관운영비, 도관년도 클리어)
			fn_D1_update();
			
			var arrList = array();

			arrList[0]  = "";
			arrList[1]  = "";
			
			Close(arrList);
		}
	}
}

