#include "JS::COMMONA10001.js";
#include "JS::COMMONA10002.js";

function D1702010011U_OnLoadCompleted(obj)
{
	gfn_LoadForm(obj);

	if(PIPE_DIAM_VALUE  == 0){
		edt_D.Value = decode(PIPE_DIAM_CD, ''   , 0, toNumber(PIPE_DIAM_CD) * 0.001);
	}else{
		edt_D.Value = PIPE_DIAM_VALUE;
	}
	
	if(PIPE_DIAM_VALUE2 == 0){
		edt_D2.Value = decode(PIPE_DIAM_CD2, '', 0, toNumber(PIPE_DIAM_CD2) * 0.001);
	}else{
		edt_D2.Value = PIPE_DIAM_VALUE2;
	}	
	
	if(LENG_VALUE == 0){
		edt_L.Text  = decode(LENG, '', 0, LENG);	
	}else{
		edt_L.Value = LENG_VALUE;
	}
	
	if(LENG_VALUE2 == 0){
		edt_L2.Text  = decode(LENG2, '', 0, LENG2);	
	}else{
		edt_L2.Value = LENG_VALUE2;
	}
	
	if(PRESS_VALUE == 0){
		if(PRESS_FLAG == '1'){
			edt_P.Value = 0.02 ;
		}else if(PRESS_FLAG == '2'){
			edt_P.Value = 8.5 ;
		}else if(PRESS_FLAG == '3'){
			edt_P.Value = 4.0 ;
		}else if(PRESS_FLAG == '4'){
			edt_P.Value = 0.2 ;
		}else{
			edt_P.Value = 0 ;
		}
	}else{
		edt_P.Value = PRESS_VALUE;
	}
	
	if(PRESS_VALUE2 == 0){
		if(PRESS_FLAG2 == '1'){
			edt_P2.Value = 0.02 ;
		}else if(PRESS_FLAG2 == '2'){
			edt_P2.Value = 8.5 ;
		}else if(PRESS_FLAG2 == '3'){
			edt_P2.Value = 4.0 ;
		}else if(PRESS_FLAG2 == '4'){
			edt_P2.Value = 0.2 ;
		}else{
			edt_P2.Value = 0 ;
		}
	}else{
		edt_P2.Value = PRESS_VALUE2;
	}
	
    Button18_OnClick();
}

//------------------------------------------------------------------------------- 
//   함수명: Button18_OnClick()
//   설명  : 관경, 압력, 연장값을 입력시 방출량이 산출된다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function Button18_OnClick(obj)
{	
	var PI = 3.14159265358979;
	
	edt_Q.Value  = (PI * Pow(toNumber(edt_D.Value),2))/4*toNumber(edt_L.Value)*((toNumber(edt_P.Value)+1.0332)/1.0332);
		
	edt_Q.Value  = ROUND(toNumber(edt_Q.Value), 2);
	
	edt_Q2.Value = (PI * Pow(toNumber(edt_D2.Value),2))/4*toNumber(edt_L2.Value)*((toNumber(edt_P2.Value)+1.0332)/1.0332);
		
	edt_Q2.Value = ROUND(toNumber(edt_Q2.Value), 2);
	
	edt_Q_Total.Value = toNumber(edt_Q.Value) + toNumber(edt_Q2.Value);
}

//------------------------------------------------------------------------------- 
//   함수명: fn_CommonSelect()
//   설명  : 관경, 압력, 연장값을 입력시 방출량이 산출된다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_CommonSelect()
{
	if(edt_D.Value == ''){
		alert("관경을 입력하세요.(없는 경우 0을 입력하세요 ");
		return;
	}
	
	if(edt_P.Value == ''){
		alert("압력을 입력하세요.(없는 경우 0을 입력하세요 ");
		return;
	}
	
	if(edt_L.Value == ''){
		alert("연장을 입력하세요.(없는 경우 0을 입력하세요 ");
		return;
	}

	 var arrList = array();
     
//	 arrList[0]  = edt_Q.Value;
	 arrList[1]  = edt_D.Value;
	 arrList[2]  = edt_P.Value;
	 arrList[3]  = edt_L.Value;
	
//	 arrList[4]  = edt_Q2.Value;
	 arrList[5]  = edt_D2.Value;
	 arrList[6]  = edt_P2.Value;
	 arrList[7]  = edt_L2.Value;
	 
	 arrList[8]  = edt_Q_Total.Value;
	  	     
     Close(arrList);
}
