===============================================================================*/ 
//-------------------------------------------------------------------------------
// 함수명: fn_CommonSave()
// 설명: 등록된 내용을 저장한다.
// arguments	없음
// return  없음
//-------------------------------------------------------------------------------
function fn_CommonSave()
{	
	 if(ds_MoveDocuV.GetColumn(0, "PROC_STS_CD") == '30')
	 {
		// 보수와, 잔존가일 경우
		//if(ds_MoveDocuV.GetColumn(0, "MOVE_REPAIR_TYPE") == '20' || ds_MoveDocuV.GetColumn(0, "MOVE_REPAIR_TYPE") == '30'){			
			if( gfn_IsNull( Calendar7.Value ) )
			{
				alert("공사완료일자는 필수입력사항입니다.");
				return;
			}
			
			if( gfn_IsNull( edt_WORK_FIRM_ADJ_COST.Value ) )
			{
				alert("정산금액은 필수입력사항입니다.");
				return;
			}
					
			if( edt_WORK_FIRM_ADJ_COST.Value == 0)
			{
				alert("정산금액을 0보다 큰 금액으로 입력하세요.");
				return;
			}
			
			// 2013.05.15. 이건민 - 권기철 과장님 추가요청 사항 (산정금액과 정산금액 비교)
			// 정산금액, 산정금액
			if( currency(edt_WORK_FIRM_ADJ_COST.Value) > currency(edt_REQ_AMT.Value) )
			{
				alert("정산금액은 산정금액과 같거나 작아야 합니다.");
				return;
			}
			
			if( gfn_IsNull( Calendar9.Value ) )
			{
				alert("정산일자는 필수입력사항입니다.");
				return;
			}
			
			// 2013.05.09. 이건민 - 권기철 과장님 요청사항 (환불 체크)
			if( (edt_REFUND_AMT.Value != 0 && edt_REFUND_AMT.Value != "") || cal_RefundAmtPayYmd.Value != "" || ( cob_BnkCd.Value != "" && cob_BnkCd.Value != null) )
			{
				if(edt_REFUND_AMT.Value == 0  )
				{
					alert("환불금액이 발생하지 않았습니다. ");
					return;
				}
				
				if( gfn_IsNull( cal_RefundAmtPayYmd.Value ) )
				{
					alert("환불지급일자는 필수입력사항입니다.");
					return;
				}
				
				if( gfn_IsNull( edt_DEAL_CD.Value ) )
				{
					alert("환불 정보를 입력하십시오.");
					return;
				}
				
				if( gfn_IsNull( cob_BnkCd.Value ) )
				{
					alert("환불 정보를 입력하십시오.");
					return;
				}
			}
		//}
	}
	
	if(ds_MoveDocuV.GetColumn(0, "PROC_STS_CD") == '50')
	{
	
		// 보수와, 잔존가일 경우
		//if(ds_MoveDocuV.GetColumn(0, "MOVE_REPAIR_TYPE") == '20' || ds_MoveDocuV.GetColumn(0, "MOVE_REPAIR_TYPE") == '30'){			
			
			//if(edt_REFUND_AMT.Value == 0 || 0 < toNumber(edt_WORK_FIRM_ADJ_COST.Value)-toNumber(edt_REQ_AMT.Value) )
			
			// 2013.05.08.이건민 - 권기철 과장님 추가요청 (진행상태 정산 시 공사완료일자, 정산금액, 정산일자 체크)
			////////////////////////////////////////////////////////////////////////////////////////////////////
			if( gfn_IsNull( Calendar7.Value ) )
			{
				alert("공사완료일자는 필수입력사항입니다. ");
				return;
			}
			
			if( gfn_IsNull( edt_WORK_FIRM_ADJ_COST.Value ) )
			{
				alert("정산금액은 필수입력사항입니다. ");
				return;
			}
			
			if( edt_WORK_FIRM_ADJ_COST.Value == 0)
			{
				alert("정산금액을 0보다 큰 금액으로 입력하세요. ");
				return;
			}
			
			// 2013.05.15. 이건민 - 권기철 과장님 추가요청 사항 (산정금액과 정산금액 비교 체크)
			if( currency(edt_WORK_FIRM_ADJ_COST.Value) > currency(edt_REQ_AMT.Value) )
			{
				alert("정산금액은 산정금액과 같거나 작아야 합니다.");
				return;
			}
			
			if( gfn_IsNull( Calendar9.Value ) )
			{
				alert("정산일자는 필수입력사항입니다. ");
				return;
			}
			
			// 2013.05.09. 이건민 - 권기철 과장님 요청사항 (환불 체크)
			if( (edt_REFUND_AMT.Value != 0 && edt_REFUND_AMT.Value != "") || cal_RefundAmtPayYmd.Value != "" || ( cob_BnkCd.Value != "" && cob_BnkCd.Value != null) )
			{
				
				if(edt_REFUND_AMT.Value == 0  )
				{
					alert("환불금액이 발생하지 않았습니다.");
					return;
				}
											
				if( gfn_IsNull( cal_RefundAmtPayYmd.Value ) )
				{
					alert("환불지급일자는 필수입력사항입니다.");
					return;
				}
				
				if( gfn_IsNull( edt_DEAL_CD.Value ) )
				{
					alert("환불 정보를 입력하십시오.");
					return;
				}
				
				if( gfn_IsNull( cob_BnkCd.Value ) )
				{
					alert("환불 정보를 입력하십시오.");
					return;
				}
			}
		//}
	}
	
	//2011.07.07 이상목 권기철과장님요청 공사내역이 있으면 fn_D1_save를 먼저 태움
	if(ds_MoveAdj.GetRowCount() > 0)
	{
		fn_D1_save();
	}else{
		fn_D1_save1();
	}
	
}

//------------------------------------------------------------------------------- 
//   함수명 : fn_D1_SAVE
//   설명  : 이설 보수 정산 내역 저장한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_save()
{	               
	ds_MoveAdj.UpdateControl = false;

	if(ds_MoveAdj1.GetRowCount() > 0){
		 for(var i=0; i<ds_MoveAdj.GetRowCount(); i++)
		 {	
			ds_MoveAdj.SetColumn(i, "UPD_EMPID"  , gfn_GetGlobalValue("G_USER_NO"));
			ds_MoveAdj.SetColumn(i, "UPD_IP"     , gfn_GetGlobalValue("G_IPADDRESS"));	
			ds_MoveAdj.SetColumn(i, "CRT_EMPID"  , gfn_GetGlobalValue("G_USER_NO"));
			ds_MoveAdj.SetColumn(i, "CRT_IP"     , gfn_GetGlobalValue("G_IPADDRESS"));		 
			ds_MoveAdj.SetRowType(i, "update");
			
			var strSvcID         = "insert";
			var strPart          = "D10";	
			var strURL           = "handle.do?ServiceName=D1_MoveingRepairAmountMgr-service";	
			var strInDatasets    = "ds_MoveAdj=ds_MoveAdj:u";
			var strOutDatasets   = "";		
			var strArgument      = "insert=1";
			
			//ds_MoveAdj.UpdateControl = false;	
			
			gfn_SyncCall( 
							 strSvcID
						   , strPart  	 
						   , strURL
						   , strInDatasets
						   , strOutDatasets
						   , strArgument
						);
		
			// 등록 후 진행상태 update
			//fn_D1_update();
			 
			if(ErrorCode<0)
			{    
				return false;
			}
	
		 }
	 }else{
		for(var i=0; i<ds_MoveAdj.GetRowCount(); i++){			
			ds_MoveAdj.SetColumn(i, "UPD_EMPID"  , gfn_GetGlobalValue("G_USER_NO"));
			ds_MoveAdj.SetColumn(i, "UPD_IP"     , gfn_GetGlobalValue("G_IPADDRESS"));	
			ds_MoveAdj.SetColumn(i, "CRT_EMPID"  , gfn_GetGlobalValue("G_USER_NO"));
			ds_MoveAdj.SetColumn(i, "CRT_IP"     , gfn_GetGlobalValue("G_IPADDRESS"));		
			// 이설보수번호를 Row 값으로 정산내역에 이설보수번호로 셋팅
			//ds_MoveAdj.SetColumn(i, "MOVE_REPAIR_NUM", ds_MoveDocuV.GetColumn(0, "MOVE_REPAIR_NUM"));	
			ds_MoveAdj.SetRowType(i, "insert"); //2011.07.07 이상목 권기철과장님 요청 기존 두개로 구분되는걸 인서트로		
			
			var strSvcID         = "insert";
			var strPart          = "D10";	
			var strURL           = "handle.do?ServiceName=D1_MoveingRepairAmountMgr-service";	
			var strInDatasets    = "ds_MoveAdj=ds_MoveAdj:u";
			var strOutDatasets   = "";		
			var strArgument      = "insert=1";
			
			//ds_MoveAdj.UpdateControl = false;	
			
			gfn_SyncCall( 
							 strSvcID
						   , strPart  	 
						   , strURL
						   , strInDatasets
						   , strOutDatasets
						   , strArgument
						);
		
			// 등록 후 진행상태 update
			//fn_D1_update();
			 
			if(ErrorCode<0)
			{    
				return false;
			}
		}
	 }			
	
	//"저장을 완료하였습니다."
	//alert("저장을 완료하였으며 이설보수 진행상태가 ["+ ds_MoveDocu.GetColumn(ds_MoveDocu.row, "PROC_STS_NM") + "] 로 변경되었습니다.");
	//gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
	fn_D1_save1();
	fn_CommonSearch();
}

//------------------------------------------------------------------------------- 
//   함수명 : fn_D1_SAVE
//   설명  : 이설 보수 정산 내역 저장한다.
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_D1_save1()
{		
	if(ds_MoveDocuV.GetRowType(0) == 'normal')
	{
		alert("변경내역이 없습니다. ");
		return;
	}

	var USER_ID = gfn_GetGlobalValue("G_USER_NO")  ;   // SESSION ID
	var USER_IP = gfn_GetGlobalValue("G_IPADDRESS");   // SESSION IP
	
	var strFlag = "";
	
	ds_MoveDocuV.SetColumn(0, "UPD_EMPID"  , USER_ID       );
	ds_MoveDocuV.SetColumn(0, "UPD_IP"     , USER_IP       );	
	
	if(cal_RefundAmtPayYmd.Value == '' || cal_RefundAmtPayYmd.Value == null)
	{
		ds_MoveDocuV.SetColumn(0, "PROC_STS_CD", "50"); // 접수 진행 코드
		strFlag = "정산";
	}else{
		ds_MoveDocuV.SetColumn(0, "PROC_STS_CD", "60"); // 접수 진행 코드
		strFlag = "환불";
	}

	var strSvcID         = "update1";
	var strPart          = "D10";	
	var strURL           = "handle.do?ServiceName=D1_MoveingRepairAmountMgr-service";
	var strInDatasets    = "ds_MoveDocuV=ds_MoveDocuV:u";
	var strOutDatasets   = "";		
	var strArgument      = "update1=1";
	
	gfn_SyncCall( 
					 strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			    );

	if(ErrorCode<0)
	{    
		return false;
	}
	
	alert("저장을 완료하였으며 이설보수 진행상태가 ["+ strFlag + "] 로 변경되었습니다.");
	fn_CommonSearch();
	//gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","정상적으로 저장을"),"black",true); 
}

///-------------------------------------------------------------------------------
// 이설보수접수내역 조회
//-------------------------------------------------------------------------------
function fn_CommonSearch()
{		
	fn_DateCheck(cal_ReceiveStart, cal_ReceiveEnd);
	
	ds_MoveDocu.ClearData();
	ds_Excel.ClearData();
	
	ds_MoveDocuV.ClearData();
	ds_MoveAdj1.ClearData();
	ds_MoveAdj2.ClearData();		
		
    var strSvcID        = "search";
	var strPart         = "D10";	
	var strURL          = "handle.do?ServiceName=D1_MoveingRepairAmountMgr-service";
	var strInDatasets   = "";
	var strOutDatasets  = "ds_MoveDocu=ds_MoveDocu ds_Excel=ds_Excel";
	var strArgument     = "search=1";
		strArgument	   += " BRANCH_CD="          + quote(cob_ObjectFlag.Value);
		strArgument	   += " ZIP_NO1="            + quote(cob_CountyFlag.Value);
		strArgument	   += " RECEIVE_YMD_START="  + quote(cal_ReceiveStart.Value);
		strArgument	   += " RECEIVE_YMD_END="    + quote(cal_ReceiveEnd.Value);
		strArgument	   += " MOVE_REPAIR_TYPE="   + quote(cob_ConstructFlag.Value);
		strArgument	   += " PROC_STS_CD="        + quote(cob_MoveStsCD.Value);
	//var strCallbackFunc = "fn_D1_CallBack";
							 	 
	gfn_SyncCall( 
	//gfn_AsyncCall(
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
				  // , strCallbackFunc
			   );

	
	st_Cnt.Value = LPAD( ds_MoveDocu.GetRowCount(), '0', 4);	
}


//--------------------------------------------------------------------------------------------------
// 로우 변경시
//--------------------------------------------------------------------------------------------------
function ds_MoveDocu_OnRowPosChanged(obj,nOldRow,nRow)
{
	if(nRow < 0) return;

		ds_MoveDocuV.ClearData();
		ds_MoveAdj1.ClearData();
		ds_MoveAdj2.ClearData();
	
		ds_Search.SetColumn(0,"MOVE_REPAIR_NUM", ds_MoveDocu.GetColumn(nRow, "MOVE_REPAIR_NUM"));
		
		var strSvcID = "view";
		var strPart = "D10";	
		var strURL = "handle.do?ServiceName=D1_MoveingRepairAmountMgr-service";  
		var strInDatasets   = "ds_Search=ds_Search";
		var strOutDatasets  = "ds_MoveDocuV=ds_MoveDocuV ds_MoveAdj1=ds_MoveAdj1 ds_MoveAdj2=ds_MoveAdj2";
		var strArgument     = "view=1";
		//var strCallbackFunc = "fn_D1_CallBack";
			
		gfn_SyncCall( 
		//gfn_AsyncCall(
						 strSvcID
					   , strPart
					   , strURL
					   , strInDatasets
					   , strOutDatasets
					   , strArgument
			//		   , strCallbackFunc
				   );		   

//trace("이설보수유형:" + ds_MoveDocu.GetColumn(ds_MoveDocu.row,"MOVE_REPAIR_TYPE"));
//trace("진행상태:" + ds_MoveDocu.GetColumn(ds_MoveDocu.row,"PROC_STS_CD"));

		//이설보수유형이 = 이설(10)  이설보수진행상태 = 입금(30)
		if(ds_MoveDocu.GetColumn(ds_MoveDocu.row,"MOVE_REPAIR_TYPE") == "10" && ds_MoveDocu.GetColumn(ds_MoveDocu.row,"PROC_STS_CD") == "30")
		{

			Grd_MoveAdj.Editable = true;
			ds_MoveAdj.Copy("ds_MoveAdj2"); //공사쪽데이터

			//edt_WORK_FIRM_ADJ_COST.value = ds_MoveAdj.GetColumn(0, "TOTAL_AMT");
			
			var PIPE_OPER_AMT = 0; //도관운영비 합하기 
			
			for(var i=0; i<ds_MoveAdj.rowcount; i++)
			{
				PIPE_OPER_AMT = toNumber(ds_MoveAdj.GetColumn(i,"PIPE_OPER_AMT"));				
				ds_MoveAdj.SetColumn(i, "TOTAL_AMT",toNumber(ds_MoveAdj.GetColumn(i, "SUM_PRICE")) + toNumber(PIPE_OPER_AMT)); //공사내역 합계(정산비용)			
			}
			
			//ds_MoveAdj.SetColumn(0, "TOTAL_AMT",ds_MoveAdj.GetColumn(0, "SUM_PRICE")); //공사내역 합계(정산비용)			
			//ds_MoveDocuV.SetColumn(0, "WORK_FIRM_ADJ_COST", toNumber(ds_MoveAdj.GetColumn(0, "TOTAL_AMT")) + toNumber(PIPE_OPER_AMT)); //합계금액 정산금액에 셋팅
			//ds_MoveAdj.SetColumn(0, "TOTAL_AMT",toNumber(ds_MoveAdj.GetColumn(0, "SUM_PRICE")) + toNumber(PIPE_OPER_AMT)); //공사내역 합계(정산비용)			
			ds_MoveDocuV.SetColumn(0, "WORK_FIRM_ADJ_COST",ds_MoveAdj.SumNF("TOTAL_AMT")); //합계금액 정산금액에 셋팅
			ds_MoveDocuV.SetColumn(0, "REFUND_AMT",toNumber(ds_MoveDocuV.GetColumn(0, "WORK_FIRM_ADJ_COST")) - toNumber(ds_MoveDocuV.GetColumn(0, "REQ_AMT"))); //환불금액 = 정산금액 - 산정금액			
		}
		else
		{	
			Grd_MoveAdj.Editable = false;
			ds_MoveAdj.Copy("ds_MoveAdj1");	//안전공급쪽데이터		
		}		
		
		cob_MoveRepairType_OnChanged();
		fn_d1_FileSearch();
				
		edt_REFUND_AMT_OnChanged();
				
		fn_D1_CheckBnkCD();
		Radio0_OnClick();
				
		// 2013.05.15. 이건민 - 권기철 과장님 추가요청으로 주석처리
		/*
		if(ds_MoveDocuV.GetColumn(0, "PROC_STS_CD") == '50'){ // 정산 
			Button6.Enable     = true ;
			btn_billDel.Enable = true ;
		}else if(ds_MoveDocuV.GetColumn(0, "PROC_STS_CD") == '60'){ // 환불
			Button6.Enable     = true ;
			btn_billDel.Enable = true ;
		}else{
			Button6.Enable     = false;
			btn_billDel.Enable = false;
		}
		*/
		
		if(ds_MoveDocuV.GetColumn(0, "PROC_STS_CD") == '50'){ // 정산 
			//Button6.Enable     = true ;
			//btn_billDel.Enable = true ;
		}else if(ds_MoveDocuV.GetColumn(0, "PROC_STS_CD") == '60'){ // 환불
			//Button6.Enable     = true ;
			//btn_billDel.Enable = true ;
		}else{
			Button6.Enable     = false;
			btn_billDel.Enable = false;
		}
		
	//}

}


//------------------------------------------------------------------------------- 
//   함수명: Grd_MoveAdj_OnCellClick()
//   설명  :  팝업 값이 바뀌는 함수
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function Grd_MoveAdj_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var strColID = Grd_MoveAdj.GetCellProp("body", nCell, "colid"); // 선택한 셀의 프로퍼티를 가져온다.
      
    var args = '';  
	if (strColID == "MOVE_LIST") { // GetCellProp로 colid를 가져왔기 때문에 colid의 값을 리턴한다.
		
		//args  = "WORK_NUM="          + "20082027434" +"%$";
		args  = "WORK_NUM="          + ds_MoveAdj.GetColumn(nRow, "WORK_NUM")+"%$";
		args += " MOVE_REPAIR_NUM="  + ds_MoveDocu.GetColumn(ds_MoveDocu.row, "MOVE_REPAIR_NUM")+"%$";
		args += " WORK_NM="          + replace(ds_MoveAdj.GetColumn(nRow, "WORK_NM"), ' ', '@@')+"%$";
		
		gfn_Dialog("D10","D1703010010U",args,660,250,true,-1,-1);
    } 
}

