//-------------------------------------------------------------------------------
// 함수명: fn_CommonExcelExport()
// 설명: 엑셀 button 실행, 조회된데이터를 엑셀형식으로변환한다
// arguments	없음
// return  없음
//-------------------------------------------------------------------------------
function fn_CommonExcelExport()
{	
	var SearchCondition= "";
    
    SearchCondition = SearchCondition + "지사 : "     + RPad(cob_ObjectFlag   .Text," ",20);
    SearchCondition = SearchCondition + "시/구 : "    + RPad(cob_CountyFlag   .Text," ",20);
    SearchCondition = SearchCondition + "유형 : "     + RPad(cob_ConstructFlag.Text," ",20);
    SearchCondition = SearchCondition + "접수일자 : " + RPad(cal_ReceiveStart .Value + ' ~ ' + cal_ReceiveEnd.Value," ",20);
        
    gfn_ExportExcel("이설보수 정산내역 등록", 5, SearchCondition, "grd_excel");
}



function cob_MoveRepairType_OnChanged()
{
	// 잔존가일 경우에는
	if(cob_MoveRepairType.Value == '30' && ds_MoveDocuV.GetRowType(0) == 'insert'){
		alert(gfn_GetMessage("M000004","%AAA%","폐관비용 산정을"));
		cob_MoveRepairType.index = 0;
		return;
	}	
	
	if(ds_MoveDocuV.GetColumn(0, "MAINTER_NUM") == '' || ds_MoveDocuV.GetColumn(0, "MAINTER_NUM") == null){
		Static34 .Visible = true;
		Edit5    .Visible = true;
		
		Static25 .Visible = false;
		Edit7    .Visible = false;
		
		Static42 .Visible = false;
		Edit13   .Visible = false;
		Static43 .Visible = false;
		Calendar4.Visible = false;
		
		Static32 .Visible = true;
		Edit9    .Visible = true;
		Static33 .Visible = true;
		Edit10	 .Visible = true;		
	}else{
		Static34.Visible = false;
		Edit5   .Visible = false;
		
		Static25.Visible = true;
		Edit7   .Visible = true;
		
		Static42 .Visible = true;
		Edit13   .Visible = true;
		Static43 .Visible = true;
		Calendar4.Visible = true;
		
		Static32 .Visible = false;
		Edit9    .Visible = false;
		Static33 .Visible = false;
		Edit10	 .Visible = false;	
	}
		
		
	// 상태값 변화 : 이설
	if(cob_MoveRepairType.Value == '10'){
		Static16.Text = '이설번호';
		Static3.Text  = '이설위치';
		Static0.Text  = '이설공사명';
	}

	// 상태값 변화 : 보수
	if(cob_MoveRepairType.Value == '20'){
		Static16.Text = '보수번호';
		Static3.Text  = '보수위치';
		Static0.Text  = '보수공사명';	
	}
	
	// 상태값 변화 : 잔존가
	if(cob_MoveRepairType.Value == '30'){
		Static16.Text = '잔존가번호';
		Static3.Text  = '잔존가위치';
		Static0.Text  = '잔존가공사명';
	}
		
	// 입금
	if(ds_MoveDocuV.GetColumn(0, "PROC_STS_CD") == '30')
	{
		cob_BnkCd.Enable = false;				
		cob_BnkCd.Text = '';
				
		edt_PayAccountNum.Enable = false;
		edt_PayAccountNum.Text = '';
		
		Button4.Enable = false;
		Button104.Enable = false;
		Button6.Enable = false;
		btn_billDel.Enable = false;
		Edit21.Enable = false;
		edt_DEAL_CD.Enable = false;
				
		//Edit21.Text = '';
		//edt_DEAL_CD.Text = '';
		
		cal_RefundAmtPayYmd.Enable = false;
		
		// 2013.05.22. 이건민 - 권기철 과장님 추가요청 사항 (정비보수 번호가 있다면 공사완료일자 비활성화)
		if(ds_MoveDocuV.GetColumn(0, "MAINTER_NUM") == '' || ds_MoveDocuV.GetColumn(0, "MAINTER_NUM") == null)
		{
			Calendar7.Enable = true;			// 공사완료일자
		}
		else
		{
			Calendar7.Enable = false;			// 공사완료일자
		}
		
		edt_WORK_FIRM_ADJ_COST.Enable = true;
		Calendar9.Enable = true;
		edt_REFUND_AMT.Enable = false;
	}
	
	// 정산
	if(ds_MoveDocuV.GetColumn(0, "PROC_STS_CD") == '50')
	{	
		Edit21.Enable = false;
		edt_DEAL_CD.Enable = false;
		
		if( !gfn_IsNull( Calendar0.Value) ) // 전표번호가 있으면
		{
			cal_RefundAmtPayYmd.Enable = false;		// 환불지급일자
			edt_PayAccountNum.Enable = false;		// 환불계좌번호
			Button4.Enable = false;					// 환불계좌번호 버튼
			cob_BnkCd.Enable = false;				// 환불은행명		
					
			Calendar7.Enable = false;				// 공사완료일자
			edt_WORK_FIRM_ADJ_COST.Enable = false;	// 정산금액
			Calendar9.Enable = false;				// 정산일자
			edt_REFUND_AMT.Enable = false;			// 환불금액
		
			Button104.Enable = false;				// 지불계좌 추가
			Button6.Enable = false;					// 전표등록 버튼
			btn_billDel.Enable = true;				// 전표취소 버튼
		}
		else
		{
			cal_RefundAmtPayYmd.Enable = true;		// 환불지급일자
			edt_PayAccountNum.Enable = true;		// 환불계좌번호
			Button4.Enable = true;					// 환불계좌번호 버튼
			cob_BnkCd.Enable = true;				// 환불은행명
			
			// 2013.05.22. 이건민 - 권기철 과장님 추가요청 사항 (정비보수 번호가 있다면 공사완료일자 비활성화)
			if(ds_MoveDocuV.GetColumn(0, "MAINTER_NUM") == '' || ds_MoveDocuV.GetColumn(0, "MAINTER_NUM") == null)
			{
				Calendar7.Enable = true;			// 공사완료일자
			}
			else
			{
				Calendar7.Enable = false;			// 공사완료일자
			}
			
			edt_WORK_FIRM_ADJ_COST.Enable = true;	// 정산금액
			Calendar9.Enable = true;				// 정산일자
			edt_REFUND_AMT.Enable = false;			// 환불금액
		
			Button104.Enable = true;				// 지불계좌 추가
			Button6.Enable = true;					// 전표등록 버튼
			btn_billDel.Enable = false;				// 전표취소 버튼
		}

	}
	
	
	
	/*
	// 2013.05.07. 이건민 - 권기철 과장님 정산 시 사용여부 재정의로 주석
	// 정산
	if(ds_MoveDocuV.GetColumn(0, "PROC_STS_CD") == '50')
	{ alert("123");
		cob_BnkCd.Enable = true;
		edt_PayAccountNum.Enable = true;
		Button4.Enable = true;
		Button104.Enable = true;
		Button6.Enable = false;
		btn_billDel.Enable = false;
		cal_RefundAmtPayYmd.Enable = true;
		Calendar7.Enable = false;
		edt_WORK_FIRM_ADJ_COST.Enable = false;
		Calendar9.Enable = false;
		edt_REFUND_AMT.Enable = false;
	}
	*/
		
	/*
	// 2012.05.09 권기철 과장님 환불 시 사용여부 재정의로 주석처리_이건민
	// 환불
	if(ds_MoveDocuV.GetColumn(0, "PROC_STS_CD") == '60')
	{
		cob_BnkCd.Enable = false;
		edt_PayAccountNum.Enable = false;
		Button4.Enable = false;
		Button104.Enable = true;
		Button6.Enable = true;
		btn_billDel.Enable = true;
		//cal_RefundAmtPayYmd.Enable = false;
		Calendar7.Enable = false;
		edt_WORK_FIRM_ADJ_COST.Enable = false;
		Calendar9.Enable = false;
		edt_REFUND_AMT.Enable = false;
		
		edt_DEAL_CD.Enable = false;
		Edit21.Enable = false;
		cal_RefundAmtPayYmd.Enable = false;
	}
	*/
	if(ds_MoveDocuV.GetColumn(0, "PROC_STS_CD") == '60')
	{
		Edit21.Enable = false;
		edt_DEAL_CD.Enable = false;
	
		if( !gfn_IsNull( Calendar0.Value) ) // 전표번호가 있으면
		{
			Button104.Enable = false;				// 지불계좌 추가
			Button6.Enable = false;					// 전표등록 버튼
			btn_billDel.Enable = true;				// 전표취소 버튼			
			
			
			cal_RefundAmtPayYmd.Enable = false;		// 환불지급일자
			edt_PayAccountNum.Enable = false;		// 환불계좌번호
			Button4.Enable = false;					// 환불계좌번호 버튼
			cob_BnkCd.Enable = false;				// 환불은행명		
					
			Calendar7.Enable = false;				// 공사완료일자
			edt_WORK_FIRM_ADJ_COST.Enable = false;	// 정산금액
			Calendar9.Enable = false;				// 정산일자
			edt_REFUND_AMT.Enable = false;			// 환불금액			
			
		}
		else
		{
			Button104.Enable = true;				// 지불계좌 추가
			Button6.Enable = true;					// 전표등록 버튼
			btn_billDel.Enable = false;				// 전표취소 버튼
			
			cal_RefundAmtPayYmd.Enable = true;		// 환불지급일자
			edt_PayAccountNum.Enable = true;		// 환불계좌번호
			Button4.Enable = true;					// 환불계좌번호 버튼
			cob_BnkCd.Enable = true;				// 환불은행명
						
			// 2013.05.22. 이건민 - 권기철 과장님 추가요청 사항 (정비보수 번호가 있다면 공사완료일자 비활성화)
			if(ds_MoveDocuV.GetColumn(0, "MAINTER_NUM") == '' || ds_MoveDocuV.GetColumn(0, "MAINTER_NUM") == null)
			{
				Calendar7.Enable = true;			// 공사완료일자
			}
			else
			{
				Calendar7.Enable = false;			// 공사완료일자
			}
			
			edt_WORK_FIRM_ADJ_COST.Enable = true;	// 정산금액
			Calendar9.Enable = true;				// 정산일자
			edt_REFUND_AMT.Enable = false;			// 환불금액
		}
	}
}

