//------------------------------------------------------------------------------- 
//   함수명: Grd_MoveAdj_OnExpandEdit()
//   설명  :  도관운영비 산출 팝업
//   arguments
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function Button2_OnClick(obj)
{
	var args  = '';
	
		args  = "WORK_NUM="         + ds_MoveAdj.GetColumn(ds_MoveAdj.row, "WORK_NUM")+"%$";
		args += " MOVE_REPAIR_NUM=" + ds_MoveDocu.GetColumn(ds_MoveDocu.row, "MOVE_REPAIR_NUM")+"%$";    
		args += " PRESS_FLAG="      + quote(ds_MoveDocuV.GetColumn(0, "PRESS_FLAG"   ));
		args += " PRESS_FLAG2="     + quote(ds_MoveDocuV.GetColumn(0, "PRESS_FLAG2"  ));
		args += " PIPE_DIAM_CD="    + quote(ds_MoveDocuV.GetColumn(0, "PIPE_DIAM_CD" ));
		args += " PIPE_DIAM_CD2="   + quote(ds_MoveDocuV.GetColumn(0, "PIPE_DIAM_CD2"));
		args += " LENG="   		    + quote(ds_MoveDocuV.GetColumn(0, "LENG"         ));
		args += " LENG2="   		+ quote(ds_MoveDocuV.GetColumn(0, "LENG2"        ));		
		args += " PIPE_OPER_PRICE_YEAR=" 
									+ quote(ds_MoveDocuV.GetColumn(0, "PIPE_OPER_PRICE_YEAR"));
	
	
	var arrList = gfn_Dialog("D10","D1702010010U",args,705,552,true,-1,-1);	
	
	ds_MoveDocuV.SetColumn(0, "PIPE_OPER_PRICE_YEAR", arrList[1]);
}


function cal_RefundAmtPayYmd_OnChanged(obj,strOldText,strNewText)
{
	if (cal_RefundAmtPayYmd.Value == "" || cal_RefundAmtPayYmd.Value == null) {
        return false;
        
    } 
    
    if (cal_RefundAmtPayYmd.Value < Calendar9.Value) {
    
		alert("환불일자는 정산일자보다 이전이 될 수 없습니다. ");
		
		cal_RefundAmtPayYmd.SetFocus();
		cal_RefundAmtPayYmd.value = "";
		return false;
	}
}

// 환불 건 발생경우와 입금건 발생여부
function edt_REFUND_AMT_OnChanged(obj,strText)
{	
	// 음수
	if(edt_REFUND_AMT.Value < 0){
		Static53.Text               = '환불지급금액(원)';
		Static51.Text               = '환불금지급일자';		
		//cal_RefundAmtPayYmd.Visible = true;
		// if(ds_MoveDocuV.GetColumn(0, "PROC_STS_CD") != '60'){
			// cal_RefundAmtPayYmd.Enable  = true;
		// }
		edt_REFUND_AMT.Visible              = true;
		
		cal_RefundAmtPayYmd.Enable = true;		// 환불지급일자
		edt_PayAccountNum.Enable = true;		// 환불계좌번호
		Button4.Enable = true;					// 환불계좌번호 버튼
		cob_BnkCd.Enable = true;				// 환불은행명
				
		// 2013.05.21. 이건민 - 권기철 과장님 추가요청		
		if(ds_MoveDocuV.GetColumn(0, "PROC_STS_CD") == '60')
		{
			if( !gfn_IsNull( Calendar0.Value) ) // 전표번호가 있으면
			{
				Button104.Enable = false;				// 지불계좌 추가
				Button6.Enable = false;					// 전표등록 버튼
				btn_billDel.Enable = true;				// 전표취소 버튼			
				
				
				cal_RefundAmtPayYmd.Enable = false;		// 환불지급일자
				edt_PayAccountNum.Enable = false;		// 환불계좌번호
				Button4.Enable = false;					// 환불계좌번호 버튼
				cob_BnkCd.Enable = false;				// 환불은행명		
				
				Calendar7.Enable = false;				// 공사완료일자
				edt_WORK_FIRM_ADJ_COST.Enable = false;	// 정산금액
				Calendar9.Enable = false;				// 정산일자
				edt_REFUND_AMT.Enable = false;			// 환불금액			
				
			}
			else
			{
				Button104.Enable = true;				// 지불계좌 추가
				Button6.Enable = true;					// 전표등록 버튼
				btn_billDel.Enable = false;				// 전표취소 버튼
				
				cal_RefundAmtPayYmd.Enable = true;		// 환불지급일자
				edt_PayAccountNum.Enable = true;		// 환불계좌번호
				Button4.Enable = true;					// 환불계좌번호 버튼
				cob_BnkCd.Enable = true;				// 환불은행명
				
				
				
				if(ds_MoveDocuV.GetColumn(0, "MAINTER_NUM") == '' || ds_MoveDocuV.GetColumn(0, "MAINTER_NUM") == null)
				{
					Calendar7.Enable = true;			// 공사완료일자
				}
				else
				{
					Calendar7.Enable = false;			// 공사완료일자
				}
				
				edt_WORK_FIRM_ADJ_COST.Enable = true;	// 정산금액
				Calendar9.Enable = true;				// 정산일자
				edt_REFUND_AMT.Enable = false;			// 환불금액
			}
		}
	}
	
	// 2013.05.15. 이건민 - 권기철 과장님 요청으로 주석처리
	/*
	// 양수
	if(edt_REFUND_AMT.Value > 0){
		Static53.Text     		    = '추징요청금액(원)';
		Static51.Text     			= '추징금지급일자';
		cal_RefundAmtPayYmd.Visible = true;
		// if(ds_MoveDocuV.GetColumn(0, "PROC_STS_CD") != '60'){
			// cal_RefundAmtPayYmd.Enable  = true;
		// }
		edt_REFUND_AMT.Visible              = true;
	}
	*/
	
	// 0
	if(edt_REFUND_AMT.Value == 0){
		Static53.Text     = '환불지급금액(원)';
		Static51.Text     = '환불금지급일자';
		cal_RefundAmtPayYmd.Visible = true;
		edt_REFUND_AMT.Visible              = true;
		
		cal_RefundAmtPayYmd.Enable = false;		// 환불지급일자
		edt_PayAccountNum.Enable = false;		// 환불계좌번호
		Button4.Enable = false;					// 환불계좌번호 버튼
		cob_BnkCd.Enable = false;				// 환불은행명
		
		// 2013.05.16 이건민 - 권기철 과장님 요청사항 (환불금액이 없다면 환불정보 공백처리)
		cal_RefundAmtPayYmd.Value = '';
		//Edit21.Text = '';
		//edt_DEAL_CD.Text = '';
		edt_PayAccountNum.Text = '';
		cob_BnkCd.Text = '';
		
		
	}
}

//-------------------------------------------------------------------------------
// 함수명: Button0_OnClick()
//   설명: 첨부파일 popup화면 로딩
