function Button116_OnClick(obj)
{
	if(ds_MoveDocuV.GetRowCount() <= 0 ){
		alert(gfn_GetMessage("M000040","%AAA%","이설보수내역을 "));
		return;
	}
		
	if(obj == Button1){
	
		var arg  = "fa_Url=handle.do?ServiceName=D1_MoveingRepairSpecMgr-service"
		         + "&MOVE_REPAIR_NUM="   + ds_MoveDocu.GetColumn(ds_MoveDocu.row, "MOVE_REPAIR_NUM") 
		         + "&ATTACH_FILE_FLAG="  + "10" 
		         + "&ATTACH_GROUP_FLAG=" + "10"
		         + " fa_Part=D10"
				 ;
		var arrList = gfn_Dialog("D10","D1101010018U",arg,450,280,true,-1,-1);
		
		Edit6.text = "";
		
		for(var i=0; i<arrList.length; i++)
		{
			Edit6.text += arrList[i];
			if(arrList.length-1 != i) Edit6.text += "/";
		}	
	}
	
	if(obj == Button23){
	
		var arg  = "fa_Url=handle.do?ServiceName=D1_MoveingRepairSpecMgr-service"
		         + "&MOVE_REPAIR_NUM="   + ds_MoveDocu.GetColumn(ds_MoveDocu.row, "MOVE_REPAIR_NUM") 
		         + "&ATTACH_FILE_FLAG="  + "20" 
		         + "&ATTACH_GROUP_FLAG=" + "20"
		         + " fa_Part=D10"
				 ;
		var arrList = gfn_Dialog("D10","D1101010018U",arg,450,280,true,-1,-1);
		
		edtFileList.text = "";
		
		for(var i=0; i<arrList.length; i++)
		{
			edtFileList.text += arrList[i];
			if(arrList.length-1 != i) edtFileList.text += "/";
		}
	}
	
	if(obj == Button3){
	
		var arg  = "fa_Url=handle.do?ServiceName=D1_MoveingRepairSpecMgr-service"
		         + "&MOVE_REPAIR_NUM="   + ds_MoveDocu.GetColumn(ds_MoveDocu.row, "MOVE_REPAIR_NUM") 
		         + "&ATTACH_FILE_FLAG="  + "30" 
		         + "&ATTACH_GROUP_FLAG=" + "30"
		         + " fa_Part=D10"
				 ;
		var arrList = gfn_Dialog("D10","D1101010018U",arg,450,280,true,-1,-1);
		
		Edit1.text = "";
		
		for(var i=0; i<arrList.length; i++)
		{
			Edit1.text += arrList[i];
			if(arrList.length-1 != i) Edit1.text += "/";
		}
	}
}


//-------------------------------------------------------------------------------
// 함수명: fn_d1_FileSearch()
//   설명: 첨부파일 조회
// arguments   
//          obj: 사용된 객체를 전달 한다.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function fn_d1_FileSearch()
{
	// 접수 첨부
	var strSvcID = "";
	var strPart  = "D10";
	var strURL   = "handle.do?ServiceName=D1_MoveingRepairSpecMgr-service&findFile=1"
	             + "&MOVE_REPAIR_NUM="    + ds_MoveDocuV.GetColumn(0,"MOVE_REPAIR_NUM" )
		         + "&ATTACH_GROUP_FLAG="  + "10"
	             ;

	var strInDatasets   = "";
	var strOutDatasets  = "";
	    strOutDatasets += "ds_FileList=ds_FileList";
	var strArgument  = "";
		
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );
	
	// 청구 첨부
	var strSvcID = "";
	var strPart  = "D10";
	var strURL   = "handle.do?ServiceName=D1_MoveingRepairSpecMgr-service&findFile=1"
	             + "&MOVE_REPAIR_NUM="    + ds_MoveDocuV.GetColumn(0,"MOVE_REPAIR_NUM" )
		         + "&ATTACH_GROUP_FLAG=" + "20"
	             ;

	var strInDatasets   = "";
	var strOutDatasets  = "";
	    strOutDatasets += "ds_FileList1=ds_FileList";
	var strArgument  = "";
		
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );	
			   	   
	// 정산 첨부
	var strSvcID = "";
	var strPart  = "D10";
	var strURL   = "handle.do?ServiceName=D1_MoveingRepairSpecMgr-service&findFile=1"
	             + "&MOVE_REPAIR_NUM="    + ds_MoveDocuV.GetColumn(0,"MOVE_REPAIR_NUM" )
		         + "&ATTACH_GROUP_FLAG=" + "30"
	             ;

	var strInDatasets   = "";
	var strOutDatasets  = "";
	    strOutDatasets += "ds_FileList2=ds_FileList";
	var strArgument     = "";
		
	gfn_SyncCall( 
					 strSvcID
				   , strPart
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );	
	
	if(ErrorCode < 0)
	{
		return;
	}
	
	Edit6      .Text = "";
	edtFileList.Text = "";
	Edit1      .Text = "";
	
	// 접수 첨부파일
	for(var i=0; i<ds_FileList.RowCount(); i++)
	{
		Edit6.text += ds_FileList.GetColumn(i,"ORG_FILE_NAME");
		if(ds_FileList.RowCount()-1 != i) Edit6.text += "/";
	}
	
	// 청구 첨부파일
	for(var i=0; i<ds_FileList1.RowCount(); i++)
	{
		edtFileList.text += ds_FileList1.GetColumn(i,"ORG_FILE_NAME");
		if(edtFileList.RowCount()-1 != i) edtFileList.text += "/";
	}
	
	// 접수 첨부파일
	for(var i=0; i<ds_FileList2.RowCount(); i++)
	{
		Edit1.text += ds_FileList2.GetColumn(i,"ORG_FILE_NAME");
		if(ds_FileList2.RowCount()-1 != i) Edit1.text += "/";
	}	
}

// 예수금 대체전표
function Button10_OnClick(obj)
{
	if(edit0.Text != edit3.Text && (edit0.Text != '0' || edit3.Text == '0')){
		alert("청구금액과 입금금액이 동일한 경우에만 전표가 발행됩니다. ");
		return;
	}
	
	// 품의서 번호가 없을경우 전표발행할 수 없다
	if((Edit5.Text == '' || Edit5.Text == null) && edit0.Text == '0' && edit3.Text == '0'){
		alert("전표 발행 가능한 데이터가 아닙니다. "); 
		return;
	}
}

// 입금일자가 NULL이 아닐 경에는 품의관련 정보와 금액부분은 비활성
function Calendar1_OnChanged(obj,strOldText,strNewText)
{
	if(Calendar1.Value == null || Calendar1.Value == ''){
		edit0.Enable   	   		= true;
		if(cob_MoveRepairType.Value != '30'){
			Edit5.Enable   	   	= true;
		}else{
			Edit5.Enable   	   	= false;
		}
		Edit7.Enable   	   		= true;
		Edit8.Enable   	   		= true;
		Edit9.Enable   	   		= true;
		Edit13.Enable      		= true;
		Calendar4.Enable   		= true;
		Edit10.Enable     		= true;
		edt_DEAL_CD.Enable		= true;
		cal_ReceiptYmd.Enable   = true;
		Button2.Enable          = true;
		Edit22.Enable		    = true;
		return;
	}	
		edit0.Enable 	        = false;
		Edit5.Enable 	        = false;
		Edit7.Enable 			= false;
		Edit8.Enable 			= false;
		Edit9.Enable   			= false;
		Edit13.Enable   		= false;
		Calendar4.Enable   		= false;
		Edit10.Enable   		= false;
		edt_DEAL_CD.Enable		= false;
		cal_ReceiptYmd.Enable   = false;
		Button2.Enable          = false;
		Edit22.Enable           = false;
	
	if (Calendar1.Value == "" || Calendar1.Value == null) {
        return true; 
    } else if (cal_ReceiptYmd.Value > Calendar1.Value) {
    
		alert("입금일자는 청구일자보다 이전이 될 수 없습니다. ");
		
		Calendar1.SetFocus();
		Calendar1.value = "";
		return false;
	}
}

