// 함수명: btn_Bill_OnClick()
//   설명: 전표 처리
// arguments   
//          obj: 사용된 객체를 전달 한다.
//   return 내용: 없음
//------------------------------------------------------------------------------- 
function btn_bill_OnClick(obj)
{
	/*
	if(ds_MoveDocuV.GetColumn(0, "PROC_STS_CD") == '50'){ // 정산 						
	
		if(edt_REFUND_AMT.Value < 0){
			alert("환불금이 존재합니다. \n환불정보(환불금 지급일자/환불거래처정보)를 등록 후 전표 발행 하십시오. ");
			return false;
		}	
	}
	
	if(length(trim(ds_MoveDocuV.GetColumn(0, "DEAL_NM"))) == 0) // 거래처이름이 없을때 						
	{
		alert("거래처가 없습니다. 거래처를 확인해 주십시요");
		return false;
	}
	*/
	if(ds_MoveDocuV.GetColumn(0, "PROC_STS_CD") == '50'){ // 정산 						
	
		if(edt_REFUND_AMT.Value < 0){
			alert("환불 정보를 저장 후 전표발행 하십시오.");
			return false;
		}	
	}
	
	if(length(trim(ds_MoveDocuV.GetColumn(0, "DEAL_NM"))) == 0) // 거래처이름이 없을때 						
	{
		alert("거래처가 없습니다. 거래처를 확인해 주십시요");
		return false;
	}

	//-------------------------------------------------------------------------------
	// 함수명   : btn_bill_OnClick()
	// 설명     : 저장 트렌젝션을 처리 fn_E4_CallBack으로 결과를 넘김.
	// return 내용 : 없음
	//-------------------------------------------------------------------------------
	// 2009-12-21 마감체크 추가 
	var strComId = gfn_GetGlobalValue("G_COM_ID");
	var strBizplc = gfn_GetGlobalValue("G_BIZPLACE");
	var strClsYmd = "";
	var massageYn = "Y";
	
	var str_BillType = ds_MoveDocuV.GetColumn(0, "PROC_STS_CD");
	
	if(str_BillType == '50'){ // 정산 
		strClsYmd = Calendar9.Value;
	}else if(str_BillType == '60'){ // 환불
		strClsYmd = cal_RefundAmtPayYmd.Value;
	}
	
	var Result  = gfn_E5_checkClsYn( strComId , strBizplc , strClsYmd , massageYn);
	
	if(Result == "1")
	{
		return;
	}	
	
	if(!fn_D1_PreBill()){
		return false;
	}

	var strSvcID = "btn_bill_OnClick";
	var strPart  = "E50";
	var strURL   = "handle.do?ServiceName=E5_AutoBillCreateMgr-service&insert=1";
	var strInDatasets   = "ds_Multi=ds_BillDetail:u ds_Single=ds_BillHeader:u";
	var strOutDatasets  = "ds_BillDetail2=ds_Multi ds_BillHeader=ds_Single";
	var strArgument     = "";
	var strCallbackFunc = "fn_D1_CallBack";
	
	gfn_AsyncCall(strSvcID,
	              strPart,
	              strURL,
	              strInDatasets,
	              strOutDatasets,
	              strArgument,
	              strCallbackFunc
	);	

}
//---------------------------------------------------------
//	
//---------------------------------------------------------
function fn_D1_PreBill()
{   

	var str_BillType = ds_MoveDocuV.GetColumn(0, "PROC_STS_CD");

	if(str_BillType == '50')
	{ 
		// 정산 						
		if( gfn_IsNull( Calendar9.Value ))
		{
			alert("정산금 지급일자를 지정하세요. ");
			return false;
		}	
	}
	else if(str_BillType == '60')
	{ 
		// 환불
		if( gfn_IsNull(cal_RefundAmtPayYmd.Value) )
		{
			alert("환불금 지급일자를 지정하세요. ");
			return false;
		}
		
		// if(edt_REFUND_AMT.Value > 0)
		// {
			// alert("환불금액이 발생한 건만 전표 발행 가능합니다. ");
			// return false;
		// }	
		
		if(edt_REFUND_AMT.Value == '0')
		{
			alert("환불금액이 존재하지 않습니다. ");
			return false;
		}
		
		if( gfn_IsNull(edt_PayAccountNum.Value) )
		{
			alert("환불계좌번호 정보를 선택하세요. 존재하지 않을 경우 지급계좌를 추가 후 이용하세요.");
			return false;
		}
	}
			
	if(!gfn_IsNull(ds_MoveDocuV.GetColumn(0,"BILL_YMD_2")))
	{
		alert("이미 전표생성된 자료입니다");
		return false;	
	}
	
	fn_D1_MakeBillData();	
	
	return true;
}
//---------------------------------------------------------
//	
//---------------------------------------------------------
function fn_D1_MakeBillData()
{

	var comId		= gfn_GetGlobalValue("G_COM_ID");
	var bizplcCd 	= gfn_GetGlobalValue("G_BIZPLACE");
	var billYmd  	= "";
	
	var str_BillType = ds_MoveDocuV.GetColumn(0, "PROC_STS_CD");

	//2011.07.13 이상목 권기철과자요청 정산,환불모두 정산일자 셋팅
	billYmd = Calendar9.Value;//정산일자
	/*
	if(str_BillType == '50'){ // 정산 
		billYmd = Calendar9.Value;
	}else if(str_BillType == '60'){ // 환불
		billYmd = cal_RefundAmtPayYmd.Value;
	}
	*/
	var billSeq  	= fn_D1_Tr_getBillSeq (comId,bizplcCd,billYmd);

	fn_D1_MakeBillDetail(comId,bizplcCd,billYmd,billSeq);
    fn_D1_MakeBillHeader(comId,bizplcCd,billYmd,billSeq);
}

function fn_D1_Tr_getBillSeq(ComId,bizplcCd,billYmd)
{
	//-------------------------------------------------------------------------------
	// 함수명   : fn_D1_getBillSeq(ComId,bizplcCd,billYmd)
	// 설명     : 전표번호를 리턴.
	// return 내용 : 4자리 문자형태의 전표번호 
	//-------------------------------------------------------------------------------
		
	var strSvcID = "fn_D1_Tr_find";
	var strPart  = "E50";
	var strURL   = "handle.do?ServiceName=E5_NonBillCreateMgr-service";
	var strInDatasets   = "";
	var strOutDatasets  = "ds_Com=ds_Multi";
	var strArgument     = "seqSelect=1";
        strArgument    += " COM_ID="	+ quote(ComId);			//회사구분
	    strArgument    += " BIZPLC_CD="	+ quote(bizplcCd);		//사업장코드
	    strArgument    += " NUMB_YMD=" 	+ quote(billYmd);		//전표일자
	    strArgument    += " NUMB_FLAG=" + "10";			
	    
	gfn_SyncCall(strSvcID,
	              strPart,
	              strURL,
	              strInDatasets,
	              strOutDatasets,
	              strArgument);
	              
	if (  ErrorCode<0 )
	{
		alert(ErrorCode);
		return false;
		
	}else{
		if(ds_Com.RowCount()==0)
		{
			return "0001";	
		}
		else
		{
			return ds_Com.GetColumn(0,"BILL_SEQ");
		}
	}
}
//---------------------------------------------------------
//	
//---------------------------------------------------------
function fn_D1_MakeBillDetail(strComId,strBizplcCd,strBillYmd,strBillSeq)
{
	
	var str_BillType = ds_MoveDocuV.GetColumn(0, "PROC_STS_CD");
	
	ds_BillDetail.ClearData();
	
	var EVIDENCE_FLAG    = '';	// 증빙구분
	var BUY_SALES_VAT_CD = '';

	var str_BranchCd = ds_MoveDocuV.GetColumn(0, "BRANCH_CD"       );
	var str_MoveType = ds_MoveDocuV.GetColumn(0, "MOVE_REPAIR_TYPE");

	var str_ACNT_TITACNT_CD_CR  = "";
	var str_ACNT_TITACNT_CD_DR1 = ""; // V5119441 잡이익(이설비) / V5119432 잡이익(보수비)		
	var str_ACNT_TITACNT_CD_DR2 = "";
	var str_ACNT_TITACNT_CD_DR3 = ds_ACNT_CD.GetColumn(0, "ACNT_TITACNT_CD_DR7"); // J33534 (예수금 부가세)			
	var str_ACNT_TITACNT_CD_DR4 = ds_ACNT_CD.GetColumn(0, "ACNT_TITACNT_CD_DR8"); // J33117 (미지급금 일반)
	
	//2011.06.30 이상목 권기철과장님유청 이설에서 이설과 잔존가로 수정
	if(str_MoveType == '10' || str_MoveType == '30')
	{
		str_ACNT_TITACNT_CD_DR1 = ds_ACNT_CD.GetColumn(0, "ACNT_TITACNT_CD_DR1");
	}else{
		str_ACNT_TITACNT_CD_DR1 = ds_ACNT_CD.GetColumn(0, "ACNT_TITACNT_CD_DR2");
	}		

	if(str_BranchCd == '100664'){// 감북
	
		str_ACNT_TITACNT_CD_CR  = ds_ACNT_CD.GetColumn(0, "ACNT_TITACNT_CD_CR1"); // J3359877 (예수금 - 강북지사)
		str_ACNT_TITACNT_CD_DR2 = ds_ACNT_CD.GetColumn(0, "ACNT_TITACNT_CD_DR1"); // J3359877 (예수금 - 강북지사)
	
	}else if(str_BranchCd == '100584'){// 서부
		
		str_ACNT_TITACNT_CD_CR  = ds_ACNT_CD.GetColumn(0, "ACNT_TITACNT_CD_CR2"); // J3359833 (예수금 - 서부지사)
		str_ACNT_TITACNT_CD_DR2 = ds_ACNT_CD.GetColumn(0, "ACNT_TITACNT_CD_DR2"); // J3359833 (예수금 - 서부지사)
	
	}else if(str_BranchCd == '100591'){// 남부
	
		str_ACNT_TITACNT_CD_CR  = ds_ACNT_CD.GetColumn(0, "ACNT_TITACNT_CD_CR3"); // J3359844 (예수금 - 남부지사)
		str_ACNT_TITACNT_CD_DR2 = ds_ACNT_CD.GetColumn(0, "ACNT_TITACNT_CD_DR3"); // J3359844 (예수금 - 남부지사)

	}else if(str_BranchCd == '101500'){// 경기
	
		str_ACNT_TITACNT_CD_CR  = ds_ACNT_CD.GetColumn(0, "ACNT_TITACNT_CD_CR4"); // J3359811 (예수금 - 경기지사)
		str_ACNT_TITACNT_CD_DR2 = ds_ACNT_CD.GetColumn(0, "ACNT_TITACNT_CD_DR4"); // J3359811 (예수금 - 경기지사)
	}
	
	var str_AMT = edt_WORK_FIRM_ADJ_COST.Value;
	
	//return; 20110524 이상목 수정

	//-------------------------------------------------------------------------------
	//공통(차변) -- 이설비-원인자이설비(지사별)
	gfn_E5_DatasetAddRow(ds_BillDetail);
	
	ds_BillDetail.SetColumn(ds_BillDetail.row,"COM_ID"           ,strComId   );           										       //회사구분               
	ds_BillDetail.SetColumn(ds_BillDetail.row,"BIZPLC_CD"        ,strBizplcCd);	         										       //사업장코드             
	ds_BillDetail.SetColumn(ds_BillDetail.row,"BILL_YMD"         ,strBillYmd );            										       //전표일자               
	ds_BillDetail.SetColumn(ds_BillDetail.row,"BILL_SEQ"         ,strBillSeq );            										       //전표SEQ                
	ds_BillDetail.SetColumn(ds_BillDetail.row,"BILL_LINE"        ,"0001"     );			
	ds_BillDetail.SetColumn(ds_BillDetail.row,"TITACNT_CD"       ,str_ACNT_TITACNT_CD_CR);											   //계정코드
	ds_BillDetail.SetColumn(ds_BillDetail.row,"DRCR_FLAG"        ,"1"        );													       //차변
	ds_BillDetail.SetColumn(ds_BillDetail.row, "APPLY"           , substr(ds_MoveDocuV.GetColumn(0, "BRANCH_NM"), 0, 2) + ', 원인자 이설비용 - '
																   + ds_MoveDocuV.GetColumn(0, "MOVE_REPAIR_WORK_NM"));		   //적요
	ds_BillDetail.SetColumn(ds_BillDetail.row,"FIRM_DEPT_CD"     ,gfn_GetGlobalValue("G_DEPT_CODE"));          
	ds_BillDetail.SetColumn(ds_BillDetail.row,"PASS_DEPT_CD"     ,gfn_GetGlobalValue("G_DEPT_CODE"));    			
	ds_BillDetail.SetColumn(ds_BillDetail.row,"NO_APPRO_BILL_AMT",str_AMT					       );	     						   //미승인전표금액(청구금액)
	ds_BillDetail.SetColumn(ds_BillDetail.row,"VAT_BIZPLC"       ,"" 			                   );				                   //부가세사업장
	//공통(차변)
		
	//계정별관리항목에서 가져오는 선택값(차변) STSRT
	ds_BillDetail.SetColumn(ds_BillDetail.row,"DEAL_FLAG"      ,  ds_MoveDocuV.GetColumn(0, "DEAL_FLAG")); // 100 거래처구분
	ds_BillDetail.SetColumn(ds_BillDetail.row,"DEAL_CD"        ,  ds_MoveDocuV.GetColumn(0, "DEAL_CD"  )); // 101 거래처
	//계정별관리항목에서 가져오는 선택값(차변) END
	//-------------------------------------------------------------------------------
	
	//-------------------------------------------------------------------------------											
	// 공통(대변) -- 잡이익 (이설비 / 보수비)
	
	gfn_E5_DatasetAddRow(ds_BillDetail);
				
	ds_BillDetail.SetColumn(ds_BillDetail.row, "COM_ID"      , strComId               ); //회사구분               
	ds_BillDetail.SetColumn(ds_BillDetail.row, "BIZPLC_CD"   , strBizplcCd            ); //사업장코드             
	ds_BillDetail.SetColumn(ds_BillDetail.row, "BILL_YMD"    , strBillYmd             ); //전표일자               
	ds_BillDetail.SetColumn(ds_BillDetail.row, "BILL_SEQ"    , strBillSeq             ); //전표SEQ                
	ds_BillDetail.SetColumn(ds_BillDetail.row, "BILL_LINE"   , "0002"                 ); //전표lINE
	ds_BillDetail.SetColumn(ds_BillDetail.row, "DRCR_FLAG"   , "2"                    ); //대변으로 세팅	
	ds_BillDetail.SetColumn(ds_BillDetail.row, "TITACNT_CD"  , str_ACNT_TITACNT_CD_DR1); //계정코드
	ds_BillDetail.SetColumn(ds_BillDetail.row, "APPLY"       , substr(ds_MoveDocuV.GetColumn(0, "BRANCH_NM"), 0, 2) + ', 원인자 이설비용 - '
																  + ds_MoveDocuV.GetColumn(0, "MOVE_REPAIR_WORK_NM")); //적요																																		
	ds_BillDetail.SetColumn(ds_BillDetail.row,"FIRM_DEPT_CD" ,gfn_GetGlobalValue("G_DEPT_CODE"));         
	ds_BillDetail.SetColumn(ds_BillDetail.row,"PASS_DEPT_CD" ,gfn_GetGlobalValue("G_DEPT_CODE"));
	ds_BillDetail.SetColumn(ds_BillDetail.row,"VAT_BIZPLC"   ,""		                       );	
	ds_BillDetail.SetColumn(ds_BillDetail.row,"NO_APPRO_BILL_AMT", 	str_AMT     			   );
	//공통(대변)
	
	//계정별관리항목에서 가져오는 선택값(대변: 잡이익) START
	 ds_BillDetail.SetColumn(ds_BillDetail.row,"DEAL_FLAG"  ,  ds_MoveDocuV.GetColumn(0, "DEAL_FLAG")); // 100 거래처구분
	 ds_BillDetail.SetColumn(ds_BillDetail.row,"DEAL_CD"    ,  ds_MoveDocuV.GetColumn(0, "DEAL_CD"  )); // 101 거래처 
	//계정별관리항목에서 가져오는 선택값(대변: 잡이익) END
	//-------------------------------------------------------------------------------	
	
	if(str_BillType == '60')  // 환불
	{
	
		//-------------------------------------------------------------------------------											
		// 공통(대변) -- 예수금 원인자이설(지사별)
		
		var str_Original_Price  = parseFloat(edt_REFUND_AMT.Value);                                   // 환불금액 (예수금 공사비)
		
		//부가세가 일반일때만 전표에 부가세가 찍히게 영세나 면세일때는 원래 부가세가 찍히도록 2011.12.22
		var str_Vat_Price = 0;
		if(ds_MoveDocuV.GetColumn(0,"EVIDENCE_YN") == "10" )
		{
			str_Vat_Price       = truncate( eval(str_Original_Price) * 0.1);           // 환불금액 부가세 (예수금 부가세)
		}else{
			str_Vat_Price       = edit0.Value;           // 환불금액 부가세 (예수금 부가세)
		}
		var str_Price           = (eval(str_Original_Price) + eval(str_Vat_Price)) * (-1) ;
		
		//trace("str_Original_Price:"+str_Original_Price);
		//trace("str_Vat_Price:"+str_Vat_Price);
		//trace("str_Price:"+str_Price);
		
		gfn_E5_DatasetAddRow(ds_BillDetail);			
		ds_BillDetail.SetColumn(ds_BillDetail.row, "COM_ID"      , strComId               ); //회사구분               
		ds_BillDetail.SetColumn(ds_BillDetail.row, "BIZPLC_CD"   , strBizplcCd            ); //사업장코드             
		ds_BillDetail.SetColumn(ds_BillDetail.row, "BILL_YMD"    , strBillYmd             ); //전표일자               
		ds_BillDetail.SetColumn(ds_BillDetail.row, "BILL_SEQ"    , strBillSeq             ); //전표SEQ                
		ds_BillDetail.SetColumn(ds_BillDetail.row, "BILL_LINE"   , "0003"                 ); //전표lINE
		ds_BillDetail.SetColumn(ds_BillDetail.row, "DRCR_FLAG"   , "2"                    ); //대변으로 세팅
		//2011.07.14 김영호과장요청 환불일때 계정코드 바꿔주기 
		ds_BillDetail.SetColumn(ds_BillDetail.row, "TITACNT_CD"  , str_ACNT_TITACNT_CD_CR); //계정코드		str_ACNT_TITACNT_CD_DR2
		ds_BillDetail.SetColumn(ds_BillDetail.row, "APPLY"       , substr(ds_MoveDocuV.GetColumn(0, "BRANCH_NM"), 0, 2) + ', 원인자 이설비용 - '
																	  + ds_MoveDocuV.GetColumn(0, "MOVE_REPAIR_WORK_NM") + ' 환불' ); //적요																																		
		ds_BillDetail.SetColumn(ds_BillDetail.row,"FIRM_DEPT_CD" ,gfn_GetGlobalValue("G_DEPT_CODE"));         
		ds_BillDetail.SetColumn(ds_BillDetail.row,"PASS_DEPT_CD" ,gfn_GetGlobalValue("G_DEPT_CODE"));
		ds_BillDetail.SetColumn(ds_BillDetail.row,"VAT_BIZPLC"   ,""		                       );	
		ds_BillDetail.SetColumn(ds_BillDetail.row,"NO_APPRO_BILL_AMT", str_Original_Price);
		//공통(대변)
		
		//계정별관리항목에서 가져오는 선택값(대변: 잡이익) START
		ds_BillDetail.SetColumn(ds_BillDetail.row,"DEAL_FLAG"  ,  ds_MoveDocuV.GetColumn(0, "DEAL_FLAG")); // 100 거래처구분
		ds_BillDetail.SetColumn(ds_BillDetail.row,"DEAL_CD"    ,  ds_MoveDocuV.GetColumn(0, "DEAL_CD"  )); // 101 거래처		
		//계정별관리항목에서 가져오는 선택값(대변: 잡이익) END
		//-------------------------------------------------------------------------------	
		
		//-------------------------------------------------------------------------------											
		// 공통(대변) -- 예수금 부가세 : J33534
		gfn_E5_DatasetAddRow(ds_BillDetail);
					
		ds_BillDetail.SetColumn(ds_BillDetail.row, "COM_ID"    , strComId               );	 //회사구분               
		ds_BillDetail.SetColumn(ds_BillDetail.row, "BIZPLC_CD" , strBizplcCd            );	 //사업장코드             
		ds_BillDetail.SetColumn(ds_BillDetail.row, "BILL_YMD"  , strBillYmd             );    //전표일자               
		ds_BillDetail.SetColumn(ds_BillDetail.row, "BILL_SEQ"  , strBillSeq             );    //전표SEQ                
		ds_BillDetail.SetColumn(ds_BillDetail.row, "BILL_LINE" , "0004"                 );	 //전표lINE
		ds_BillDetail.SetColumn(ds_BillDetail.row, "DRCR_FLAG" , "2"                    );	 //대변으로 세팅
		ds_BillDetail.SetColumn(ds_BillDetail.row, "TITACNT_CD", str_ACNT_TITACNT_CD_DR3);	 //계정코드			
		ds_BillDetail.SetColumn(ds_BillDetail.row, "APPLY"     , substr(ds_MoveDocuV.GetColumn(0, "BRANCH_NM"), 0, 2) + ', 원인자 이설비용 - '
																	  + ds_MoveDocuV.GetColumn(0, "MOVE_REPAIR_WORK_NM") + ' 환불' ); //적요																																		
		ds_BillDetail.SetColumn(ds_BillDetail.row,"FIRM_DEPT_CD" ,gfn_GetGlobalValue("G_DEPT_CODE"));         
		ds_BillDetail.SetColumn(ds_BillDetail.row,"PASS_DEPT_CD" ,gfn_GetGlobalValue("G_DEPT_CODE"));
		ds_BillDetail.SetColumn(ds_BillDetail.row,"VAT_BIZPLC"   ,"1098131605"                     );	
		ds_BillDetail.SetColumn(ds_BillDetail.row,"NO_APPRO_BILL_AMT", str_Vat_Price	  );
		//공통(대변)
		
		//계정별관리항목에서 가져오는 선택값(대변: 예수금 부가세) START
		ds_BillDetail.SetColumn(ds_BillDetail.row,"DEAL_FLAG"      ,  ds_MoveDocuV.GetColumn(0, "DEAL_FLAG"  )); // 100 거래처구분
		ds_BillDetail.SetColumn(ds_BillDetail.row,"DEAL_CD"        ,  ds_MoveDocuV.GetColumn(0, "DEAL_CD"    )); // 101 거래처			 
			  if(ds_MoveDocuV.GetColumn(0, "EVIDENCE_YN") == '10'){ EVIDENCE_FLAG = '01';}
		 else if(ds_MoveDocuV.GetColumn(0, "EVIDENCE_YN") == '20'){ EVIDENCE_FLAG = '09';}
		 else if(ds_MoveDocuV.GetColumn(0, "EVIDENCE_YN") == '30'){ EVIDENCE_FLAG = '02';}

			  // if(ds_MoveDocuV.GetColumn(0, "EVIDENCE_YN") == '10'){ BUY_SALES_VAT_CD = '11';}
		 // else if(ds_MoveDocuV.GetColumn(0, "EVIDENCE_YN") == '20'){ BUY_SALES_VAT_CD = '50';}
		 // else if(ds_MoveDocuV.GetColumn(0, "EVIDENCE_YN") == '30'){ BUY_SALES_VAT_CD = '12';}	
		
		ds_BillDetail.SetColumn(ds_BillDetail.row,"EVIDENCE_FLAG"      , EVIDENCE_FLAG                         ); // 증빙구분(필수)
		ds_BillDetail.SetColumn(ds_BillDetail.row,"BUY_SALES_VAT_CD"   , 11                        			   ); //
		ds_BillDetail.SetColumn(ds_BillDetail.row,"SALES_VAT_CLASS_CD" , 602                       			   ); // 매출부가세분류코드
		//ds_BillDetail.SetColumn(ds_BillDetail.row,"DEAL_YMD"         , cal_RefundAmtPayYmd.Value               ); // 거래일자(필수)	
		ds_BillDetail.SetColumn(ds_BillDetail.row,"DEAL_YMD"         , strBillYmd               			   ); // 거래일자(필수)		2011.07.29 이상목 거래코드에 전표일자로 바꾸어줌
		ds_BillDetail.SetColumn(ds_BillDetail.row,"CAPI_SUPPLY_VAL"  , str_Original_Price					   ); // 원금액/공급가액(필수)
		ds_BillDetail.SetColumn(ds_BillDetail.row,"QTY_PERS"         , "1"                                     ); // 수량/인원(필수)
		ds_BillDetail.SetColumn(ds_BillDetail.row,"UNIT_CD"		     , "400"								   ); // 단위(필수)
		
		ds_BillDetail.SetColumn(ds_BillDetail.row,"CHG_TAX_YN"		 , "Y"								       ); // 수정세금계산서 여부항목 2011.10.13 서부 김영호차장님 환불일때는 Y로 넘겨주도록 수정
		
		//계정별관리항목에서 가져오는 선택값(대변: 원인자이설비부가세) END										
		//-------------------------------------------------------------------------------				
		
		//-------------------------------------------------------------------------------											
		// 공통(대변) -- 미지급금 일반 : J33117
		gfn_E5_DatasetAddRow(ds_BillDetail);
	
		ds_BillDetail.SetColumn(ds_BillDetail.row, "COM_ID"    , strComId               );											 //회사구분               
		ds_BillDetail.SetColumn(ds_BillDetail.row, "BIZPLC_CD" , strBizplcCd            );   											 //사업장코드             
		ds_BillDetail.SetColumn(ds_BillDetail.row, "BILL_YMD"  , strBillYmd             );   											 //전표일자               
		ds_BillDetail.SetColumn(ds_BillDetail.row, "BILL_SEQ"  , strBillSeq             );   											 //전표SEQ                
		ds_BillDetail.SetColumn(ds_BillDetail.row, "BILL_LINE" , "0005"                 );						       				 //전표lINE
		ds_BillDetail.SetColumn(ds_BillDetail.row, "DRCR_FLAG" , "2"                    );					 						 //대변으로세팅
		ds_BillDetail.SetColumn(ds_BillDetail.row, "TITACNT_CD", str_ACNT_TITACNT_CD_DR4);		 		 //계정코드		
		ds_BillDetail.SetColumn(ds_BillDetail.row, "APPLY"     , substr(ds_MoveDocuV.GetColumn(0, "BRANCH_NM"), 0, 2) + ', 원인자 이설비용 - '
																		  + ds_MoveDocuV.GetColumn(0, "MOVE_REPAIR_WORK_NM") + " 환불"); //적요																																		
		ds_BillDetail.SetColumn(ds_BillDetail.row,"FIRM_DEPT_CD"     ,gfn_GetGlobalValue("G_DEPT_CODE"));         
		ds_BillDetail.SetColumn(ds_BillDetail.row,"PASS_DEPT_CD"     ,gfn_GetGlobalValue("G_DEPT_CODE"));
		ds_BillDetail.SetColumn(ds_BillDetail.row,"VAT_BIZPLC"       ,"1098131605"                     );	
	
		ds_BillDetail.SetColumn(ds_BillDetail.row,"NO_APPRO_BILL_AMT", str_Price);
		//공통(대변)
		
		//계정별관리항목에서 가져오는 선택값(대변: 미지급금) START
		 ds_BillDetail.SetColumn(ds_BillDetail.row,"DEAL_FLAG"       , ds_MoveDocuV.GetColumn(0, "DEAL_FLAG"         )); // 100 거래처구분
		 ds_BillDetail.SetColumn(ds_BillDetail.row,"DEAL_CD"         , ds_MoveDocuV.GetColumn(0, "DEAL_CD"           )); // 101 거래처		 
		 ds_BillDetail.SetColumn(ds_BillDetail.row,"BNK_BRANCH_CD"   , ds_MoveDocuV.GetColumn(0, "PAY_BNK_CD"        )); // 102 은행
		 ds_BillDetail.SetColumn(ds_BillDetail.row,"PAY_WAY"         , "80"); // 106 지불수단 : 송금
		 ds_BillDetail.SetColumn(ds_BillDetail.row,"PLAN_YMD"        , ds_MoveDocuV.GetColumn(0, "REFUND_AMT_PAY_YMD")); // 107 예정일자 : 환불일자
		 ds_BillDetail.SetColumn(ds_BillDetail.row,"PAY_ACCOUNT_NUM" , ds_MoveDocuV.GetColumn(0, "PAY_ACCOUNT_NUM"   )); // 108 지불계좌번호	 
		 ds_BillDetail.SetColumn(ds_BillDetail.row,"CAPI_SUPPLY_VAL" , ""); // 116 원금액/공급가액
		 ds_BillDetail.SetColumn(ds_BillDetail.row,"UNIT_CD"         , ""); // 121 단위
		//계정별관리항목에서 가져오는 선택값(대변: 미지급금) END
		//-------------------------------------------------------------------------------	
	}
}
//---------------------------------------------------------
//	
//---------------------------------------------------------
function fn_D1_MakeBillHeader(comId,bizplcCd,billYmd,billSeq)
{
    //헤더추가
    ds_BillHeader.ClearData();
    
	gfn_E5_DatasetAddRow(ds_BillHeader);
		
	// 공통
	ds_BillHeader.SetColumn(ds_BillHeader.row,"COM_ID"    , comId   );  //회사구분
	ds_BillHeader.SetColumn(ds_BillHeader.row,"BIZPLC_CD" , bizplcCd);  //사업장
	ds_BillHeader.SetColumn(ds_BillHeader.row,"BILL_YMD"  , billYmd );  //전표일자 
	ds_BillHeader.SetColumn(ds_BillHeader.row,"BILL_SEQ"  , billSeq );  //전표번호                        
	ds_BillHeader.SetColumn(ds_BillHeader.row,"APPRO_YN"  , "N"     );  //승인여부
	ds_BillHeader.SetColumn(ds_BillHeader.row,"BILL_WRITE_DEPT_CD" ,gfn_GetGlobalValue("G_DEPT_CODE")); 
																		//입력부서코드
	ds_BillHeader.SetColumn(ds_BillHeader.row,"BILL_KIND" ,"2");        //전표종류 3:대체	
	// 공통
	
	//변경되는부분
	ds_BillHeader.SetColumn(ds_BillHeader.row,"SYS_FLAG"      ,"D1");       //시스템구분 D1:안전공급 
	
	// 이설보수 유형 (10-이설) , (20-보수),(30-잔존가)	
	if(ds_MoveDocuV.GetColumn(0, "MOVE_REPAIR_TYPE") == '10' 
	|| ds_MoveDocuV.GetColumn(0, "MOVE_REPAIR_TYPE") == '30')
	{
		ds_BillHeader.SetColumn(ds_BillHeader.row,"BILL_FLAG" ,"403");      //전표구분   000:수전표
	}else{
		ds_BillHeader.SetColumn(ds_BillHeader.row,"BILL_FLAG" ,"412");      //전표구분   000:수전표
	}
	
	//trace("BILL_FLAG"+ds_BillHeader.GetColumn(ds_BillHeader.row,"BILL_FLAG"));
	
	
	ds_BillHeader.SetColumn(ds_BillHeader.row,"BILL_CRT_INFO" ,"D1703010010F"); //전표생성정보 D1801020010F : 전표등록프로그램
	ds_BillHeader.SetColumn(ds_BillHeader.row,"ACNT_FLAG"     ,'30');            //회계구분 3:공통         
	ds_BillHeader.SetColumn(ds_BillHeader.row,"DEL_YN"        ,"N");            //삭제구분 
	//여기까지변경되는부분

	ds_BillHeader.SetColumn(ds_BillHeader.row,"TOT_LN_NUM"  ,ds_BillDetail.rowcount);			//총행수
	ds_BillHeader.SetColumn(ds_BillHeader.row,"FINL_LN_NUM" ,ds_BillDetail.Max("BILL_LINE"));	//최종행수	

	//2011.6.2 이상목  권기처과장님 요청으로 flag값에 따라 값 더해서 차변과 대변에 넣어줌
	var cha = 0;
	var dae = 0;
	
	for(i=0; i<ds_BillDetail.rowcount; i++){
		if(ds_BillDetail.GetColumn(i,"DRCR_FLAG") == "1"){
			cha += ds_BillDetail.GetColumn(i,"NO_APPRO_BILL_AMT");
		}else if(ds_BillDetail.GetColumn(i,"DRCR_FLAG") == "2"){
			dae += ds_BillDetail.GetColumn(i,"NO_APPRO_BILL_AMT");
		}
	}

    ds_BillHeader.SetColumn(ds_BillHeader.row,"DR_SUM_AMT"  ,cha );	//차변합계금액
	ds_BillHeader.SetColumn(ds_BillHeader.row,"CR_SUM_AMT"  ,dae );  //대변합계금액   
	//ds_BillHeader.SetColumn(ds_BillHeader.row,"DR_SUM_AMT"  ,"0" );	//차변합계금액
	//ds_BillHeader.SetColumn(ds_BillHeader.row,"CR_SUM_AMT"  ,"0" );  //대변합계금액   
}

//-------------------------------------------------------------------------------
// 함수명   : btn_billDel_OnClick()
// 설명     : 전표취소//조회 트렌젝션을 처리 fn_D1_CallBack으로 결과를 넘김.
// return 내용 : 없음
//-------------------------------------------------------------------------------
function btn_billDel_OnClick(obj)
{
 
    if(!confirm("발행된 전표를를 정말로 취소하시겠습니까? "))
    {
		return false;
	}
    	
	fn_D1_billDelete();
	return true;           
}

function fn_D1_billDelete()
{

	var comId		= gfn_GetGlobalValue("G_COM_ID");
	var bizplcCd 	= gfn_GetGlobalValue("G_BIZPLACE");
    
	ds_BillDelete.ClearData();
	
	ds_BillDelete.AddColumn("COM_ID"    	   , "STRING"   , 256);
	ds_BillDelete.AddColumn("BIZPLC_CD"    	   , "STRING"   , 256);	
	ds_BillDelete.AddColumn("BILL_YMD"     	   , "STRING"   , 256);
	ds_BillDelete.AddColumn("BILL_SEQ"     	   , "STRING"   , 256);
	ds_BillDelete.AddColumn("SYS_FLAG"     	   , "STRING"   , 256);

	ds_BillDelete.AddRow();
			
	if(gfn_IsNull(trim(ds_MoveDocuV.GetColumn(0,"BILL_YMD_2"))))
	{
		alert("전표가 발행되지 않아 취소 할 수 없습니다.");
		return;
	}else if(ds_MoveDocuV.GetColumn(0,"BILL_APPRO_YN_2") == '1')
	{
		alert("전표가 승인되어 취소 할 수 없습니다");
		return;
	}else{		
		ds_BillDelete.SetColumn(ds_BillDelete.row,"COM_ID"    , comId);         							//회사구분
		ds_BillDelete.SetColumn(ds_BillDelete.row,"BIZPLC_CD" , bizplcCd);         							//사업장
		ds_BillDelete.SetColumn(ds_BillDelete.row,"BILL_YMD"  , ds_MoveDocuV.GetColumn(0,"BILL_YMD_2"));	//전표일자 
		ds_BillDelete.SetColumn(ds_BillDelete.row,"BILL_SEQ"  , ds_MoveDocuV.GetColumn(0,"BILL_SEQ_2"));	//전표번호 
		ds_BillDelete.SetColumn(ds_BillDelete.row,"SYS_FLAG"  ,"D1");   									//지급여부
	}		
	
	var strSvcID = "fn_D1_Tr_delete";
	var strPart  = "E50";
	var strURL   = "handle.do?ServiceName=E5_AutoBillCreateMgr-service&delete=1";	 
	var strInDatasets   = "ds_Single=ds_BillDelete:u ";
	var strOutDatasets  = "";
	var strArgument     = "";
	var strCallbackFunc = "fn_D1_CallBack";	
	
	gfn_AsyncCall(strSvcID,
	              strPart,
	              strURL,
	              strInDatasets,
	              strOutDatasets,
	              strArgument,
	              strCallbackFunc
	);
	
	if(ErrorCode<0)
	{
		return false;
	}
		return true;
}

//-------------------------------------------------------------------------------
// 함수명   :  fn_D1_billUpdate(value)
// 설명     : 전표변경 정보 테이블에 업데이트 
// return 내용 : 없음
//-------------------------------------------------------------------------------	
function fn_D1_billUpdate(value)
{
		
	var Flag = value;
	
	ds_Modify.ClearData();
	
	var USER_ID = gfn_GetGlobalValue("G_USER_NO")  ;            // SESSION ID
	var USER_IP = gfn_GetGlobalValue("G_IPADDRESS");            // SESSION IP
	
	ds_Modify.AddColumn("UPD_EMPID"		     );
	ds_Modify.AddColumn("UPD_IP"	         );
	ds_Modify.AddColumn("BILL_APPRO_YN_2"    );
	ds_Modify.AddColumn("BILL_YMD_2"	     );
	ds_Modify.AddColumn("BILL_SEQ_2"	     );
	ds_Modify.AddColumn("TXBILL_VAT_BIZPLC_2");
	ds_Modify.AddColumn("TXBILL_PUBL_YM_2"   );
	ds_Modify.AddColumn("TXBILL_PUBL_SEQ_2"  );
	ds_Modify.AddColumn("MOVE_REPAIR_NUM"    );
	
	ds_Modify.UpdateControl = false;
		
	ds_Modify.AddRow();
	
	ds_Modify.SetColumn(ds_Modify.row, "UPD_EMPID"      , USER_ID);
	ds_Modify.SetColumn(ds_Modify.row, "UPD_IP"         , USER_IP);
	
	if(Value == 'update'){
		ds_Modify.SetColumn(ds_Modify.row, "BILL_APPRO_YN_2", 'N' );
		ds_Modify.SetColumn(ds_Modify.row, "BILL_YMD_2"         , ds_BillDetail2.GetColumn(0, "BILL_YMD"    ));
		ds_Modify.SetColumn(ds_Modify.row, "BILL_SEQ_2"         , ds_BillDetail2.GetColumn(0, "BILL_SEQ"    ));		
		ds_Modify.SetColumn(ds_Modify.row, "TXBILL_VAT_BIZPLC_2", ds_BillDetail2.GetColumn(3, "VAT_BIZPLC"  ));
		ds_Modify.SetColumn(ds_Modify.row, "TXBILL_PUBL_YM_2"   , ds_BillDetail2.GetColumn(3, "PUBL_YM"     ));
		ds_Modify.SetColumn(ds_Modify.row, "TXBILL_PUBL_SEQ_2"  , ds_BillDetail2.GetColumn(3, "PUBL_SEQ"    ));
			
	}else{
		ds_Modify.SetColumn(ds_Modify.row, "BILL_APPRO_YN_2"    , '');
		ds_Modify.SetColumn(ds_Modify.row, "BILL_YMD_2"         , '');
		ds_Modify.SetColumn(ds_Modify.row, "BILL_SEQ_2"         , '');
		
		ds_Modify.SetColumn(ds_Modify.row, "TXBILL_VAT_BIZPLC_2", '');
		ds_Modify.SetColumn(ds_Modify.row, "TXBILL_PUBL_YM_2"   , '');
		ds_Modify.SetColumn(ds_Modify.row, "TXBILL_PUBL_SEQ_2"  , '');
	}
			
	ds_Modify.SetColumn(ds_Modify.row, "MOVE_REPAIR_NUM"    , ds_MoveDocu.GetColumn(ds_MoveDocu.row, "MOVE_REPAIR_NUM"));		
	ds_Modify.SetRowType(ds_Modify.row, "update");
	
	var strSvcID         = "bill_update";
	var strPart          = "D10";	
	var strURL           = "handle.do?ServiceName=D1_MoveingRepairAmountMgr-service" ;					
	var strInDatasets    = "ds_Modify=ds_Modify:u";
	var strOutDatasets   = "";		
	var strArgument      = "bill_update=1";
			
	gfn_SyncCall( 
					 strSvcID
				   , strPart  	 
				   , strURL
				   , strInDatasets
				   , strOutDatasets
				   , strArgument
			   );

	if(ErrorCode<0)
	{
		return false;
	}
		return true;
	
}


function fn_D1_CallBack(svcId, ErrorCode, ErrorMsg)
{    
	//-------------------------------------------------------------------------------
	// 함수명   : fn_D1_CallBack(svcid, errcd, errmsg)
	// 설명     : 각 트렌젝션의 결과를 처리 .
	// return 내용 : 없음
	//-------------------------------------------------------------------------------
	if ( ErrorCode <0 ){
		alert(ErrorMsg);
		return;
	}

	switch(svcId)
	{	
	    case "fn_D1_Tr_find":	    
			//조회 성공메세지 출력
			if (ds_Receipt.RowCount() > 0) {
				gfn_SetStatusMsg(gfn_GetMessage("M000009","%AAA%","조회를"),"black",true);				//"조회를 완료하였습니다."
			} else {
				gfn_SetStatusMsg(gfn_GetMessage("M000008","%AAA%","조회결과 데이터가"),"black",true);	//"조회결과데이터가없습니다."
			}
			break;	
			
		case "btn_bill_OnClick": //전표생성후
			alert("전표가 발행되었습니다. ");
					
			fn_D1_billUpdate("update"); 
			fn_CommonSearch();
			break;	
		case "fn_D1_Tr_delete": //업데이트 성공시 조회가 들어가므로 조회와 똑같이 처리 
			alert("전표가 취소되었습니다");
		
			fn_D1_billUpdate("delete"); 			
			fn_CommonSearch();
			break;									
	}
}

